     1â†’#!/usr/bin/env node
     2â†’/**
     3â†’ * Pre-Task Hook: Validates PRD files exist before spawning Maven Flow specialist agents
     4â†’ *
     5â†’ * This hook runs before Task tool calls to Maven Flow specialist agents.
     6â†’ * It validates when spawning:
     7â†’ * - development-agent
     8â†’ * - refactor-agent
     9â†’ * - quality-agent
    10â†’ * - security-agent
    11â†’ * - design-agent
    12â†’ *
    13â†’ * Usage: Called by Claude Code via PreToolUse hook in flow.md
    14â†’ */
    15â†’
    16â†’const fs = require('fs');
    17â†’const path = require('path');
    18â†’
    19â†’// Maven Flow specialist agent types that require PRD validation
    20â†’const MAVEN_FLOW_AGENTS = [
    21â†’  'development-agent',
    22â†’  'refactor-agent',
    23â†’  'quality-agent',
    24â†’  'security-agent',
    25â†’  'design-agent'
    26â†’];
    27â†’
    28â†’function main() {
    29â†’  try {
    30â†’    // Read JSON from STDIN (Claude Code hooks pass input via stdin)
    31â†’    const stdinBuffer = fs.readFileSync(0, 'utf-8');
    32â†’    let input;
    33â†’
    34â†’    if (stdinBuffer.trim()) {
    35â†’      try {
    36â†’        input = JSON.parse(stdinBuffer);
    37â†’      } catch (parseError) {
    38â†’        // Input is not valid JSON, exit silently
    39â†’        process.exit(0);
    40â†’      }
    41â†’    } else {
    42â†’      // No input received, exit silently
    43â†’      process.exit(0);
    44â†’    }
    45â†’
    46â†’    // Extract tool_input from the hook JSON structure
    47â†’    const toolInput = input.tool_input || {};
    48â†’
    49â†’    // Only validate Maven Flow agent spawns
    50â†’    if (!toolInput.subagent_type || !MAVEN_FLOW_AGENTS.includes(toolInput.subagent_type)) {
    51â†’      process.exit(0);
    52â†’    }
    53â†’
    54â†’    // Get working directory from Claude or use current directory
    55â†’    const workingDir = input.cwd || process.cwd();
    56â†’
    57â†’    // Check if docs/ directory exists
    58â†’    const docsDir = path.join(workingDir, 'docs');
    59â†’    if (!fs.existsSync(docsDir)) {
    60â†’      console.error('Error: No docs/ directory found. Create a PRD first using the flow-prd skill.');
    61â†’      process.exit(3); // Exit code 3 tells Claude to block the tool
    62â†’    }
    63â†’
    64â†’    // Check if any PRD files exist
    65â†’    const prdFiles = fs.readdirSync(docsDir)
    66â†’      .filter(f => f.startsWith('prd-') && f.endsWith('.json'));
    67â†’
    68â†’    if (prdFiles.length === 0) {
    69â†’      console.error('Error: No PRD files found in docs/. Create a PRD first using the flow-prd skill.');
    70â†’      process.exit(3); // Exit code 3 tells Claude to block the tool
    71â†’    }
    72â†’
    73â†’    // Validation passed
    74â†’    process.exit(0);
    75â†’
    76â†’  } catch (error) {
    77â†’    // Log error but don't block execution
    78â†’    // This prevents the hook from breaking the flow if something unexpected happens
    79â†’    if (process.env.DEBUG) {
    80â†’      console.error('Hook error:', error.message);
    81â†’    }
    82â†’    process.exit(0);
    83â†’  }
    84â†’}
    85â†’
    86â†’main();
    87â†’

