// NextMavens Platform API - WhatsApp Instances Endpoint
// POST /api/whatsapp/instances - Create WhatsApp instance
// GET /api/whatsapp/instances - List WhatsApp instances

import { NextRequest } from 'next/server';
import { createAdminClient } from '../lib/sabase-client';
import { withAuthAndError } from '../lib/middleware';
import { createInstance as evolutionCreateInstance } from '../lib/evolution';
import type { CreateWhatsappInstanceInput, WhatsappInstance } from '../types';

// POST /api/whatsapp/instances - Create WhatsApp instance
export const POST = withAuthAndError(async (user, req: NextRequest) => {
  const body = await req.json() as CreateWhatsappInstanceInput;

  // Validate input
  if (!body.project_id) {
    throw new Error('project_id is required');
  }

  if (!body.instance_name || body.instance_name.trim().length < 2) {
    throw new Error('instance_name must be at least 2 characters');
  }

  // Validate instance_name format (alphanumeric, hyphens, underscores)
  if (!/^[a-zA-Z0-9_-]+$/.test(body.instance_name)) {
    throw new Error('instance_name must contain only letters, numbers, hyphens, and underscores');
  }

  const supabase = createAdminClient();

  // Verify user has access to the project
  const { data: project, error: projectError } = await supabase
    .from('projects')
    .select('organization_id')
    .eq('id', body.project_id)
    .single();

  if (projectError || !project) {
    throw new Error('Project not found');
  }

  // Check if user is member of the organization
  const { data: membership, error: memberError } = await supabase
    .from('organization_members')
    .select('role')
    .eq('organization_id', project.organization_id)
    .eq('user_id', user.id)
    .single();

  if (memberError || !membership) {
    throw new Error('You do not have access to this project');
  }

  // Check if instance_name is already taken in this project
  const { data: existingInstance } = await supabase
    .from('whatsapp_instances')
    .select('id')
    .eq('project_id', body.project_id)
    .eq('instance_name', body.instance_name)
    .single();

  if (existingInstance) {
    throw new Error('An instance with this name already exists in this project');
  }

  // Call Evolution API to create instance
  let evolutionResponse;
  try {
    evolutionResponse = await evolutionCreateInstance({
      instanceName: body.instance_name,
      webhook: true,
      webhookByEvents: true,
    });
  } catch (error) {
    console.error('Evolution API error:', error);
    throw new Error('Failed to create WhatsApp instance. Please ensure Evolution API is running.');
  }

  // Store instance record in database
  const { data: instance, error: instanceError } = await supabase
    .from('whatsapp_instances')
    .insert({
      project_id: body.project_id,
      instance_name: body.instance_name,
      instance_id: evolutionResponse.instance.instanceName,
      status: 'pending',
      webhook_url: body.webhook_url,
      settings: {},
    })
    .select()
    .single();

  if (instanceError) {
    throw instanceError;
  }

  return instance as WhatsappInstance;
});

// GET /api/whatsapp/instances - List WhatsApp instances for user's projects
export const GET = withAuthAndError(async (user, req: NextRequest) => {
  const { searchParams } = new URL(req.url);
  const projectId = searchParams.get('project_id');

  const supabase = createAdminClient();

  // Build query
  let query = supabase
    .from('whatsapp_instances')
    .select('*')
    .order('created_at', { ascending: false });

  if (projectId) {
    // Verify user has access to the project
    const { data: project } = await supabase
      .from('projects')
      .select('organization_id')
      .eq('id', projectId)
      .single();

    if (project) {
      const { data: membership } = await supabase
        .from('organization_members')
        .select('role')
        .eq('organization_id', project.organization_id)
        .eq('user_id', user.id)
        .single();

      if (membership) {
        query = query.eq('project_id', projectId);
      } else {
        // User doesn't have access, return empty
        return [];
      }
    } else {
      return [];
    }
  } else {
    // Get all instances from user's organizations
    const { data: memberships } = await supabase
      .from('organization_members')
      .select('organization_id')
      .eq('user_id', user.id);

    const orgIds = memberships?.map(m => m.organization_id) || [];

    if (orgIds.length > 0) {
      // Get projects from these organizations
      const { data: projects } = await supabase
        .from('projects')
        .select('id')
        .in('organization_id', orgIds);

      const projectIds = projects?.map(p => p.id) || [];

      if (projectIds.length > 0) {
        query = query.in('project_id', projectIds);
      } else {
        return [];
      }
    } else {
      return [];
    }
  }

  const { data: instances, error } = await query;

  if (error) {
    throw error;
  }

  return instances || [];
});

