     1→// NextMavens Platform - API Middleware
     2→// Authentication and authorization middleware for API routes
     3→
     4→import { NextRequest } from 'next/server';
     5→import { createAdminClient } from './supabase-client';
     6→import { validateApiKey, updateApiKeyLastUsed } from './db/api-keys';
     7→import { checkApiKeyRateLimit, apiKeyRateLimitToResponse, ApiKeyRateLimitError } from './api-key-rate-limit';
     8→import type { AuthUser, ApiKey } from './types';
     9→
    10→// ====================================================================
    11→// Error Classes
    12→// ====================================================================
    13→
    14→export class AuthError extends Error {
    15→  constructor(message: string, public code: string = 'AUTH_ERROR') {
    16→    super(message);
    17→    this.name = 'AuthError';
    18→  }
    19→}
    20→
    21→export class ValidationError extends Error {
    22→  constructor(message: string, public field?: string) {
    23→    super(message);
    24→    this.name = 'ValidationError';
    25→  }
    26→}
    27→
    28→export class NotFoundError extends Error {
    29→  constructor(resource: string, identifier?: string) {
    30→    const message = identifier
    31→      ? `${resource} with identifier "${identifier}" not found`
    32→      : `${resource} not found`;
    33→    super(message);
    34→    this.name = 'NotFoundError';
    35→  }
    36→}
    37→
    38→export class PermissionError extends Error {
    39→  constructor(message: string = 'You do not have permission to perform this action') {
    40→    super(message);
    41→    this.name = 'PermissionError';
    42→  }
    43→}
    44→
    45→export class RateLimitError extends Error {
    46→  constructor(message: string, public resetAt: Date, public retryAfter: number) {
    47→    super(message);
    48→    this.name = 'RateLimitError';
    49→  }
    50→}
    51→
    52→// ====================================================================
    53→// Authentication Middleware
    54→// ====================================================================
    55→
    56→/**
    57→ * Extract and verify the user from a request
    58→ * @throws {AuthError} if authentication fails
    59→ */
    60→export async function getAuthenticatedUser(req: NextRequest): Promise<AuthUser> {
    61→  const authHeader = req.headers.get('authorization');
    62→
    63→  if (!authHeader?.startsWith('Bearer ')) {
    64→    throw new AuthError('Missing or invalid authorization header', 'MISSING_TOKEN');
    65→  }
    66→
    67→  const token = authHeader.substring(7);
    68→  const supabase = createAdminClient();
    69→
    70→  const { data: { user }, error } = await supabase.auth.getUser(token);
    71→
    72→  if (error || !user) {
    73→    throw new AuthError('Invalid or expired token', 'INVALID_TOKEN');
    74→  }
    75→
    76→  // Check if email is confirmed
    77→  if (!user.email_confirmed_at && user.email) {
    78→    throw new AuthError('Email must be confirmed', 'EMAIL_NOT_CONFIRMED');
    79→  }
    80→
    81→  return {
    82→    id: user.id,
    83→    email: user.email || '',
    84→    email_confirmed_at: user.email_confirmed_at || undefined,
    85→    phone: user.phone || undefined,
    86→    phone_confirmed_at: user.phone_confirmed_at || undefined,
    87→    last_sign_in_at: user.last_sign_in_at || undefined,
    88→    created_at: user.created_at || new Date().toISOString(),
    89→    updated_at: user.updated_at || new Date().toISOString(),
    90→    app_metadata: user.app_metadata as Record<string, unknown>,
    91→    user_metadata: user.user_metadata as Record<string, unknown>,
    92→  };
    93→}
    94→
    95→/**
    96→ * Optional authentication - returns user if authenticated, null otherwise
    97→ */
    98→export async function getOptionalUser(req: NextRequest): Promise<AuthUser | null> {
    99→  try {
   100→    return await getAuthenticatedUser(req);
   101→  } catch {
   102→    return null;
   103→  }
   104→}
   105→
   106→// ====================================================================
   107→// API Key Authentication Middleware
   108→// ====================================================================
   109→
   110→/**
   111→ * Extract and validate API key from request
   112→ * @throws {AuthError} if API key is invalid
   113→ */
   114→export async function getAuthenticatedApiKey(req: NextRequest): Promise<ApiKey> {
   115→  const authHeader = req.headers.get('authorization');
   116→
   117→  if (!authHeader?.startsWith('Bearer ')) {
   118→    throw new AuthError('Missing or invalid authorization header', 'MISSING_API_KEY');
   119→  }
   120→
   121→  const token = authHeader.substring(7);
   122→
   123→  // Check if it's an API key (starts with nm_)
   124→  if (!token.startsWith('nm_')) {
   125→    throw new AuthError('Invalid API key format', 'INVALID_API_KEY_FORMAT');
   126→  }
   127→
   128→  // Validate the API key
   129→  const apiKey = await validateApiKey(token);
   130→
   131→  if (!apiKey) {
   132→    throw new AuthError('Invalid or expired API key', 'INVALID_API_KEY');
   133→  }
   134→
   135→  // Check rate limit for this API key
   136→  const rateLimitResult = await checkApiKeyRateLimit(apiKey);
   137→
   138→  if (!rateLimitResult.success) {
   139→    throw new ApiKeyRateLimitError(rateLimitResult, apiKey.id);
   140→  }
   141→
   142→  // Update last_used timestamp asynchronously (don't await)
   143→  updateApiKeyLastUsed(apiKey.id).catch(err => {
   144→    console.error('Failed to update API key last_used:', err);
   145→  });
   146→
   147→  return apiKey;
   148→}
   149→
   150→/**
   151→ * Optional API key authentication - returns API key if authenticated, null otherwise
   152→ */
   153→export async function getOptionalApiKey(req: NextRequest): Promise<ApiKey | null> {
   154→  try {
   155→    return await getAuthenticatedApiKey(req);
   156→  } catch {
   157→    return null;
   158→  }
   159→}
   160→
   161→// ====================================================================
   162→// Authorization Middleware
   163→// ====================================================================
   164→
   165→/**
   166→ * Check if user has a specific role in an organization
   167→ */
   168→export async function checkOrganizationRole(
   169→  userId: string,
   170→  organizationId: string,
   171→  allowedRoles: string[] = ['owner', 'admin']
   172→): Promise<boolean> {
   173→  const supabase = createAdminClient();
   174→
   175→  const { data, error } = await supabase
   176→    .from('organization_members')
   177→    .select('role')
   178→    .eq('organization_id', organizationId)
   179→    .eq('user_id', userId)
   180→    .single();
   181→
   182→  if (error || !data) {
   183→    return false;
   184→  }
   185→
   186→  return allowedRoles.includes(data.role);
   187→}
   188→
   189→/**
   190→ * Require a specific role in an organization
   191→ * @throws {PermissionError} if user doesn't have the required role
   192→ * @throws {NotFoundError} if user is not a member of the organization
   193→ */
   194→export async function requireOrganizationRole(
   195→  userId: string,
   196→  organizationId: string,
   197→  allowedRoles: string[] = ['owner', 'admin']
   198→): Promise<void> {
   199→  const supabase = createAdminClient();
   200→
   201→  const { data, error } = await supabase
   202→    .from('organization_members')
   203→    .select('role')
   204→    .eq('organization_id', organizationId)
   205→    .eq('user_id', userId)
   206→    .single();
   207→
   208→  if (error || !data) {
   209→    throw new NotFoundError('Organization membership');
   210→  }
   211→
   212→  if (!allowedRoles.includes(data.role)) {
   213→    throw new PermissionError(
   214→      `This action requires one of the following roles: ${allowedRoles.join(', ')}`
   215→    );
   216→  }
   217→}
   218→
   219→/**
   220→ * Check if user has a specific role in a project
   221→ */
   222→export async function checkProjectRole(
   223→  userId: string,
   224→  projectId: string,
   225→  allowedRoles: string[] = ['owner', 'admin']
   226→): Promise<boolean> {
   227→  const supabase = createAdminClient();
