// NextMavens Platform - API Middleware
// Authentication and authorization middleware for API routes

import { NextRequest } from 'next/server';
import { createAdminClient } from './supabase-client';
import { validateApiKey, updateApiKeyLastUsed } from './db/api-keys';
import { checkApiKeyRateLimit, apiKeyRateLimitToResponse, ApiKeyRateLimitError } from './api-key-rate-limit';
import type { AuthUser, ApiKey } from './types';

// ====================================================================
// Error Classes
// ====================================================================

export class AuthError extends Error {
  constructor(message: string, public code: string = 'AUTH_ERROR') {
    super(message);
    this.name = 'AuthError';
  }
}

export class ValidationError extends Error {
  constructor(message: string, public field?: string) {
    super(message);
    this.name = 'ValidationError';
  }
}

export class NotFoundError extends Error {
  constructor(resource: string, identifier?: string) {
    const message = identifier
      ? `${resource} with identifier "${identifier}" not found`
      : `${resource} not found`;
    super(message);
    this.name = 'NotFoundError';
  }
}

export class PermissionError extends Error {
  constructor(message: string = 'You do not have permission to perform this action') {
    super(message);
    this.name = 'PermissionError';
  }
}

export class RateLimitError extends Error {
  constructor(message: string, public resetAt: Date, public retryAfter: number) {
    super(message);
    this.name = 'RateLimitError';
  }
}

// ====================================================================
// Authentication Middleware
// ====================================================================

/**
 * Extract and verify the user from a request
 * @throws {AuthError} if authentication fails
 */
export async function getAuthenticatedUser(req: NextRequest): Promise<AuthUser> {
  const authHeader = req.headers.get('authorization');

  if (!authHeader?.startsWith('Bearer ')) {
    throw new AuthError('Missing or invalid authorization header', 'MISSING_TOKEN');
  }

  const token = authHeader.substring(7);
  const supabase = createAdminClient();

  const { data: { user }, error } = await supabase.auth.getUser(token);

  if (error || !user) {
    throw new AuthError('Invalid or expired token', 'INVALID_TOKEN');
  }

  // Check if email is confirmed
  if (!user.email_confirmed_at && user.email) {
    throw new AuthError('Email must be confirmed', 'EMAIL_NOT_CONFIRMED');
  }

  return {
    id: user.id,
    email: user.email || '',
    email_confirmed_at: user.email_confirmed_at || undefined,
    phone: user.phone || undefined,
    phone_confirmed_at: user.phone_confirmed_at || undefined,
    last_sign_in_at: user.last_sign_in_at || undefined,
    created_at: user.created_at || new Date().toISOString(),
    updated_at: user.updated_at || new Date().toISOString(),
    app_metadata: user.app_metadata as Record<string, unknown>,
    user_metadata: user.user_metadata as Record<string, unknown>,
  };
}

/**
 * Optional authentication - returns user if authenticated, null otherwise
 */
export async function getOptionalUser(req: NextRequest): Promise<AuthUser | null> {
  try {
    return await getAuthenticatedUser(req);
  } catch {
    return null;
  }
}

// ====================================================================
// API Key Authentication Middleware
// ====================================================================

/**
 * Extract and validate API key from request
 * @throws {AuthError} if API key is invalid
 */
export async function getAuthenticatedApiKey(req: NextRequest): Promise<ApiKey> {
  const authHeader = req.headers.get('authorization');

  if (!authHeader?.startsWith('Bearer ')) {
    throw new AuthError('Missing or invalid authorization header', 'MISSING_API_KEY');
  }

  const token = authHeader.substring(7);

  // Check if it's an API key (starts with nm_)
  if (!token.startsWith('nm_')) {
    throw new AuthError('Invalid API key format', 'INVALID_API_KEY_FORMAT');
  }

  // Validate the API key
  const apiKey = await validateApiKey(token);

  if (!apiKey) {
    throw new AuthError('Invalid or expired API key', 'INVALID_API_KEY');
  }

  // Check rate limit for this API key
  const rateLimitResult = await checkApiKeyRateLimit(apiKey);

  if (!rateLimitResult.success) {
    throw new ApiKeyRateLimitError(rateLimitResult, apiKey.id);
  }

  // Update last_used timestamp asynchronously (don't await)
  updateApiKeyLastUsed(apiKey.id).catch(err => {
    console.error('Failed to update API key last_used:', err);
  });

  return apiKey;
}

/**
 * Optional API key authentication - returns API key if authenticated, null otherwise
 */
export async function getOptionalApiKey(req: NextRequest): Promise<ApiKey | null> {
  try {
    return await getAuthenticatedApiKey(req);
  } catch {
    return null;
  }
}

// ====================================================================
// Authorization Middleware
// ====================================================================

/**
 * Check if user has a specific role in an organization
 */
export async function checkOrganizationRole(
  userId: string,
  organizationId: string,
  allowedRoles: string[] = ['owner', 'admin']
): Promise<boolean> {
  const supabase = createAdminClient();

  const { data, error } = await supabase
    .from('organization_members')
    .select('role')
    .eq('organization_id', organizationId)
    .eq('user_id', userId)
    .single();

  if (error || !data) {
    return false;
  }

  return allowedRoles.includes(data.role);
}

/**
 * Require a specific role in an organization
 * @throws {PermissionError} if user doesn't have the required role
 * @throws {NotFoundError} if user is not a member of the organization
 */
export async function requireOrganizationRole(
  userId: string,
  organizationId: string,
  allowedRoles: string[] = ['owner', 'admin']
): Promise<void> {
  const supabase = createAdminClient();

  const { data, error } = await supabase
    .from('organization_members')
    .select('role')
    .eq('organization_id', organizationId)
    .eq('user_id', userId)
    .single();

  if (error || !data) {
    throw new NotFoundError('Organization membership');
  }

  if (!allowedRoles.includes(data.role)) {
    throw new PermissionError(
      `This action requires one of the following roles: ${allowedRoles.join(', ')}`
    );
  }
}

/**
 * Check if user has a specific role in a project
 */
export async function checkProjectRole(
  userId: string,
  projectId: string,
  allowedRoles: string[] = ['owner', 'admin']
): Promise<boolean> {
  const supabase = createAdminClient();

  const { data, error } = await supabase
    .from('projects')
    .select('organization_id')
    .eq('id', projectId)
    .single();

  if (error || !data) {
    return false;
  }

  return checkOrganizationRole(userId, data.organization_id, allowedRoles);
}

/**
 * Require a specific role in a project
 * @throws {PermissionError} if user doesn't have the required role
 * @throws {NotFoundError} if project or membership doesn't exist
 */
export async function requireProjectRole(
  userId: string,
  projectId: string,
  allowedRoles: string[] = ['owner', 'admin']
): Promise<void> {
  const supabase = createAdminClient();

  const { data, error } = await supabase
    .from('projects')
    .select('organization_id')
    .eq('id', projectId)
    .single();

  if (error || !data) {
    throw new NotFoundError('Project', projectId);
  }

  await requireOrganizationRole(userId, data.organization_id, allowedRoles);
}

// ====================================================================
// Validation Middleware
// ====================================================================

/**
 * Validate request body against a schema
 */
export function validateBody<T>(body: unknown, schema: {
  required?: (keyof T)[];
  optional?: (keyof T)[];
  validators?: Partial<Record<keyof T, (value: unknown) => boolean | string>>;
}): T {
  const result = {} as T;

  // Check required fields
  if (schema.required) {
    for (const field of schema.required) {
      const fieldStr = String(field);
      if (body && typeof body === 'object' && fieldStr in body) {
        const value = (body as Record<string, unknown>)[fieldStr];

        // Run validator if present
        if (schema.validators?.[field]) {
          const validation = schema.validators[field](value);
          if (validation === true) {
            (result as Record<string, unknown>)[fieldStr] = value;
          } else {
            throw new ValidationError(
              typeof validation === 'string' ? validation : `Invalid ${fieldStr}`,
              fieldStr
            );
          }
        } else {
          (result as Record<string, unknown>)[fieldStr] = value;
        }
      } else {
        throw new ValidationError(`${fieldStr} is required`, fieldStr);
      }
    }
  }

  // Check optional fields
  if (schema.optional && body && typeof body === 'object') {
    for (const field of schema.optional) {
      const fieldStr = String(field);
      if (fieldStr in body) {
        const value = (body as Record<string, unknown>)[fieldStr];

        // Run validator if present
        if (schema.validators?.[field]) {
          const validation = schema.validators[field](value);
          if (validation === true) {
            (result as Record<string, unknown>)[fieldStr] = value;
          } else if (validation !== false) {
            throw new ValidationError(
              typeof validation === 'string' ? validation : `Invalid ${fieldStr}`,
              fieldStr
            );
          }
        } else {
          (result as Record<string, unknown>)[fieldStr] = value;
        }
      }
    }
  }

  return result;
}

/**
 * Common validators
 */
export const validators = {
  string: (value: unknown): boolean => typeof value === 'string',
  email: (value: unknown): boolean => {
    if (typeof value !== 'string') return false;
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value);
  },
  slug: (value: unknown): boolean => {
    if (typeof value !== 'string') return false;
    return /^[a-z0-9-]+$/.test(value) && value.length >= 2 && value.length <= 50;
  },
  minLength: (min: number) => (value: unknown): boolean | string => {
    if (typeof value !== 'string') return 'Must be a string';
    if (value.length < min) return `Must be at least ${min} characters`;
    return true;
  },
  maxLength: (max: number) => (value: unknown): boolean | string => {
    if (typeof value !== 'string') return 'Must be a string';
    if (value.length > max) return `Must be at most ${max} characters`;
    return true;
  },
  url: (value: unknown): boolean => {
    if (typeof value !== 'string') return false;
    try {
      new URL(value);
      return true;
    } catch {
      return false;
    }
  },
  uuid: (value: unknown): boolean => {
    if (typeof value !== 'string') return false;
    return /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(value);
  },
  enum: <T extends string>(values: T[]) => (value: unknown): boolean | string => {
    if (typeof value !== 'string') return 'Must be a string';
    if (!values.includes(value as T)) return `Must be one of: ${values.join(', ')}`;
    return true;
  },
};

// ====================================================================
// Error Response Helpers
// ====================================================================

/**
 * Convert an error to an appropriate HTTP response
 */
export function errorToResponse(error: unknown): Response {
  if (error instanceof ApiKeyRateLimitError) {
    return apiKeyRateLimitToResponse(error.result);
  }

  if (error instanceof AuthError) {
    return Response.json(
      { error: { code: error.code, message: error.message } },
      { status: 401 }
    );
  }

  if (error instanceof ValidationError) {
    return Response.json(
      {
        error: {
          code: 'VALIDATION_ERROR',
          message: error.message,
          details: error.field ? { field: error.field } : undefined,
        },
      },
      { status: 400 }
    );
  }

  if (error instanceof NotFoundError) {
    return Response.json(
      { error: { code: 'NOT_FOUND', message: error.message } },
      { status: 404 }
    );
  }

  if (error instanceof PermissionError) {
    return Response.json(
      { error: { code: 'FORBIDDEN', message: error.message } },
      { status: 403 }
    );
  }

  // Log unexpected errors
  console.error('Unexpected error:', error);

  return Response.json(
    { error: { code: 'INTERNAL_ERROR', message: 'An unexpected error occurred' } },
    { status: 500 }
  );
}

// ====================================================================
// Request Wrapper
// ====================================================================

/**
 * Wrap an API handler with authentication
 */
export function withAuth<T>(
  handler: (user: AuthUser, req: NextRequest) => Promise<T>
) {
  return async (req: NextRequest): Promise<T> => {
    try {
      const user = await getAuthenticatedUser(req);
      return await handler(user, req);
    } catch (error) {
      throw error;
    }
  };
}

/**
 * Wrap an API handler with optional authentication
 */
export function withOptionalAuth<T>(
  handler: (user: AuthUser | null, req: NextRequest) => Promise<T>
) {
  return async (req: NextRequest): Promise<T> => {
    const user = await getOptionalUser(req);
    return await handler(user, req);
  };
}

/**
 * Wrap an API handler with API key authentication
 */
export function withApiKeyAuth<T>(
  handler: (apiKey: ApiKey, req: NextRequest) => Promise<T>
) {
  return async (req: NextRequest): Promise<T> => {
    try {
      const apiKey = await getAuthenticatedApiKey(req);
      return await handler(apiKey, req);
    } catch (error) {
      throw error;
    }
  };
}

/**
 * Wrap an API handler with optional API key authentication
 */
export function withOptionalApiKeyAuth<T>(
  handler: (apiKey: ApiKey | null, req: NextRequest) => Promise<T>
) {
  return async (req: NextRequest): Promise<T> => {
    const apiKey = await getOptionalApiKey(req);
    return await handler(apiKey, req);
  };
}

/**
 * Wrap an API handler with both API key auth and error handling
 */
export function withApiKeyAuthAndError<T>(
  handler: (apiKey: ApiKey, req: NextRequest) => Promise<T>
) {
  return async (req: NextRequest): Promise<Response> => {
    try {
      const apiKey = await getAuthenticatedApiKey(req);
      const result = await handler(apiKey, req);
      return Response.json({ success: true, data: result });
    } catch (error) {
      return errorToResponse(error);
    }
  };
}

/**
 * Wrap an API handler with error handling
 */
export function withErrorHandler<T>(
  handler: () => Promise<T>
): Promise<T> {
  return handler();
}

/**
 * Wrap an API handler with both auth and error handling
 */
export function withAuthAndError<T>(
  handler: (user: AuthUser, req: NextRequest) => Promise<T>
) {
  return async (req: NextRequest): Promise<Response> => {
    try {
      const user = await getAuthenticatedUser(req);
      const result = await handler(user, req);
      return Response.json({ success: true, data: result });
    } catch (error) {
      return errorToResponse(error);
    }
  };
}

