// NextMavens Platform - Evolution API Client
// Client library for Evolution API v2.3.7 - WhatsApp integration

// ====================================================================
// Configuration
// ====================================================================

const EVOLUTION_API_URL = process.env.EVOLUTION_API_URL || 'http://localhost:8080';
const EVOLUTION_API_KEY = process.env.EVOLUTION_API_KEY || '';
const EVOLUTION_WEBHOOK_SECRET = process.env.EVOLUTION_WEBHOOK_SECRET || '';
const EVOLUTION_WEBHOOK_URL = process.env.EVOLUTION_WEBHOOK_URL || '';

interface EvolutionConfig {
  apiUrl: string;
  apiKey: string;
  webhookUrl?: string;
  webhookSecret?: string;
}

// ====================================================================
// Types
// ====================================================================

export interface CreateInstanceParams {
  instanceName: string;
  qrcode?: boolean;
  integration?: string;
  webhook?: boolean;
  webhookByEvents?: boolean;
  webhookBase64?: boolean;
  rejectCall?: boolean;
}

export interface EvolutionInstanceResponse {
  instance: {
    instanceName: string;
    status: string;
  };
}

export interface EvolutionQrResponse {
  base64: string;
  code: string;
  pairingCode?: string;
}

export interface EvolutionStatusResponse {
  state: string;
  status?: string;
  user?: {
    id: string;
    name: string;
    phone: string;
  };
  battery?: number;
  plugged?: boolean;
}

export interface SendMessageParams {
  number: string;
  text?: string;
  linkUrl?: string;
  caption?: string;
  location?: {
    name: string;
    address: string;
    latitude: number;
    longitude: number;
  };
}

// ====================================================================
// HTTP Client
// ====================================================================

class EvolutionApiError extends Error {
  constructor(
    message: string,
    public statusCode?: number,
    public responseBody?: string
  ) {
    super(message);
    this.name = 'EvolutionApiError';
  }
}

async function evolutionRequest<T>(
  endpoint: string,
  options: RequestInit = {}
): Promise<T> {
  const url = `${EVOLUTION_API_URL}${endpoint}`;
  const headers: HeadersInit = {
    'Content-Type': 'application/json',
    ...options.headers,
  };

  if (EVOLUTION_API_KEY) {
    headers['apikey'] = EVOLUTION_API_KEY;
  }

  try {
    const response = await fetch(url, {
      ...options,
      headers,
    });

    if (!response.ok) {
      const body = await response.text();
      throw new EvolutionApiError(
        `Evolution API error: ${response.statusText}`,
        response.status,
        body
      );
    }

    return response.json() as Promise<T>;
  } catch (error) {
    if (error instanceof EvolutionApiError) {
      throw error;
    }
    throw new EvolutionApiError(
      `Failed to connect to Evolution API: ${error instanceof Error ? error.message : 'Unknown error'}`
    );
  }
}

// ====================================================================
// Instance Management
// ====================================================================

/**
 * Create a new WhatsApp instance in Evolution API
 * POST /instance/create
 */
export async function createInstance(params: CreateInstanceParams): Promise<EvolutionInstanceResponse> {
  return evolutionRequest<EvolutionInstanceResponse>('/instance/create', {
    method: 'POST',
    body: JSON.stringify({
      instanceName: params.instanceName,
      qrcode: params.qrcode ?? false,
      integration: params.integration || 'WHATSAPP-BAILEYS',
      webhook: params.webhook ?? true,
      webhookByEvents: params.webhookByEvents ?? true,
      webhookBase64: params.webhookBase64 ?? false,
      rejectCall: params.rejectCall ?? true,
      ...(EVOLUTION_WEBHOOK_URL && { webhookUrl: EVOLUTION_WEBHOOK_URL }),
    }),
  });
}

/**
 * Fetch QR code for connecting an instance
 * GET /instance/connect/{instanceName}
 */
export async function fetchQrCode(instanceName: string): Promise<EvolutionQrResponse> {
  return evolutionRequest<EvolutionQrResponse>(`/instance/connect/${instanceName}`, {
    method: 'GET',
  });
}

/**
 * Get connection status of an instance
 * GET /instance/connectionState/{instanceName}
 */
export async function getConnectionState(instanceName: string): Promise<EvolutionStatusResponse> {
  return evolutionRequest<EvolutionStatusResponse>(`/instance/connectionState/${instanceName}`, {
    method: 'GET',
  });
}

/**
 * Delete an instance
 * DELETE /instance/delete/{instanceName}
 */
export async function deleteInstance(instanceName: string): Promise<{ message: string }> {
  return evolutionRequest<{ message: string }>(`/instance/delete/${instanceName}`, {
    method: 'DELETE',
  });
}

/**
 * Logout an instance (disconnect from WhatsApp)
 * DELETE /instance/logout/{instanceName}
 */
export async function logoutInstance(instanceName: string): Promise<{ message: string }> {
  return evolutionRequest<{ message: string }>(`/instance/logout/${instanceName}`, {
    method: 'DELETE',
  });
}

// ====================================================================
// Message Operations
// ====================================================================

/**
 * Send a text message via WhatsApp
 * POST /message/sendText/{instanceName}
 */
export async function sendTextMessage(
  instanceName: string,
  params: SendMessageParams
): Promise<{ key: { remoteJid: string; fromMe: boolean; id: string }; message: unknown }> {
  if (!params.text) {
    throw new EvolutionApiError('Text message is required');
  }

  return evolutionRequest(`/message/sendText/${instanceName}`, {
    method: 'POST',
    body: JSON.stringify({
      number: params.number,
      text: params.text,
    }),
  });
}

/**
 * Send a media message via WhatsApp
 * POST /message/sendMedia/{instanceName}
 */
export async function sendMediaMessage(
  instanceName: string,
  params: {
    number: string;
    mediaUrl: string;
    caption?: string;
    fileName?: string;
  }
): Promise<{ key: { remoteJid: string; fromMe: boolean; id: string } }> {
  return evolutionRequest(`/message/sendMedia/${instanceName}`, {
    method: 'POST',
    body: JSON.stringify({
      number: params.number,
      mediaUrl: params.mediaUrl,
      caption: params.caption,
      fileName: params.fileName,
    }),
  });
}

// ====================================================================
// Webhook Signature Verification
// ====================================================================

/**
 * Verify webhook signature from Evolution API
 * Uses HMAC SHA256 with the webhook secret
 */
export function verifyWebhookSignature(
  payload: string,
  signature: string
): boolean {
  if (!EVOLUTION_WEBHOOK_SECRET) {
    console.warn('EVOLUTION_WEBHOOK_SECRET not set, skipping signature verification');
    return true;
  }

  const crypto = require('crypto');
  const hmac = crypto.createHmac('sha256', EVOLUTION_WEBHOOK_SECRET);
  hmac.update(payload);
  const expectedSignature = `sha256=${hmac.digest('hex')}`;

  return signature === expectedSignature;
}

// ====================================================================
// Health Check
// ====================================================================

/**
 * Check if Evolution API is reachable
 */
export async function healthCheck(): Promise<boolean> {
  try {
    await evolutionRequest('/health', { method: 'GET' });
    return true;
  } catch {
    return false;
  }
}

