// NextMavens Platform API - WhatsApp QR Code Endpoint
// GET /api/whatsapp/qr/[instanceName] - Fetch QR code for connecting an instance

import { NextRequest } from 'next/server';
import { withAuthAndError } from '@/lib/middleware';
import { getWhatsappInstanceByName } from '@/lib/db/whatsapp';
import { getProjectById } from '@/lib/db/projects';

interface EvolutionQrResponse {
  base64: string;
  code: string;
  pairingCode?: string;
}

// GET /api/whatsapp/qr/[instanceName] - Fetch QR code for connecting an instance
export async function GET(
  req: NextRequest,
  { params }: { params: Promise<{ instanceName: string }> }
) {
  return withAuthAndError(async (user) => {
    const { instanceName } = await params;
    const url = new URL(req.url);
    const projectId = url.searchParams.get('project_id');

    if (!projectId) {
      throw new Error('project_id is required');
    }

    // Verify user has access to the project
    const project = await getProjectById(projectId);

    // Verify instance exists and belongs to the project
    const instance = await getWhatsappInstanceByName(projectId, instanceName);
    if (!instance) {
      throw new Error('WhatsApp instance not found');
    }

    // Check if instance is already connected
    if (instance.status === 'connected') {
      return {
        success: true,
        already_connected: true,
        phone_number: instance.phone_number,
      };
    }

    // Call Evolution API to fetch QR code
    const EVOLUTION_API_URL = process.env.EVOLUTION_API_URL || 'http://localhost:8080';
    const EVOLUTION_API_KEY = process.env.EVOLUTION_API_KEY || '';

    const evolutionResponse = await fetch(`${EVOLUTION_API_URL}/instance/connect/${instanceName}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        ...(EVOLUTION_API_KEY && { 'apikey': EVOLUTION_API_KEY }),
      },
    });

    if (!evolutionResponse.ok) {
      const errorText = await evolutionResponse.text();
      console.error('Evolution API QR fetch error:', evolutionResponse.status, errorText);
      throw new Error('Failed to fetch QR code from Evolution API');
    }

    const qrData: EvolutionQrResponse = await evolutionResponse.json();

    return {
      success: true,
      qr_code_base64: qrData.base64,
      code: qrData.code,
      pairing_code: qrData.pairingCode,
    };
  })(req);
}

