// NextMavens Platform API - WhatsApp Send Message Endpoint
// POST /api/whatsapp/send - Send WhatsApp message via Evolution API

import { NextRequest } from 'next/server';
import { withAuthAndError } from '@/lib/middleware';
import { getWhatsappInstanceByName } from '@/lib/db/whatsapp';
import { getProjectById } from '@/lib/db/projects';
import { createWhatsappMessage } from '@/lib/db/whatsapp-messages';
import type { WhatsappMessage } from '@/lib/types';

interface EvolutionSendMessageResponse {
  key?: {
    remoteJid?: string;
    fromMe?: boolean;
    id?: string;
  };
  message?: {
    extendedTextMessage?: {
      text?: string;
    };
  };
  status?: number;
  message_id?: string;
}

interface SendMessageBody {
  instance_name: string;
  project_id: string;
  number: string;
  text: string;
  media_url?: string;
}

/**
 * Format phone number to WhatsApp format
 * Removes non-numeric characters and ensures it has country code
 */
function formatPhoneNumber(number: string): string {
  // Remove all non-numeric characters
  let cleaned = number.replace(/\D/g, '');

  // If number starts with 0, remove it (common in some countries)
  if (cleaned.startsWith('0')) {
    cleaned = cleaned.substring(1);
  }

  // Ensure the number is at least 10 digits
  if (cleaned.length < 10) {
    throw new Error('Invalid phone number: must be at least 10 digits');
  }

  // Return in format: number@s.whatsapp.net
  return `${cleaned}@s.whatsapp.net`;
}

/**
 * Validate that instance belongs to the specified project
 */
function validateInstanceProject(
  instance: { project_id: string },
  projectId: string
): void {
  if (instance.project_id !== projectId) {
    throw new Error('WhatsApp instance does not belong to the specified project');
  }
}

// POST /api/whatsapp/send - Send WhatsApp message
export async function POST(req: NextRequest) {
  return withAuthAndError(async (user) => {
    const body: SendMessageBody = await req.json();

    // Validate required fields
    if (!body.project_id || typeof body.project_id !== 'string') {
      throw new Error('project_id is required');
    }

    if (!body.instance_name || typeof body.instance_name !== 'string') {
      throw new Error('instance_name is required');
    }

    if (!body.number || typeof body.number !== 'string') {
      throw new Error('number is required');
    }

    if (!body.text || typeof body.text !== 'string') {
      throw new Error('text is required');
    }

    const instanceName = body.instance_name.trim();
    const number = body.number.trim();
    const text = body.text.trim();

    // Validate text length
    if (text.length === 0) {
      throw new Error('text cannot be empty');
    }

    if (text.length > 4096) {
      throw new Error('text is too long (max 4096 characters)');
    }

    // Verify user has access to the project
    const project = await getProjectById(body.project_id);

    // Verify instance exists and belongs to the project
    const instance = await getWhatsappInstanceByName(body.project_id, instanceName);
    if (!instance) {
      throw new Error('WhatsApp instance not found');
    }

    // Validate instance belongs to project
    validateInstanceProject(instance, body.project_id);

    // Check if instance is connected
    if (instance.status !== 'connected') {
      throw new Error(`WhatsApp instance is not connected (current status: ${instance.status})`);
    }

    // Format phone number
    const formattedNumber = formatPhoneNumber(number);

    // Call Evolution API to send message
    const EVOLUTION_API_URL = process.env.EVOLUTION_API_URL || 'http://localhost:8080';
    const EVOLUTION_API_KEY = process.env.EVOLUTION_API_KEY || '';

    let evolutionEndpoint: string;
    let evolutionBody: Record<string, unknown>;

    // Determine endpoint based on whether media is included
    if (body.media_url) {
      // Send media message
      evolutionEndpoint = `/message/sendMedia/${instanceName}`;
      evolutionBody = {
        number: formattedNumber,
        mediaUrl: body.media_url,
        caption: text,
      };
    } else {
      // Send text message
      evolutionEndpoint = `/message/sendText/${instanceName}`;
      evolutionBody = {
        number: formattedNumber,
        text: text,
      };
    }

    const evolutionResponse = await fetch(`${EVOLUTION_API_URL}${evolutionEndpoint}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        ...(EVOLUTION_API_KEY && { 'apikey': EVOLUTION_API_KEY }),
      },
      body: JSON.stringify(evolutionBody),
    });

    if (!evolutionResponse.ok) {
      const errorText = await evolutionResponse.text();
      console.error('Evolution API send message error:', evolutionResponse.status, errorText);
      throw new Error(`Failed to send message via Evolution API: ${evolutionResponse.status} ${errorText}`);
    }

    const evolutionData: EvolutionSendMessageResponse = await evolutionResponse.json();

    // Extract message ID from response
    const messageId = evolutionData.key?.id || evolutionData.message_id || '';

    if (!messageId) {
      console.warn('Evolution API did not return a message ID', evolutionData);
    }

    // Store message in database
    const messageInput: Omit<WhatsappMessage, 'id' | 'created_at'> = {
      instance_id: instance.id,
      message_id: messageId,
      from_number: instance.phone_number || 'unknown',
      to_number: formattedNumber,
      message_type: body.media_url ? 'media' : 'text',
      content: text,
      media_url: body.media_url,
      direction: 'outbound',
      status: 'sent',
      metadata: {
        evolution_response: evolutionData,
      },
    };

    const savedMessage = await createWhatsappMessage(messageInput);

    return {
      success: true,
      message: 'Message sent successfully',
      message_id: savedMessage.message_id,
      data: {
        id: savedMessage.id,
        to_number: savedMessage.to_number,
        message_type: savedMessage.message_type,
        status: savedMessage.status,
        created_at: savedMessage.created_at,
      },
    };
  })(req);
}

