     4→import { getAdminClient } from '../supabase-client';
     5→import type { WhatsappMessage } from '../types';
     6→
     7→// ====================================================================
     8→// WhatsApp Messages CRUD
     9→// ====================================================================
    10→
    11→/**
    12→ * Create a new WhatsApp message record
    13→ */
    14→export async function createWhatsappMessage(
    15→  input: Omit<WhatsappMessage, 'id' | 'created_at'>
    16→): Promise<WhatsappMessage> {
    17→  const supabase = getAdminClient();
    18→
    19→  const { data, error } = await supabase
    20→    .from('whatsapp_messages')
    21→    .insert({
    22→      instance_id: input.instance_id,
    23→      message_id: input.message_id,
    24→      from_number: input.from_number,
    25→      to_number: input.to_number,
    26→      message_type: input.message_type,
    27→      content: input.content,
    28→      media_url: input.media_url,
    29→      direction: input.direction,
    30→      status: input.status,
    31→      metadata: input.metadata,
    32→    })
    33→    .select()
    34→    .single();
    35→
    36→  if (error || !data) {
    37→    throw new Error(`Failed to create WhatsApp message: ${error?.message || 'Unknown error'}`);
    38→  }
    39→
    40→  return data;
