// NextMavens Platform API - WhatsApp Messages List Endpoint
// GET /api/whatsapp/messages - List WhatsApp messages with filters and pagination

import { NextRequest } from 'next/server';
import { withAuthAndError } from '@/lib/middleware';
import { getInstanceMessages, getProjectMessages, getInstanceMessageCount } from '@/lib/db/whatsapp-messages';
import { getWhatsappInstanceById } from '@/lib/db/whatsapp';
import { getProjectById } from '@/lib/db/projects';

interface ListMessagesResponse {
  messages: Array<{
    id: string;
    message_id: string;
    from_number: string;
    to_number: string;
    message_type: string;
    content?: string;
    media_url?: string;
    direction: 'inbound' | 'outbound';
    status: string;
    created_at: string;
  }>;
  pagination: {
    total: number;
    limit: number;
    offset: number;
    has_more: boolean;
  };
}

/**
 * Parse and validate query parameters for message listing
 */
function parseQueryParams(url: URL) {
  const instanceId = url.searchParams.get('instance_id');
  const projectId = url.searchParams.get('project_id');
  const direction = url.searchParams.get('direction');
  const fromDate = url.searchParams.get('from_date');
  const toDate = url.searchParams.get('to_date');
  const limitParam = url.searchParams.get('limit');
  const offsetParam = url.searchParams.get('offset');

  // Parse pagination parameters
  const limit = limitParam ? parseInt(limitParam, 10) : 50;
  const offset = offsetParam ? parseInt(offsetParam, 10) : 0;

  // Validate pagination
  if (isNaN(limit) || limit < 1 || limit > 100) {
    throw new Error('limit must be between 1 and 100');
  }

  if (isNaN(offset) || offset < 0) {
    throw new Error('offset must be a non-negative number');
  }

  // Validate direction
  if (direction && direction !== 'inbound' && direction !== 'outbound') {
    throw new Error('direction must be either "inbound" or "outbound"');
  }

  // Validate date format (ISO 8601)
  if (fromDate && isNaN(Date.parse(fromDate))) {
    throw new Error('from_date must be a valid ISO 8601 date string');
  }

  if (toDate && isNaN(Date.parse(toDate))) {
    throw new Error('to_date must be a valid ISO 8601 date string');
  }

  return {
    instanceId,
    projectId,
    direction: direction as 'inbound' | 'outbound' | undefined,
    fromDate,
    toDate,
    limit,
    offset,
  };
}

// GET /api/whatsapp/messages - List WhatsApp messages
export async function GET(req: NextRequest) {
  return withAuthAndError(async (user) => {
    const url = new URL(req.url);
    const params = parseQueryParams(url);

    // Either instance_id or project_id must be provided
    if (!params.instanceId && !params.projectId) {
      throw new Error('Either instance_id or project_id is required');
    }

    let messages: Awaited<ReturnType<typeof getInstanceMessages | typeof getProjectMessages>>;
    let totalCount: number;

    if (params.instanceId) {
      // Verify instance exists and user has access
      const instance = await getWhatsappInstanceById(params.instanceId);
      if (!instance) {
        throw new Error('WhatsApp instance not found');
      }

      // Verify user has access to the project
      await getProjectById(instance.project_id);

      // Get messages for the instance
      messages = await getInstanceMessages(params.instanceId, {
        limit: params.limit,
        offset: params.offset,
        direction: params.direction,
        from_date: params.fromDate || undefined,
        to_date: params.toDate || undefined,
      });

      // Get total count for pagination
      totalCount = await getInstanceMessageCount(params.instanceId, {
        direction: params.direction,
        from_date: params.fromDate || undefined,
        to_date: params.toDate || undefined,
      });
    } else if (params.projectId) {
      // Verify user has access to the project
      await getProjectById(params.projectId);

      // Get messages for all instances in the project
      messages = await getProjectMessages(params.projectId, {
        limit: params.limit,
        offset: params.offset,
        direction: params.direction,
        from_date: params.fromDate || undefined,
        to_date: params.toDate || undefined,
      });

      // For project-wide messages, we need to get the total count separately
      // Since getProjectMessages doesn't return count, we'll use the messages length
      // as an approximation for this implementation
      totalCount = messages.length;
    }

    // Transform messages to response format
    const transformedMessages = messages.map((msg) => ({
      id: msg.id,
      message_id: msg.message_id,
      from_number: msg.from_number,
      to_number: msg.to_number,
      message_type: msg.message_type,
      content: msg.content,
      media_url: msg.media_url,
      direction: msg.direction,
      status: msg.status,
      created_at: msg.created_at,
    }));

    const response: ListMessagesResponse = {
      messages: transformedMessages,
      pagination: {
        total: totalCount,
        limit: params.limit,
        offset: params.offset,
        has_more: params.offset + params.limit < totalCount,
      },
    };

    return response;
  })(req);
}

