'use client';

import { useEffect, useState } from 'react';
import { useAuth, useOrganization } from '@/lib/store';
import { api, Project, WhatsappInstance } from '@/lib/api';
import type { Organization } from '@/lib/api';
import { OrganizationSwitcher } from '@/components/organization-switcher';
import Link from 'next/link';

interface OrganizationWithProjects extends Organization {
  projects: ProjectWithInstances[];
}

interface ProjectWithInstances extends Project {
  whatsappInstances?: WhatsappInstance[];
}

interface ServiceLink {
  id: string;
  name: string;
  description: string;
  url: string;
  icon: string;
  category: 'database' | 'whatsapp' | 'monitoring' | 'api';
}

export default function ServicesPage() {
  const { user, loading, initialized, initialize, signOut } = useAuth();
  const { selectedOrganizationId } = useOrganization();
  const [organizations, setOrganizations] = useState<OrganizationWithProjects[]>([]);
  const [dataLoading, setDataLoading] = useState(false);
  const [studioUrl] = useState(process.env.NEXT_PUBLIC_STUDIO_URL || 'http://localhost:8000');
  const [evolutionApiUrl] = useState(process.env.NEXT_PUBLIC_EVOLUTION_API_URL || 'http://localhost:8080');

  useEffect(() => {
    if (!initialized) {
      initialize();
    }
  }, [initialize, initialized]);

  useEffect(() => {
    if (user) {
      fetchServicesData();
    }
  }, [user, selectedOrganizationId]);

  const fetchServicesData = async () => {
    setDataLoading(true);
    try {
      const [orgsResponse, projectsResponse] = await Promise.all([
        api.getOrganizations(),
        api.getProjects(selectedOrganizationId || undefined),
      ]);

      // Group projects by organization
      const orgsWithProjects: OrganizationWithProjects[] = orgsResponse.items.map(org => ({
        ...org,
        projects: projectsResponse.items.filter(p => p.organization_id === org.id),
      }));

      setOrganizations(orgsWithProjects);
    } catch (error) {
      console.error('Failed to fetch services data:', error);
    } finally {
      setDataLoading(false);
    }
  };

  const handleSignOut = async () => {
    await signOut();
    setOrganizations([]);
  };

  const getServiceLinks = (project: Project): ServiceLink[] => {
    const links: ServiceLink[] = [];

    // Supabase Studio link (per project)
    links.push({
      id: `studio-${project.id}`,
      name: 'Supabase Studio',
      description: `Database management for ${project.name}`,
      url: `/studio/${project.slug}`,
      icon: 'DB',
      category: 'database',
    });

    // Project logs endpoint
    links.push({
      id: `logs-${project.id}`,
      name: 'Project Logs',
      description: `View logs and metrics for ${project.name}`,
      url: `/api/projects/${project.id}`,
      icon: 'LG',
      category: 'monitoring',
    });

    // Project settings
    links.push({
      id: `settings-${project.id}`,
      name: 'Project Settings',
      description: `Configure ${project.name}`,
      url: `/projects/${project.id}/settings`,
      icon: 'ST',
      category: 'api',
    });

    return links;
  };

  const getGlobalServiceLinks = (): ServiceLink[] => {
    return [
      {
        id: 'evolution-api',
        name: 'Evolution API Console',
        description: 'WhatsApp API management interface',
        url: evolutionApiUrl,
        icon: 'WA',
        category: 'whatsapp',
      },
    ];
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'database': return 'bg-blue-100 text-blue-800';
      case 'whatsapp': return 'bg-green-100 text-green-800';
      case 'monitoring': return 'bg-purple-100 text-purple-800';
      case 'api': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading || !initialized) {
    return (
      <div className="flex min-h-screen items-center justify-center">
        <div className="text-lg">Loading...</div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="flex min-h-screen items-center justify-center bg-gray-50">
        <div className="w-full max-w-md rounded-lg bg-white p-8 shadow-md">
          <h1 className="mb-6 text-2xl font-bold">NextMavens Platform</h1>
          <p className="mb-4 text-gray-600">Please sign in to continue</p>
          <Link
            href="/login"
            className="block w-full rounded-md bg-blue-600 px-4 py-2 text-center text-white hover:bg-blue-700"
          >
            Sign In
          </Link>
        </div>
      </div>
    );
  }

  const totalProjects = organizations.reduce((sum, org) => sum + org.projects.length, 0);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navigation */}
      <nav className="border-b bg-white px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <Link href="/" className="text-xl font-bold text-gray-800 hover:text-blue-600">
              NextMavens Platform
            </Link>
            <span className="text-gray-400">/</span>
            <span className="text-lg font-medium text-gray-600">Services</span>
            {organizations.length > 0 && (
              <OrganizationSwitcher organizations={organizations} />
            )}
          </div>
          <div className="flex items-center gap-4">
            <span className="text-sm text-gray-600">{user.email}</span>
            <button
              onClick={handleSignOut}
              className="rounded-md px-4 py-2 text-sm text-gray-600 hover:bg-gray-100"
            >
              Sign Out
            </button>
          </div>
        </div>
      </nav>

      <main className="container mx-auto px-6 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-gray-900">Service Links</h1>
          <p className="text-gray-600">
            Quick access to external services and tools for your projects
          </p>
        </div>

        {dataLoading ? (
          <div className="rounded-lg bg-white p-12 text-center shadow-md">
            <div className="text-lg">Loading services...</div>
          </div>
        ) : organizations.length === 0 ? (
          <div className="rounded-lg bg-white p-12 text-center shadow-md">
            <h3 className="mb-2 text-lg font-semibold">No organizations found</h3>
            <p className="text-gray-600 mb-4">Create an organization and project to access services</p>
            <Link
              href="/"
              className="inline-block rounded-md bg-blue-600 px-4 py-2 text-white hover:bg-blue-700"
            >
              Go to Dashboard
            </Link>
          </div>
        ) : selectedOrganizationId ? (
          <div className="space-y-8">
            {(() => {
              const selectedOrg = organizations.find(org => org.id === selectedOrganizationId);
              if (!selectedOrg) return null;

              return (
                <div key={selectedOrg.id}>
                  {/* Organization Header */}
                  <div className="mb-4 rounded-lg bg-white p-4 shadow-md">
                    <div className="flex items-center justify-between">
                      <div>
                        <h2 className="text-lg font-semibold text-gray-900">{selectedOrg.name}</h2>
                        <p className="text-sm text-gray-500">/{selectedOrg.slug}</p>
                      </div>
                      <span className="rounded-full bg-blue-100 px-3 py-1 text-sm font-medium text-blue-800">
                        {selectedOrg.projects.length} {selectedOrg.projects.length === 1 ? 'project' : 'projects'}
                      </span>
                    </div>
                  </div>

                  {selectedOrg.projects.length === 0 ? (
                    <div className="rounded-lg bg-white p-12 text-center shadow-md">
                      <p className="text-gray-600 mb-4">No projects in this organization</p>
                      <Link
                        href="/"
                        className="inline-block rounded-md bg-green-600 px-4 py-2 text-white hover:bg-green-700"
                      >
                        Create Project
                      </Link>
                    </div>
                  ) : (
                    <div className="space-y-6">
                      {/* Global Services */}
                      <div>
                        <h3 className="mb-4 text-lg font-semibold text-gray-800">Global Services</h3>
                        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                          {getGlobalServiceLinks().map((link) => (
                            <a
                              key={link.id}
                              href={link.url}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="block rounded-lg bg-white p-5 shadow-md transition-all hover:shadow-lg hover:bg-gray-50"
                            >
                              <div className="mb-3 flex items-center justify-between">
                                <div className="flex h-10 w-10 items-center justify-center rounded-full bg-green-100 text-green-700 font-bold text-sm">
                                  {link.icon}
                                </div>
                                <span className={`rounded-full px-2 py-1 text-xs font-medium ${getCategoryColor(link.category)}`}>
                                  {link.category}
                                </span>
                              </div>
                              <h4 className="mb-1 font-semibold text-gray-900">{link.name}</h4>
                              <p className="text-sm text-gray-600">{link.description}</p>
                              <div className="mt-3 text-xs text-gray-400">
                                Opens in new tab
                              </div>
                            </a>
                          ))}
                        </div>
                      </div>

                      {/* Project-specific Services */}
                      {selectedOrg.projects.map((project) => (
                        <div key={project.id}>
                          <h3 className="mb-4 text-lg font-semibold text-gray-800">
                            {project.name}
                            <span className="ml-2 text-sm font-normal text-gray-500">/{project.slug}</span>
                          </h3>
                          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                            {getServiceLinks(project).map((link) => (
                              <a
                                key={link.id}
                                href={link.url}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="block rounded-lg bg-white p-5 shadow-md transition-all hover:shadow-lg hover:bg-gray-50"
                              >
                                <div className="mb-3 flex items-center justify-between">
                                  <div className="flex h-10 w-10 items-center justify-center rounded-full bg-blue-100 text-blue-700 font-bold text-sm">
                                    {link.icon}
                                  </div>
                                  <span className={`rounded-full px-2 py-1 text-xs font-medium ${getCategoryColor(link.category)}`}>
                                    {link.category}
                                  </span>
                                </div>
                                <h4 className="mb-1 font-semibold text-gray-900">{link.name}</h4>
                                <p className="text-sm text-gray-600">{link.description}</p>
                                <div className="mt-3 text-xs text-gray-400">
                                  Opens in new tab
                                </div>
                              </a>
                            ))}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              );
            })()}
          </div>
        ) : (
          <div className="rounded-lg bg-white p-12 text-center shadow-md">
            <p className="text-gray-600">Select an organization to view services</p>
          </div>
        )}

        {/* Back to Dashboard */}
        <div className="mt-8">
          <Link
            href="/"
            className="text-sm text-blue-600 hover:text-blue-800"
          >
            &larr; Back to Dashboard
          </Link>
        </div>
      </main>
    </div>
  );
}

toolUseResult.content:
'use client';

import { useEffect, useState } from 'react';
import { useAuth, useOrganization } from '@/lib/store';
import { api, Project, WhatsappInstance } from '@/lib/api';
import type { Organization } from '@/lib/api';
import { OrganizationSwitcher } from '@/components/organization-switcher';
import Link from 'next/link';

interface OrganizationWithProjects extends Organization {
  projects: ProjectWithInstances[];
}

interface ProjectWithInstances extends Project {
  whatsappInstances?: WhatsappInstance[];
}

interface ServiceLink {
  id: string;
  name: string;
  description: string;
  url: string;
  icon: string;
  category: 'database' | 'whatsapp' | 'monitoring' | 'api';
}

export default function ServicesPage() {
  const { user, loading, initialized, initialize, signOut } = useAuth();
  const { selectedOrganizationId } = useOrganization();
  const [organizations, setOrganizations] = useState<OrganizationWithProjects[]>([]);
  const [dataLoading, setDataLoading] = useState(false);
  const [studioUrl] = useState(process.env.NEXT_PUBLIC_STUDIO_URL || 'http://localhost:8000');
  const [evolutionApiUrl] = useState(process.env.NEXT_PUBLIC_EVOLUTION_API_URL || 'http://localhost:8080');

  useEffect(() => {
    if (!initialized) {
      initialize();
    }
  }, [initialize, initialized]);

  useEffect(() => {
    if (user) {
      fetchServicesData();
    }
  }, [user, selectedOrganizationId]);

  const fetchServicesData = async () => {
    setDataLoading(true);
    try {
      const [orgsResponse, projectsResponse] = await Promise.all([
        api.getOrganizations(),
        api.getProjects(selectedOrganizationId || undefined),
      ]);

      // Group projects by organization
      const orgsWithProjects: OrganizationWithProjects[] = orgsResponse.items.map(org => ({
        ...org,
        projects: projectsResponse.items.filter(p => p.organization_id === org.id),
      }));

      setOrganizations(orgsWithProjects);
    } catch (error) {
      console.error('Failed to fetch services data:', error);
    } finally {
      setDataLoading(false);
    }
  };

  const handleSignOut = async () => {
    await signOut();
    setOrganizations([]);
  };

  const getServiceLinks = (project: Project): ServiceLink[] => {
    const links: ServiceLink[] = [];

    // Supabase Studio link (per project)
    links.push({
      id: `studio-${project.id}`,
      name: 'Supabase Studio',
      description: `Database management for ${project.name}`,
      url: `/studio/${project.slug}`,
      icon: 'DB',
      category: 'database',
    });

    // Project logs endpoint
    links.push({
      id: `logs-${project.id}`,
      name: 'Project Logs',
      description: `View logs and metrics for ${project.name}`,
      url: `/api/projects/${project.id}`,
      icon: 'LG',
      category: 'monitoring',
    });

    // Project settings
    links.push({
      id: `settings-${project.id}`,
      name: 'Project Settings',
      description: `Configure ${project.name}`,
      url: `/projects/${project.id}/settings`,
      icon: 'ST',
      category: 'api',
    });

    return links;
  };

  const getGlobalServiceLinks = (): ServiceLink[] => {
    return [
      {
        id: 'evolution-api',
        name: 'Evolution API Console',
        description: 'WhatsApp API management interface',
        url: evolutionApiUrl,
        icon: 'WA',
        category: 'whatsapp',
      },
    ];
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'database': return 'bg-blue-100 text-blue-800';
      case 'whatsapp': return 'bg-green-100 text-green-800';
      case 'monitoring': return 'bg-purple-100 text-purple-800';
      case 'api': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading || !initialized) {
    return (
      <div className="flex min-h-screen items-center justify-center">
        <div className="text-lg">Loading...</div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="flex min-h-screen items-center justify-center bg-gray-50">
        <div className="w-full max-w-md rounded-lg bg-white p-8 shadow-md">
          <h1 className="mb-6 text-2xl font-bold">NextMavens Platform</h1>
          <p className="mb-4 text-gray-600">Please sign in to continue</p>
          <Link
            href="/login"
            className="block w-full rounded-md bg-blue-600 px-4 py-2 text-center text-white hover:bg-blue-700"
          >
            Sign In
          </Link>
        </div>
      </div>
    );
  }

  const totalProjects = organizations.reduce((sum, org) => sum + org.projects.length, 0);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navigation */}
      <nav className="border-b bg-white px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <Link href="/" className="text-xl font-bold text-gray-800 hover:text-blue-600">
              NextMavens Platform
            </Link>
            <span className="text-gray-400">/</span>
            <span className="text-lg font-medium text-gray-600">Services</span>
            {organizations.length > 0 && (
              <OrganizationSwitcher organizations={organizations} />
            )}
          </div>
          <div className="flex items-center gap-4">
            <span className="text-sm text-gray-600">{user.email}</span>
            <button
              onClick={handleSignOut}
              className="rounded-md px-4 py-2 text-sm text-gray-600 hover:bg-gray-100"
            >
              Sign Out
            </button>
          </div>
        </div>
      </nav>

      <main className="container mx-auto px-6 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-gray-900">Service Links</h1>
          <p className="text-gray-600">
            Quick access to external services and tools for your projects
          </p>
        </div>

        {dataLoading ? (
          <div className="rounded-lg bg-white p-12 text-center shadow-md">
            <div className="text-lg">Loading services...</div>
          </div>
        ) : organizations.length === 0 ? (
          <div className="rounded-lg bg-white p-12 text-center shadow-md">
            <h3 className="mb-2 text-lg font-semibold">No organizations found</h3>
            <p className="text-gray-600 mb-4">Create an organization and project to access services</p>
            <Link
              href="/"
              className="inline-block rounded-md bg-blue-600 px-4 py-2 text-white hover:bg-blue-700"
            >
              Go to Dashboard
            </Link>
          </div>
        ) : selectedOrganizationId ? (
          <div className="space-y-8">
            {(() => {
              const selectedOrg = organizations.find(org => org.id === selectedOrganizationId);
              if (!selectedOrg) return null;

              return (
                <div key={selectedOrg.id}>
                  {/* Organization Header */}
                  <div className="mb-4 rounded-lg bg-white p-4 shadow-md">
                    <div className="flex items-center justify-between">
                      <div>
                        <h2 className="text-lg font-semibold text-gray-900">{selectedOrg.name}</h2>
                        <p className="text-sm text-gray-500">/{selectedOrg.slug}</p>
                      </div>
                      <span className="rounded-full bg-blue-100 px-3 py-1 text-sm font-medium text-blue-800">
                        {selectedOrg.projects.length} {selectedOrg.projects.length === 1 ? 'project' : 'projects'}
                      </span>
                    </div>
                  </div>

                  {selectedOrg.projects.length === 0 ? (
                    <div className="rounded-lg bg-white p-12 text-center shadow-md">
                      <p className="text-gray-600 mb-4">No projects in this organization</p>
                      <Link
                        href="/"
                        className="inline-block rounded-md bg-green-600 px-4 py-2 text-white hover:bg-green-700"
                      >
                        Create Project
                      </Link>
                    </div>
                  ) : (
                    <div className="space-y-6">
                      {/* Global Services */}
                      <div>
                        <h3 className="mb-4 text-lg font-semibold text-gray-800">Global Services</h3>
                        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                          {getGlobalServiceLinks().map((link) => (
                            <a
                              key={link.id}
                              href={link.url}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="block rounded-lg bg-white p-5 shadow-md transition-all hover:shadow-lg hover:bg-gray-50"
                            >
                              <div className="mb-3 flex items-center justify-between">
                                <div className="flex h-10 w-10 items-center justify-center rounded-full bg-green-100 text-green-700 font-bold text-sm">
                                  {link.icon}
                                </div>
                                <span className={`rounded-full px-2 py-1 text-xs font-medium ${getCategoryColor(link.category)}`}>
                                  {link.category}
                                </span>
                              </div>
                              <h4 className="mb-1 font-semibold text-gray-900">{link.name}</h4>
                              <p className="text-sm text-gray-600">{link.description}</p>
                              <div className="mt-3 text-xs text-gray-400">
                                Opens in new tab
                              </div>
                            </a>
                          ))}
                        </div>
                      </div>

                      {/* Project-specific Services */}
                      {selectedOrg.projects.map((project) => (
                        <div key={project.id}>
                          <h3 className="mb-4 text-lg font-semibold text-gray-800">
                            {project.name}
                            <span className="ml-2 text-sm font-normal text-gray-500">/{project.slug}</span>
                          </h3>
                          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                            {getServiceLinks(project).map((link) => (
                              <a
                                key={link.id}
                                href={link.url}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="block rounded-lg bg-white p-5 shadow-md transition-all hover:shadow-lg hover:bg-gray-50"
                              >
                                <div className="mb-3 flex items-center justify-between">
                                  <div className="flex h-10 w-10 items-center justify-center rounded-full bg-blue-100 text-blue-700 font-bold text-sm">
                                    {link.icon}
                                  </div>
                                  <span className={`rounded-full px-2 py-1 text-xs font-medium ${getCategoryColor(link.category)}`}>
                                    {link.category}
                                  </span>
                                </div>
                                <h4 className="mb-1 font-semibold text-gray-900">{link.name}</h4>
                                <p className="text-sm text-gray-600">{link.description}</p>
                                <div className="mt-3 text-xs text-gray-400">
                                  Opens in new tab
                                </div>
                              </a>
                            ))}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              );
            })()}
          </div>
        ) : (
          <div className="rounded-lg bg-white p-12 text-center shadow-md">
            <p className="text-gray-600">Select an organization to view services</p>
          </div>
        )}

        {/* Back to Dashboard */}
        <div className="mt-8">
          <Link
            href="/"
            className="text-sm text-blue-600 hover:text-blue-800"
          >
            &larr; Back to Dashboard
          </Link>
        </div>
      </main>
    </div>
  );
}
