   207→/**
   208→ * Validate request body against a schema
   209→ */
   210→export function validateBody<T>(body: unknown, schema: {
   211→  required?: (keyof T)[];
   212→  optional?: (keyof T)[];
   213→  validators?: Partial<Record<keyof T, (value: unknown) => boolean | string>>;
   214→}): T {
   215→  const result = {} as T;
   216→
   217→  // Check required fields
   218→  if (schema.required) {
   219→    for (const field of schema.required) {
   220→      const fieldStr = String(field);
   221→      if (body && typeof body === 'object' && fieldStr in body) {
   222→        const value = (body as Record<string, unknown>)[fieldStr];
   223→
   224→        // Run validator if present
   225→        if (schema.validators?.[field]) {
   226→          const validation = schema.validators[field](value);
   227→          if (validation === true) {
   228→            (result as Record<string, unknown>)[fieldStr] = value;
   229→          } else {
   230→            throw new ValidationError(
   231→              typeof validation === 'string' ? validation : `Invalid ${fieldStr}`,
   232→              fieldStr
   233→            );
   234→          }
   235→        } else {
   236→          (result as Record<string, unknown>)[fieldStr] = value;
   237→        }
   238→      } else {
   239→        throw new ValidationError(`${fieldStr} is required`, fieldStr);
   240→      }
   241→    }
   242→  }
   243→
   244→  // Check optional fields
   245→  if (schema.optional && body && typeof body === 'object') {
   246→    for (const field of schema.optional) {
   247→      const fieldStr = String(field);
   248→      if (fieldStr in body) {
   249→        const value = (body as Record<string, unknown>)[fieldStr];
   250→
   251→        // Run validator if present
   252→        if (schema.validators?.[field]) {
   253→          const validation = schema.validators[field](value);
   254→          if (validation === true) {
   255→            (result as Record<string, unknown>)[fieldStr] = value;
   256→          } else if (validation !== false) {
   257→            throw new ValidationError(
   258→              typeof validation === 'string' ? validation : `Invalid ${fieldStr}`,
   259→              fieldStr
   260→            );
   261→          }
   262→        } else {
   263→          (result as Record<string, unknown>)[fieldStr] = value;
   264→        }
   265→      }
   266→    }
   267→  }
   268→
   269→  return result;
   270→}
   271→
   272→/**
   273→ * Common validators
   274→ */
   275→export const validators = {
   276→  string: (value: unknown): boolean => typeof value === 'string',
   277→  email: (value: unknown): boolean => {
   278→    if (typeof value !== 'string') return false;
   279→    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value);
   280→  },
   281→  slug: (value: unknown): boolean => {
   282→    if (typeof value !== 'string') return false;
   283→    return /^[a-z0-9-]+$/.test(value) && value.length >= 2 && value.length <= 50;
   284→  },
   285→  minLength: (min: number) => (value: unknown): boolean | string => {
   286→    if (typeof value !== 'string') return 'Must be a string';
   287→    if (value.length < min) return `Must be at least ${min} characters`;
   288→    return true;
   289→  },
   290→  maxLength: (max: number) => (value: unknown): boolean | string => {
   291→    if (typeof value !== 'string') return 'Must be a string';
   292→    if (value.length > max) return `Must be at most ${max} characters`;
   293→    return true;
   294→  },
   295→  url: (value: unknown): boolean => {
   296→    if (typeof value !== 'string') return false;
   297→    try {
   298→      new URL(value);
   299→      return true;
   300→    } catch {
   301→      return false;
   302→    }
   303→  },
   304→  uuid: (value: unknown): boolean => {
   305→    if (typeof value !== 'string') return false;
   306→    return /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(value);
   307→  },
   308→  enum: <T extends string>(values: T[]) => (value: unknown): boolean | string => {
   309→    if (typeof value !== 'string') return 'Must be a string';
   310→    if (!values.includes(value as T)) return `Must be one of: ${values.join(', ')}`;
   311→    return true;
   312→  },
   313→};
   314→
   315→// ====================================================================
   316→// Error Response Helpers
   317→// ====================================================================
   318→
   319→/**
   320→ * Convert an error to an appropriate HTTP response
   321→ * Security: Never expose internal error details to the client
   322→ */
   323→export function errorToResponse(error: unknown, includeRequestId = true): Response {
   324→  const requestId = includeRequestId ? crypto.randomUUID() : undefined;
   325→
   326→  if (error instanceof AuthError) {
   327→    return Response.json(
   328→      {
   329→        error: {
   330→          code: error.code,
   331→          message: error.message,
   332→          ...(requestId && { requestId }),
   333→        },
   334→      },
   335→      { status: 401 }
   336→    );
   337→  }
   338→
   339→  if (error instanceof ValidationError) {
   340→    return Response.json(
   341→      {
   342→        error: {
   343→          code: 'VALIDATION_ERROR',
   344→          message: error.message,
   345→          details: error.field ? { field: error.field } : undefined,
   346→          ...(requestId && { requestId }),
   347→        },
   348→      },
   349→      { status: 400 }
   350→    );
   351→  }
   352→
   353→  if (error instanceof NotFoundError) {
   354→    return Response.json(
   355→      {
   356→        error: {
   357→          code: 'NOT_FOUND',
   358→          message: error.message,
   359→          ...(requestId && { requestId }),
   360→        },
   361→      },
   362→      { status: 404 }
   363→    );
   364→  }
   365→
   366→  if (error instanceof PermissionError) {
   367→    return Response.json(
   368→      {
   369→        error: {
   370→          code: 'FORBIDDEN',
   371→          message: error.message,
   372→          ...(requestId && { requestId }),
   373→        },
   374→      },
   375→      { status: 403 }
   376→    );
   377→  }
   378→
   379→  // Log unexpected errors with request ID for debugging
   380→  const errorMessage = error instanceof Error ? error.message : 'Unknown error';
   381→  const errorStack = error instanceof Error ? error.stack : undefined;
   382→
   383→  console.error('[API Error]', {
   384→    requestId,
   385→    message: errorMessage,
   386→    stack: errorStack,
   387→    timestamp: new Date().toISOString(),
   388→  });
   389→
   390→  // Return generic error message to client
   391→  return Response.json(
   392→    {
   393→      error: {
   394→        code: 'INTERNAL_ERROR',
   395→        message: 'An unexpected error occurred. Please try again later.',
   396→        ...(requestId && { requestId }),
   397→      },
   398→    },
   399→    { status: 500 }
   400→  );
   401→}
   402→
   403→// ====================================================================
   404→// Request Wrapper
   405→// ====================================================================
   406→
   407→/**
   408→ * Wrap an API handler with authentication
   409→ */
   410→export function withAuth<T>(
   411→  handler: (user: AuthUser, req: NextRequest) => Promise<T>
   412→) {
   413→  return async (req: NextRequest): Promise<T> => {
   414→    try {
   415→      const user = await getAuthenticatedUser(req);
   416→      return await handler(user, req);
   417→    } catch (error) {
   418→      throw error;
   419→    }
   420→  };
   421→}
   422→
   423→/**
   424→ * Wrap an API handler with optional authentication
   425→ */
   426→export function withOptionalAuth<T>(
   427→  handler: (user: AuthUser | null, req: NextRequest) => Promise<T>
   428→) {
   429→  return async (req: NextRequest): Promise<T> => {
   430→    const user = await getOptionalUser(req);
   431→    return await handler(user, req);
   432→  };
   433→}
   434→
   435→/**
   436→ * Wrap an API handler with error handling
   437→ */
   438→export function withErrorHandler<T>(
   439→  handler: () => Promise<T>
   440→): Promise<T> {
   441→  return handler();
   442→}
   443→
   444→/**
   445→ * Wrap an API handler with both auth and error handling
   446→ */
   447→export function withAuthAndError<T>(
   448→  handler: (user: AuthUser, req: NextRequest) => Promise<T>
   449→) {
   450→  return async (req: NextRequest): Promise<Response> => {
   451→    try {
   452→      const user = await getAuthenticatedUser(req);
   453→      const result = await handler(user, req);
   454→      return Response.json({ success: true, data: result });
   455→    } catch (error) {
   456→      return errorToResponse(error);
   457→    }
   458→  };
   459→}
