// NextMavens Platform - Centralized Supabase Client
// This module provides the main Supabase client configuration for the API layer

import { createClient, SupabaseClient } from '@supabase/supabase-js';

// ====================================================================
// Environment Configuration
// ====================================================================

const getSupabaseConfig = () => {
  const url = process.env.SUPABASE_URL;
  const serviceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;
  const anonKey = process.env.SUPABASE_ANON_KEY;

  if (!url) {
    throw new Error('SUPABASE_URL environment variable is required');
  }

  if (!serviceKey) {
    throw new Error('SUPABASE_SERVICE_ROLE_KEY environment variable is required');
  }

  return { url, serviceKey, anonKey: anonKey || '' };
};

// ====================================================================
// Client Creation
// ====================================================================

/**
 * Create a Supabase admin client with service role privileges
 * Use this for server-side operations that bypass RLS
 */
export function createAdminClient(): SupabaseClient {
  const config = getSupabaseConfig();

  return createClient(config.url, config.serviceKey, {
    auth: {
      autoRefreshToken: false,
      persistSession: false,
    },
    db: {
      schema: 'public',
    },
    global: {
      headers: {
        'x-service-role': 'true',
      },
    },
  });
}

/**
 * Create a Supabase client with anon key
 * Use this for operations that should respect RLS
 */
export function createAnonClient(): SupabaseClient {
  const config = getSupabaseConfig();

  if (!config.anonKey) {
    throw new Error('SUPABASE_ANON_KEY environment variable is required for anon client');
  }

  return createClient(config.url, config.anonKey, {
    auth: {
      autoRefreshToken: true,
      persistSession: false,
    },
    db: {
      schema: 'public',
    },
  });
}

/**
 * Create a Supabase client for a specific project database
 * This is used for tenant database operations
 */
export function createProjectClient(config: {
  url: string;
  anonKey: string;
  schema?: string;
}): SupabaseClient {
  return createClient(config.url, config.anonKey, {
    auth: {
      autoRefreshToken: true,
      persistSession: false,
    },
    db: {
      schema: config.schema || 'public',
    },
  });
}

/**
 * Create a Supabase client with a specific user's token
 * Use this for operations on behalf of a user
 */
export function createUserClient(token: string): SupabaseClient {
  const config = getSupabaseConfig();

  return createClient(config.url, config.anonKey, {
    auth: {
      autoRefreshToken: false,
      persistSession: false,
    },
    global: {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    },
  });
}

// ====================================================================
// Singleton Instances
// ====================================================================

/**
 * Shared admin client instance
 * Reuse this for most server-side operations
 */
let adminClientInstance: SupabaseClient | null = null;

export function getAdminClient(): SupabaseClient {
  if (!adminClientInstance) {
    adminClientInstance = createAdminClient();
  }
  return adminClientInstance;
}

/**
 * Shared anon client instance
 * Reuse this for client operations
 */
let anonClientInstance: SupabaseClient | null = null;

export function getAnonClient(): SupabaseClient {
  if (!anonClientInstance) {
    anonClientInstance = createAnonClient();
  }
  return anonClientInstance;
}

// ====================================================================
// Connection Pool Configuration
// ====================================================================

/**
 * Connection pool configuration reference for Supavisor
 *
 * Supavisor is Supabase's connection pooler that provides:
 * - Efficient connection management
 * - Reduced database connection overhead
 * - Better performance for serverless environments
 *
 * Configuration is done via environment variables in the Supabase instance:
 * - DB_USE_POOLER=true
 * - DB_POOLER_URL=postgresql://postgres:[YOUR-PASSWORD]@db.[YOUR-REF].supabase.co:6543/postgres
 * - DB_POOLER_MODE=transaction
 *
 * For self-hosted Supabase, configure Supavisor in docker-compose.yml:
 *
 * supavisor:
 *   image: supabase/supavisor:latest
 *   environment:
 *     - PORT=4000
 *     - PGHOST=db
 *     - PGPORT=5432
 *     - PGDATABASE=postgres
 *     - PGUSER=postgres
 *     - PGPASSWORD=your-password
 *   ports:
 *     - "4000:4000"
 *
 * Connection string format:
 * postgresql://postgres:[YOUR-PASSWORD]@localhost:4000/postgres
 */
export const CONNECTION_POOL_CONFIG = {
  mode: 'transaction', // 'session' or 'transaction'
  defaultPoolSize: 15, // Default for transaction mode
  maxPoolSize: 50, // Maximum connections
  minPoolSize: 2, // Minimum idle connections
  acquireTimeoutMillis: 10000, // Connection acquisition timeout
  idleTimeoutMillis: 30000, // Idle connection timeout
  reapIntervalMillis: 1000, // Connection check interval
} as const;

// ====================================================================
// Type Exports
// ====================================================================

export type { SupabaseClient } from '@supabase/supabase-js';

