     1→// NextMavens Platform API - Projects Endpoint
     2→// POST /api/projects - Create project (with database)
     3→// GET /api/projects - List user's projects
     4→
     5→import { NextRequest, NextResponse } from 'next/server';
     6→import { createClient } from '@supabase/supabase-js';
     7→import { Pool } from 'pg';
     8→import { slugify, generateApiKey, hashApiKey, buildConnectionString } from '../lib/utils';
     9→import type { Project, CreateProjectInput, ProjectDatabaseConnection } from '../types';
    10→
    11→const supabaseUrl = process.env.SUPABASE_URL || 'http://localhost:8000';
    12→const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY || '';
    13→const masterDbUrl = process.env.MASTER_DB_URL || 'postgres://postgres:postgres@localhost:5432/postgres';
    14→
    15→// Helper to get authenticated user
    16→async function getAuthenticatedUser(req: NextRequest) {
    17→  const authHeader = req.headers.get('authorization');
    18→  if (!authHeader?.startsWith('Bearer ')) {
    19→    return null;
    20→  }
    21→
    22→  const token = authHeader.substring(7);
    23→  const supabase = createClient(supabaseUrl, supabaseServiceKey);
    24→  const { data: { user }, error } = await supabase.auth.getUser(token);
    25→
    26→  if (error || !user) {
    27→    return null;
    28→  }
    29→
    30→  return user;
    31→}
    32→
    33→// GET /api/projects - List projects
    34→export async function GET(req: NextRequest) {
    35→  try {
    36→    const user = await getAuthenticatedUser(req);
    37→    if (!user) {
    38→      return NextResponse.json({ error: { code: 'UNAUTHORIZED', message: 'Invalid or missing token' } }, { status: 401 });
    39→    }
    40→
    41→    const { searchParams } = new URL(req.url);
    42→    const orgId = searchParams.get('org_id');
    43→    const status = searchParams.get('status');
    44→
    45→    const supabase = createClient(supabaseUrl, supabaseServiceKey);
    46→
    47→    // Build query
    48→    let query = supabase
    49→      .from('projects')
    50→      .select('*')
    51→      .order('created_at', { ascending: false });
    52→
    53→    // Filter by organization if specified
    54→    if (orgId) {
    55→      // Verify user is member of org
    56→      const { data: membership } = await supabase
    57→        .from('organization_members')
    58→        .select('role')
    59→        .eq('organization_id', orgId)
    60→        .eq('user_id', user.id)
    61→        .single();
    62→
    63→      if (!membership) {
    64→        return NextResponse.json({ error: { code: 'FORBIDDEN', message: 'Not a member of this organization' } }, { status: 403 });
    65→      }
    66→
    67→      query = query.eq('organization_id', orgId);
    68→    } else {
    69→      // Otherwise get all projects from user's organizations
