// NextMavens Platform - Project JWT Signing
// Issue and verify project-scoped JWTs for external service authentication

import { SignJWT, jwtVerify, type JWTVerifyResult } from 'jose';
import type { Project } from '../types';

// ====================================================================
// Types
// ====================================================================

export interface ProjectJwtPayload {
  project_id: string;
  scopes: string[];
  type: 'project';
}

export interface ProjectJwtToken {
  token: string;
  expires_at: string;
}

export interface JwtVerificationResult {
  valid: boolean;
  payload?: ProjectJwtPayload;
  error?: string;
}

// ====================================================================
// Configuration
// ====================================================================

const JWT_EXPIRATION_MS = 60 * 60 * 1000; // 1 hour
const JWT_ISSUER = 'nextmavens-platform';

// ====================================================================
// Helper Functions
// ====================================================================

/**
 * Get the JWT secret for a project
 * Throws an error if the project has no JWT secret configured
 */
export function getProjectJwtSecret(project: Project): string {
  if (!project.jwt_secret) {
    throw new Error('Project does not have a JWT secret configured');
  }
  return project.jwt_secret;
}

/**
 * Create a text encoder for JWT signing
 */
function textEncoder(): TextEncoder {
  return new TextEncoder();
}

// ====================================================================
// JWT Signing
// ====================================================================

/**
 * Issue a project-scoped JWT token
 *
 * @param project - The project to issue a token for
 * @param scopes - Scopes to include in the token (defaults to ['read', 'write'])
 * @returns The signed JWT token with expiration
 */
export async function issueProjectJwt(
  project: Project,
  scopes: string[] = ['read', 'write']
): Promise<ProjectJwtToken> {
  const secret = getProjectJwtSecret(project);
  const key = textEncoder().encode(secret);

  const now = Date.now();
  const expiresAt = new Date(now + JWT_EXPIRATION_MS);

  const payload: ProjectJwtPayload = {
    project_id: project.id,
    scopes,
    type: 'project',
  };

  const token = await new SignJWT(payload)
    .setProtectedHeader({ alg: 'HS256' })
    .setIssuedAt(now)
    .setExpirationTime(expiresAt.getTime())
    .setIssuer(JWT_ISSUER)
    .setSubject(project.id)
    .sign(key);

  return {
    token,
    expires_at: expiresAt.toISOString(),
  };
}

// ====================================================================
// JWT Verification
// ====================================================================

/**
 * Verify a project JWT token
 *
 * @param token - The JWT token to verify
 * @param project - The project to verify against (provides the secret)
 * @returns Verification result with payload if valid
 */
export async function verifyProjectJwt(
  token: string,
  project: Project
): Promise<JwtVerificationResult> {
  try {
    const secret = getProjectJwtSecret(project);
    const key = textEncoder().encode(secret);

    const result: JWTVerifyResult = await jwtVerify(token, key, {
      issuer: JWT_ISSUER,
      subject: project.id,
    });

    const payload = result.payload as unknown as ProjectJwtPayload;

    // Verify the payload structure
    if (!payload.project_id || !payload.scopes || payload.type !== 'project') {
      return {
        valid: false,
        error: 'Invalid JWT payload structure',
      };
    }

    // Verify the project ID matches
    if (payload.project_id !== project.id) {
      return {
        valid: false,
        error: 'Project ID mismatch',
      };
    }

    return {
      valid: true,
      payload,
    };
  } catch (error) {
    if (error instanceof Error) {
      // Handle specific JWT errors
      if (error.name === 'JWTExpired') {
        return {
          valid: false,
          error: 'Token has expired',
        };
      }
      if (error.name === 'JWTInvalid') {
        return {
          valid: false,
          error: 'Invalid token format',
        };
      }
      if (error.name === 'JWSSignatureVerificationFailed') {
        return {
          valid: false,
          error: 'Invalid signature',
        };
      }
      return {
        valid: false,
        error: error.message,
      };
    }
    return {
      valid: false,
      error: 'Unknown verification error',
    };
  }
}

/**
 * Verify a project JWT token by project ID
 * This variant fetches the project from the database
 *
 * @param token - The JWT token to verify
 * @param projectId - The project ID to verify against
 * @returns Verification result with payload if valid
 */
export async function verifyProjectJwtById(
  token: string,
  projectId: string
): Promise<JwtVerificationResult> {
  try {
    // Import here to avoid circular dependency
    const { createClient } = await import('@supabase/supabase-js');

    const supabaseUrl = process.env.SUPABASE_URL || 'http://localhost:8000';
    const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY || '';
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    const { data: project, error } = await supabase
      .from('projects')
      .select('id, jwt_secret')
      .eq('id', projectId)
      .single();

    if (error || !project) {
      return {
        valid: false,
        error: 'Project not found',
      };
    }

    return verifyProjectJwt(token, project);
  } catch (error) {
    return {
      valid: false,
      error: 'Failed to verify token',
    };
  }
}

/**
 * Check if a project JWT has a specific scope
 *
 * @param payload - The verified JWT payload
 * @param scope - The scope to check for
 * @returns true if the token has the scope
 */
export function hasProjectJwtScope(payload: ProjectJwtPayload, scope: string): boolean {
  return payload.scopes.includes(scope);
}

/**
 * Check if a project JWT has any of the specified scopes
 *
 * @param payload - The verified JWT payload
 * @param scopes - The scopes to check for
 * @returns true if the token has any of the scopes
 */
export function hasAnyProjectJwtScope(payload: ProjectJwtPayload, scopes: string[]): boolean {
  return scopes.some(scope => payload.scopes.includes(scope));
}

/**
 * Decode a JWT token without verification (for debugging/inspection)
 * Warning: This does not verify the signature, only decodes the payload
 *
 * @param token - The JWT token to decode
 * @returns The decoded payload or null if invalid
 */
export function decodeUnsafeJwt(token: string): ProjectJwtPayload | null {
  try {
    const parts = token.split('.');
    if (parts.length !== 3) {
      return null;
    }

    const payload = parts[1];
    const decoded = atob(payload);
    return JSON.parse(decoded) as ProjectJwtPayload;
  } catch {
    return null;
  }
}

