// NextMavens Platform API - JWT Verification Endpoint
// POST /api/auth/verify - Verify a project JWT token

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { verifyProjectJwt, decodeUnsafeJwt } from '../../lib/jwt';

const supabaseUrl = process.env.SUPABASE_URL || 'http://localhost:8000';
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY || '';

// Helper to get authenticated user (optional for public endpoint)
async function getAuthenticatedUser(req: NextRequest) {
  const authHeader = req.headers.get('authorization');
  if (!authHeader?.startsWith('Bearer ')) {
    return null;
  }

  const token = authHeader.substring(7);
  const supabase = createClient(supabaseUrl, supabaseServiceKey);
  const { data: { user }, error } = await supabase.auth.getUser(token);

  if (error || !user) {
    return null;
  }

  return user;
}

// POST /api/auth/verify - Verify a project JWT token
export async function POST(req: NextRequest) {
  try {
    const body = await req.json();

    // Validate request body
    if (!body.token) {
      return NextResponse.json(
        { error: { code: 'VALIDATION_ERROR', message: 'token is required' } },
        { status: 400 }
      );
    }

    if (!body.project_id) {
      return NextResponse.json(
        { error: { code: 'VALIDATION_ERROR', message: 'project_id is required' } },
        { status: 400 }
      );
    }

    // Optionally verify the requester has access to the project
    const user = await getAuthenticatedUser(req);
    if (user) {
      const supabase = createClient(supabaseUrl, supabaseServiceKey);

      const { data: project } = await supabase
        .from('projects')
        .select('organization_id')
        .eq('id', body.project_id)
        .single();

      if (project) {
        const { data: membership } = await supabase
          .from('organization_members')
          .select('role')
          .eq('organization_id', project.organization_id)
          .eq('user_id', user.id)
          .single();

        if (!membership) {
          return NextResponse.json(
            { error: { code: 'FORBIDDEN', message: 'You do not have access to this project' } },
            { status: 403 }
          );
        }
      }
    }

    // Fetch project with JWT secret
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    const { data: project, error } = await supabase
      .from('projects')
      .select('id, jwt_secret, status')
      .eq('id', body.project_id)
      .single();

    if (error || !project) {
      return NextResponse.json(
        { error: { code: 'NOT_FOUND', message: 'Project not found' } },
        { status: 404 }
      );
    }

    // Check if project is active
    if (project.status !== 'active') {
      return NextResponse.json(
        { error: { code: 'PROJECT_INACTIVE', message: 'Project is not active' } },
        { status: 403 }
      );
    }

    // Verify the JWT token
    const result = await verifyProjectJwt(body.token, project);

    if (result.valid && result.payload) {
      return NextResponse.json({
        success: true,
        data: {
          valid: true,
          payload: {
            project_id: result.payload.project_id,
            scopes: result.payload.scopes,
            type: result.payload.type,
          },
        },
      });
    }

    // Return error details for debugging
    const unsafeDecoded = decodeUnsafeJwt(body.token);
    return NextResponse.json({
      success: true,
      data: {
        valid: false,
        error: result.error || 'Token verification failed',
        ...(unsafeDecoded && {
          decoded_payload: {
            project_id: unsafeDecoded.project_id,
            scopes: unsafeDecoded.scopes,
            type: unsafeDecoded.type,
          },
        }),
      },
    });
  } catch (error: unknown) {
    console.error('Error verifying JWT:', error);
    return NextResponse.json(
      { error: { code: 'INTERNAL_ERROR', message: error instanceof Error ? error.message : 'Failed to verify token' } },
      { status: 500 }
    );
  }
}

