// NextMavens Platform API - Project JWT Endpoint
// POST /api/projects/:id/jwt - Issue a project-scoped JWT token

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { issueProjectJwt } from '../../../lib/jwt';

const supabaseUrl = process.env.SUPABASE_URL || 'http://localhost:8000';
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY || '';

// Helper to get authenticated user
async function getAuthenticatedUser(req: NextRequest) {
  const authHeader = req.headers.get('authorization');
  if (!authHeader?.startsWith('Bearer ')) {
    return null;
  }

  const token = authHeader.substring(7);
  const supabase = createClient(supabaseUrl, supabaseServiceKey);
  const { data: { user }, error } = await supabase.auth.getUser(token);

  if (error || !user) {
    return null;
  }

  return user;
}

// Helper to check if user can access project
async function canAccessProject(userId: string, projectId: string): Promise<boolean> {
  const supabase = createClient(supabaseUrl, supabaseServiceKey);

  const { data, error } = await supabase
    .from('projects')
    .select('organization_id')
    .eq('id', projectId)
    .single();

  if (error || !data) {
    return false;
  }

  // Check if user is member of the organization
  const { data: membership } = await supabase
    .from('organization_members')
    .select('role')
    .eq('organization_id', data.organization_id)
    .eq('user_id', userId)
    .single();

  return !!membership;
}

// POST /api/projects/:id/jwt - Issue a project-scoped JWT token
export async function POST(
  req: NextRequest,
  { params }: { params: { id: string } }
) {
  try {
    const user = await getAuthenticatedUser(req);
    if (!user) {
      return NextResponse.json(
        { error: { code: 'UNAUTHORIZED', message: 'Invalid or missing token' } },
        { status: 401 }
      );
    }

    const hasAccess = await canAccessProject(user.id, params.id);
    if (!hasAccess) {
      return NextResponse.json(
        { error: { code: 'FORBIDDEN', message: 'Access denied' } },
        { status: 403 }
      );
    }

    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    // Get project with JWT secret
    const { data: project, error } = await supabase
      .from('projects')
      .select('*')
      .eq('id', params.id)
      .single();

    if (error || !project) {
      return NextResponse.json(
        { error: { code: 'NOT_FOUND', message: 'Project not found' } },
        { status: 404 }
      );
    }

    // Check if project is active
    if (project.status !== 'active') {
      return NextResponse.json(
        { error: { code: 'PROJECT_INACTIVE', message: 'Project must be active to issue JWT tokens' } },
        { status: 403 }
      );
    }

    // Parse request body for optional scopes
    let scopes: string[] = ['read', 'write'];
    try {
      const body = await req.json();
      if (body.scopes && Array.isArray(body.scopes)) {
        // Validate scopes
        const validScopes = ['read', 'write', 'admin'];
        scopes = body.scopes.filter((s: string) => validScopes.includes(s));
        if (scopes.length === 0) {
          scopes = ['read', 'write'];
        }
      }
    } catch {
      // Use default scopes if body parsing fails
    }

    // Issue the JWT token
    const jwtResult = await issueProjectJwt(project, scopes);

    return NextResponse.json({
      success: true,
      data: {
        token: jwtResult.token,
        expires_at: jwtResult.expires_at,
        project_id: project.id,
        project_name: project.name,
        scopes,
      },
    });
  } catch (error: unknown) {
    console.error('Error issuing JWT:', error);
    return NextResponse.json(
      { error: { code: 'INTERNAL_ERROR', message: error instanceof Error ? error.message : 'Failed to issue JWT token' } },
      { status: 500 }
    );
  }
}

