// NextMavens Platform API - WhatsApp Instance Status Endpoint
// GET /api/whatsapp/status/[instanceName] - Get connection status for an instance

import { NextRequest } from 'next/server';
import { withAuthAndError } from '@/lib/middleware';
import { getWhatsappInstanceByName, updateWhatsappInstanceStatus } from '@/lib/db/whatsapp';
import { getProjectById } from '@/lib/db/projects';
import type { WhatsappInstance } from '@/lib/types';

interface EvolutionStatusResponse {
  state: string;
  status?: string;
  user?: {
    id: string;
    name: string;
    phone: string;
  };
  battery?: number;
  plugged?: boolean;
}

// Map Evolution API state to our status enum
function mapEvolutionStateToStatus(state: string): WhatsappInstance['status'] {
  switch (state) {
    case 'open':
      return 'connected';
    case 'close':
      return 'disconnected';
    case 'connecting':
      return 'connecting';
    default:
      return 'pending';
  }
}

// GET /api/whatsapp/status/[instanceName] - Get connection status for an instance
export async function GET(
  req: NextRequest,
  { params }: { params: Promise<{ instanceName: string }> }
) {
  return withAuthAndError(async (user) => {
    const { instanceName } = await params;
    const url = new URL(req.url);
    const projectId = url.searchParams.get('project_id');

    if (!projectId) {
      throw new Error('project_id is required');
    }

    // Verify user has access to the project
    const project = await getProjectById(projectId);

    // Verify instance exists and belongs to the project
    const instance = await getWhatsappInstanceByName(projectId, instanceName);
    if (!instance) {
      throw new Error('WhatsApp instance not found');
    }

    // Call Evolution API to get connection status
    const EVOLUTION_API_URL = process.env.EVOLUTION_API_URL || 'http://localhost:8080';
    const EVOLUTION_API_KEY = process.env.EVOLUTION_API_KEY || '';

    const evolutionResponse = await fetch(`${EVOLUTION_API_URL}/instance/connectionState/${instanceName}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        ...(EVOLUTION_API_KEY && { 'apikey': EVOLUTION_API_KEY }),
      },
    });

    if (!evolutionResponse.ok) {
      const errorText = await evolutionResponse.text();
      console.error('Evolution API status fetch error:', evolutionResponse.status, errorText);
      throw new Error('Failed to fetch status from Evolution API');
    }

    const statusData: EvolutionStatusResponse = await evolutionResponse.json();

    // Map Evolution API response to our format
    const newStatus = mapEvolutionStateToStatus(statusData.state);
    const phoneNumber = statusData.user?.phone;
    const battery = statusData.battery;

    // Update instance record in database if status changed
    if (newStatus !== instance.status || (phoneNumber && phoneNumber !== instance.phone_number)) {
      await updateWhatsappInstanceStatus(
        instance.id,
        newStatus,
        {
          phone_number: phoneNumber,
          connected_at: newStatus === 'connected' && !instance.connected_at ? new Date().toISOString() : undefined,
        }
      );
    }

    return {
      success: true,
      state: statusData.state,
      status: newStatus,
      phone_number: phoneNumber,
      battery: battery,
    };
  })(req);
}

