     1→// NextMavens Platform - WhatsApp Instances Data Access Layer
     2→// Centralized data access patterns for whatsapp_instances table
     3→
     4→import { getAdminClient } from '../supabase-client';
     5→import type { WhatsappInstance, CreateWhatsappInstanceInput } from '../types';
     6→import { NotFoundError } from '../middleware';
     7→
     8→// ====================================================================
     9→// WhatsApp Instances CRUD
    10→// ====================================================================
    11→
    12→/**
    13→ * Get all WhatsApp instances for a user's projects
    14→ */
    15→export async function getUserWhatsappInstances(userId: string): Promise<WhatsappInstance[]> {
    16→  const supabase = getAdminClient();
    17→
    18→  // Get user's organizations
    19→  const { data: memberships, error: memberError } = await supabase
    20→    .from('organization_members')
    21→    .select('organization_id')
    22→    .eq('user_id', userId);
    23→
    24→  if (memberError) {
    25→    throw new Error(`Failed to fetch memberships: ${memberError.message}`);
    26→  }
    27→
    28→  if (!memberships || memberships.length === 0) {
    29→    return [];
    30→  }
    31→
    32→  const orgIds = memberships.map((m) => m.organization_id);
    33→
    34→  // Get projects from these organizations
    35→  const { data: projects, error: projectsError } = await supabase
    36→    .from('projects')
    37→    .select('id')
    38→    .in('organization_id', orgIds);
    39→
    40→  if (projectsError) {
    41→    throw new Error(`Failed to fetch projects: ${projectsError.message}`);
    42→  }
    43→
    44→  if (!projects || projects.length === 0) {
    45→    return [];
    46→  }
    47→
    48→  const projectIds = projects.map((p) => p.id);
    49→
    50→  // Get WhatsApp instances
    51→  const { data: instances, error: instancesError } = await supabase
    52→    .from('whatsapp_instances')
    53→    .select('*')
    54→    .in('project_id', projectIds)
    55→    .order('created_at', { ascending: false });
    56→
    57→  if (instancesError) {
    58→    throw new Error(`Failed to fetch WhatsApp instances: ${instancesError.message}`);
    59→  }
    60→
    61→  return instances || [];
    62→}
    63→
    64→/**
    65→ * Get WhatsApp instances for a specific project
    66→ */
    67→export async function getProjectWhatsappInstances(projectId: string): Promise<WhatsappInstance[]> {
    68→  const supabase = getAdminClient();
    69→
    70→  const { data, error } = await supabase
    71→    .from('whatsapp_instances')
    72→    .select('*')
    73→    .eq('project_id', projectId)
    74→    .order('created_at', { ascending: false });
    75→
    76→  if (error) {
    77→    throw new Error(`Failed to fetch WhatsApp instances: ${error.message}`);
    78→  }
    79→
    80→  return data || [];
    81→}
    82→
    83→/**
    84→ * Get a single WhatsApp instance by ID
    85→ */
    86→export async function getWhatsappInstanceById(instanceId: string): Promise<WhatsappInstance> {
    87→  const supabase = getAdminClient();
    88→
    89→  const { data, error } = await supabase
    90→    .from('whatsapp_instances')
    91→    .select('*')
    92→    .eq('id', instanceId)
    93→    .single();
    94→
    95→  if (error || !data) {
    96→    throw new NotFoundError('WhatsApp instance', instanceId);
    97→  }
    98→
    99→  return data;
   100→}
   101→
   102→/**
   103→ * Get a WhatsApp instance by name within a project
   104→ */
   105→export async function getWhatsappInstanceByName(
   106→  projectId: string,
   107→  instanceName: string
   108→): Promise<WhatsappInstance | null> {
   109→  const supabase = getAdminClient();
   110→
   111→  const { data, error } = await supabase
   112→    .from('whatsapp_instances')
   113→    .select('*')
   114→    .eq('project_id', projectId)
   115→    .eq('instance_name', instanceName)
   116→    .maybeSingle();
   117→
   118→  if (error) {
   119→    throw new Error(`Failed to fetch WhatsApp instance: ${error.message}`);
   120→  }
   121→
   122→  return data;
   123→}
   124→
   125→/**
   126→ * Create a new WhatsApp instance
   127→ */
   128→export async function createWhatsappInstance(
   129→  input: CreateWhatsappInstanceInput & { instance_id: string }
   130→): Promise<WhatsappInstance> {
   131→  const supabase = getAdminClient();
   132→
   133→  // Check if instance name already exists in this project
   134→  const existing = await getWhatsappInstanceByName(input.project_id, input.instance_name);
   135→  if (existing) {
   136→    throw new Error(`An instance with name "${input.instance_name}" already exists in this project`);
   137→  }
   138→
   139→  // Create instance
   140→  const { data, error } = await supabase
   141→    .from('whatsapp_instances')
   142→    .insert({
   143→      project_id: input.project_id,
   144→      instance_name: input.instance_name,
   145→      instance_id: input.instance_id,
   146→      status: 'pending',
   147→      webhook_url: input.webhook_url,
   148→      settings: {},
   149→    })
   150→    .select()
   151→    .single();
   152→
   153→  if (error || !data) {
   154→    throw new Error(`Failed to create WhatsApp instance: ${error?.message || 'Unknown error'}`);
   155→  }
   156→
   157→  return data;
   158→}
   159→
   160→/**
   161→ * Update WhatsApp instance status
   162→ */
   163→export async function updateWhatsappInstanceStatus(
   164→  instanceId: string,
   165→  status: WhatsappInstance['status'],
   166→  updates?: Partial<Pick<WhatsappInstance, 'phone_number' | 'qr_code_base64' | 'connected_at' | 'last_activity_at'>>
   167→): Promise<WhatsappInstance> {
   168→  const supabase = getAdminClient();
   169→
   170→  const { data, error } = await supabase
   171→    .from('whatsapp_instances')
   172→    .update({
   173→      status,
   174→      ...updates,
   175→      last_activity_at: new Date().toISOString(),
   176→    })
   177→    .eq('id', instanceId)
   178→    .select()
   179→    .single();
   180→
   181→  if (error || !data) {
   182→    throw new Error(`Failed to update WhatsApp instance: ${error?.message || 'Unknown error'}`);
   183→  }
   184→
   185→  return data;
   186→}
   187→
   188→/**
   189→ * Update WhatsApp instance QR code
   190→ */
   191→export async function updateWhatsappInstanceQrCode(
   192→  instanceId: string,
   193→  qrCodeBase64: string
   194→): Promise<WhatsappInstance> {
   195→  const supabase = getAdminClient();
   196→
   197→  const { data, error } = await supabase
   198→    .from('whatsapp_instances')
   199→    .update({
   200→      qr_code_base64: qrCodeBase64,
   201→      status: 'connecting',
   202→    })
   203→    .eq('id', instanceId)
   204→    .select()
   205→    .single();
   206→
   207→  if (error || !data) {
   208→    throw new Error(`Failed to update WhatsApp instance: ${error?.message || 'Unknown error'}`);
   209→  }
   210→
   211→  return data;
   212→}
   213→
   214→/**
   215→ * Delete a WhatsApp instance
