// NextMavens Platform - WhatsApp Instances Data Access Layer
// Centralized data access patterns for whatsapp_instances table

import { getAdminClient } from '../supabase-client';
import type { WhatsappInstance, CreateWhatsappInstanceInput } from '../types';
import { NotFoundError } from '../middleware';

// ====================================================================
// WhatsApp Instances CRUD
// ====================================================================

/**
 * Get all WhatsApp instances for a user's projects
 */
export async function getUserWhatsappInstances(userId: string): Promise<WhatsappInstance[]> {
  const supabase = getAdminClient();

  // Get user's organizations
  const { data: memberships, error: memberError } = await supabase
    .from('organization_members')
    .select('organization_id')
    .eq('user_id', userId);

  if (memberError) {
    throw new Error(`Failed to fetch memberships: ${memberError.message}`);
  }

  if (!memberships || memberships.length === 0) {
    return [];
  }

  const orgIds = memberships.map((m) => m.organization_id);

  // Get projects from these organizations
  const { data: projects, error: projectsError } = await supabase
    .from('projects')
    .select('id')
    .in('organization_id', orgIds);

  if (projectsError) {
    throw new Error(`Failed to fetch projects: ${projectsError.message}`);
  }

  if (!projects || projects.length === 0) {
    return [];
  }

  const projectIds = projects.map((p) => p.id);

  // Get WhatsApp instances
  const { data: instances, error: instancesError } = await supabase
    .from('whatsapp_instances')
    .select('*')
    .in('project_id', projectIds)
    .order('created_at', { ascending: false });

  if (instancesError) {
    throw new Error(`Failed to fetch WhatsApp instances: ${instancesError.message}`);
  }

  return instances || [];
}

/**
 * Get WhatsApp instances for a specific project
 */
export async function getProjectWhatsappInstances(projectId: string): Promise<WhatsappInstance[]> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_instances')
    .select('*')
    .eq('project_id', projectId)
    .order('created_at', { ascending: false });

  if (error) {
    throw new Error(`Failed to fetch WhatsApp instances: ${error.message}`);
  }

  return data || [];
}

/**
 * Get a single WhatsApp instance by ID
 */
export async function getWhatsappInstanceById(instanceId: string): Promise<WhatsappInstance> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_instances')
    .select('*')
    .eq('id', instanceId)
    .single();

  if (error || !data) {
    throw new NotFoundError('WhatsApp instance', instanceId);
  }

  return data;
}

/**
 * Get a WhatsApp instance by name within a project
 */
export async function getWhatsappInstanceByName(
  projectId: string,
  instanceName: string
): Promise<WhatsappInstance | null> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_instances')
    .select('*')
    .eq('project_id', projectId)
    .eq('instance_name', instanceName)
    .maybeSingle();

  if (error) {
    throw new Error(`Failed to fetch WhatsApp instance: ${error.message}`);
  }

  return data;
}

/**
 * Create a new WhatsApp instance
 */
export async function createWhatsappInstance(
  input: CreateWhatsappInstanceInput & { instance_id: string }
): Promise<WhatsappInstance> {
  const supabase = getAdminClient();

  // Check if instance name already exists in this project
  const existing = await getWhatsappInstanceByName(input.project_id, input.instance_name);
  if (existing) {
    throw new Error(`An instance with name "${input.instance_name}" already exists in this project`);
  }

  // Create instance
  const { data, error } = await supabase
    .from('whatsapp_instances')
    .insert({
      project_id: input.project_id,
      instance_name: input.instance_name,
      instance_id: input.instance_id,
      status: 'pending',
      webhook_url: input.webhook_url,
      settings: {},
    })
    .select()
    .single();

  if (error || !data) {
    throw new Error(`Failed to create WhatsApp instance: ${error?.message || 'Unknown error'}`);
  }

  return data;
}

/**
 * Update WhatsApp instance status
 */
export async function updateWhatsappInstanceStatus(
  instanceId: string,
  status: WhatsappInstance['status'],
  updates?: Partial<Pick<WhatsappInstance, 'phone_number' | 'qr_code_base64' | 'connected_at' | 'last_activity_at'>>
): Promise<WhatsappInstance> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_instances')
    .update({
      status,
      ...updates,
      last_activity_at: new Date().toISOString(),
    })
    .eq('id', instanceId)
    .select()
    .single();

  if (error || !data) {
    throw new Error(`Failed to update WhatsApp instance: ${error?.message || 'Unknown error'}`);
  }

  return data;
}

/**
 * Update WhatsApp instance QR code
 */
export async function updateWhatsappInstanceQrCode(
  instanceId: string,
  qrCodeBase64: string
): Promise<WhatsappInstance> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_instances')
    .update({
      qr_code_base64: qrCodeBase64,
      status: 'connecting',
    })
    .eq('id', instanceId)
    .select()
    .single();

  if (error || !data) {
    throw new Error(`Failed to update WhatsApp instance: ${error?.message || 'Unknown error'}`);
  }

  return data;
}

/**
 * Delete a WhatsApp instance
 */
export async function deleteWhatsappInstance(instanceId: string): Promise<void> {
  const supabase = getAdminClient();

  const { error } = await supabase
    .from('whatsapp_instances')
    .delete()
    .eq('id', instanceId);

  if (error) {
    throw new Error(`Failed to delete WhatsApp instance: ${error.message}`);
  }
}

/**
 * Check if user has access to a WhatsApp instance (via project access)
 */
export async function hasUserAccessToWhatsappInstance(
  instanceId: string,
  userId: string
): Promise<boolean> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_instances')
    .select('project_id, projects(organization_id)')
    .eq('id', instanceId)
    .single();

  if (error || !data) {
    return false;
  }

  const project = data.projects as any;
  if (!project) {
    return false;
  }

  // Check if user is member of the organization
  const { data: membership } = await supabase
    .from('organization_members')
    .select('role')
    .eq('organization_id', project.organization_id)
    .eq('user_id', userId)
    .maybeSingle();

  return !!membership;
}

