// NextMavens Platform - WhatsApp Messages Data Access Layer
// Centralized data access patterns for whatsapp_messages table

import { getAdminClient } from '../supabase-client';
import type { WhatsappMessage } from '../types';

// ====================================================================
// WhatsApp Messages CRUD
// ====================================================================

/**
 * Create a new WhatsApp message record
 */
export async function createWhatsappMessage(
  input: Omit<WhatsappMessage, 'id' | 'created_at'>
): Promise<WhatsappMessage> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_messages')
    .insert({
      instance_id: input.instance_id,
      message_id: input.message_id,
      from_number: input.from_number,
      to_number: input.to_number,
      message_type: input.message_type,
      content: input.content,
      media_url: input.media_url,
      direction: input.direction,
      status: input.status,
      metadata: input.metadata,
    })
    .select()
    .single();

  if (error || !data) {
    throw new Error(`Failed to create WhatsApp message: ${error?.message || 'Unknown error'}`);
  }

  return data;
}

/**
 * Get messages for a specific instance
 */
export async function getInstanceMessages(
  instanceId: string,
  options?: {
    limit?: number;
    offset?: number;
    direction?: 'inbound' | 'outbound';
    from_date?: string;
    to_date?: string;
  }
): Promise<WhatsappMessage[]> {
  const supabase = getAdminClient();

  let query = supabase
    .from('whatsapp_messages')
    .select('*')
    .eq('instance_id', instanceId)
    .order('created_at', { ascending: false });

  // Apply filters
  if (options?.direction) {
    query = query.eq('direction', options.direction);
  }

  if (options?.from_date) {
    query = query.gte('created_at', options.from_date);
  }

  if (options?.to_date) {
    query = query.lte('created_at', options.to_date);
  }

  // Apply pagination
  if (options?.limit) {
    query = query.limit(options.limit);
  }

  if (options?.offset) {
    query = query.range(options.offset, (options.offset || 0) + (options.limit || 50) - 1);
  }

  const { data, error } = await query;

  if (error) {
    throw new Error(`Failed to fetch WhatsApp messages: ${error.message}`);
  }

  return data || [];
}

/**
 * Get messages for a project (across all instances)
 */
export async function getProjectMessages(
  projectId: string,
  options?: {
    limit?: number;
    offset?: number;
    direction?: 'inbound' | 'outbound';
    from_date?: string;
    to_date?: string;
  }
): Promise<WhatsappMessage[]> {
  const supabase = getAdminClient();

  // First get all instance IDs for the project
  const { data: instances, error: instancesError } = await supabase
    .from('whatsapp_instances')
    .select('id')
    .eq('project_id', projectId);

  if (instancesError) {
    throw new Error(`Failed to fetch WhatsApp instances: ${instancesError.message}`);
  }

  if (!instances || instances.length === 0) {
    return [];
  }

  const instanceIds = instances.map((i) => i.id);

  // Build query for messages
  let query = supabase
    .from('whatsapp_messages')
    .select('*')
    .in('instance_id', instanceIds)
    .order('created_at', { ascending: false });

  // Apply filters
  if (options?.direction) {
    query = query.eq('direction', options.direction);
  }

  if (options?.from_date) {
    query = query.gte('created_at', options.from_date);
  }

  if (options?.to_date) {
    query = query.lte('created_at', options.to_date);
  }

  // Apply pagination
  if (options?.limit) {
    query = query.limit(options.limit);
  }

  if (options?.offset) {
    query = query.range(options.offset, (options.offset || 0) + (options.limit || 50) - 1);
  }

  const { data, error } = await query;

  if (error) {
    throw new Error(`Failed to fetch WhatsApp messages: ${error.message}`);
  }

  return data || [];
}

/**
 * Get a single message by database ID
 */
export async function getMessageById(messageId: string): Promise<WhatsappMessage> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_messages')
    .select('*')
    .eq('id', messageId)
    .single();

  if (error || !data) {
    throw new Error(`Failed to fetch WhatsApp message: ${error?.message || 'Unknown error'}`);
  }

  return data;
}

/**
 * Get a message by the external message ID (from Evolution API)
 */
export async function getMessageByExternalId(
  instanceId: string,
  externalMessageId: string
): Promise<WhatsappMessage | null> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_messages')
    .select('*')
    .eq('instance_id', instanceId)
    .eq('message_id', externalMessageId)
    .maybeSingle();

  if (error) {
    throw new Error(`Failed to fetch WhatsApp message: ${error.message}`);
  }

  return data;
}

/**
 * Update message status
 */
export async function updateMessageStatus(
  messageId: string,
  status: WhatsappMessage['status'],
  metadata?: Record<string, unknown>
): Promise<WhatsappMessage> {
  const supabase = getAdminClient();

  const { data, error } = await supabase
    .from('whatsapp_messages')
    .update({
      status,
      ...(metadata && { metadata }),
    })
    .eq('id', messageId)
    .select()
    .single();

  if (error || !data) {
    throw new Error(`Failed to update WhatsApp message: ${error?.message || 'Unknown error'}`);
  }

  return data;
}

/**
 * Delete a message (soft delete by updating status)
 */
export async function deleteMessage(messageId: string): Promise<void> {
  const supabase = getAdminClient();

  const { error } = await supabase
    .from('whatsapp_messages')
    .delete()
    .eq('id', messageId);

  if (error) {
    throw new Error(`Failed to delete WhatsApp message: ${error.message}`);
  }
}

/**
