'use client'

import { useState, useEffect } from 'react'
import { motion } from 'framer-motion'
import { Loader2, ChevronLeft, ChevronRight, Users, Mail, Calendar, Shield } from 'lucide-react'
import type { User, UserListQuery } from '@/lib/types/auth-user.types'
import { authServiceClient } from '@/lib/api/auth-service-client'
import { UserFilterBar } from './UserFilterBar'

interface UserListProps {
  initialFilters?: UserListQuery
}

export function UserList({ initialFilters = {} }: UserListProps) {
  const [users, setUsers] = useState<User[]>([])
  const [total, setTotal] = useState(0)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [filters, setFilters] = useState<UserListQuery>(initialFilters)
  const [showFilters, setShowFilters] = useState(false)
  const [page, setPage] = useState(0)
  const limit = 50

  const fetchUsers = async () => {
    setLoading(true)
    setError(null)

    try {
      const query: UserListQuery = {
        ...filters,
        limit,
        offset: page * limit,
      }

      const response = await authServiceClient.listUsers(query)
      setUsers(response.users)
      setTotal(response.total)
    } catch (err) {
      console.error('Failed to fetch users:', err)
      setError(err instanceof Error ? err.message : 'Failed to load users')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchUsers()
  }, [page, filters])

  const handleFiltersChange = (newFilters: UserListQuery) => {
    setFilters(newFilters)
  }

  const handleApplyFilters = () => {
    setPage(0)
    fetchUsers()
  }

  const handleClearFilters = () => {
    const clearedFilters: UserListQuery = {}
    setFilters(clearedFilters)
    setPage(0)
    fetchUsers()
  }

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'Never'
    return new Date(dateString).toLocaleDateString()
  }

  const getStatusBadge = (status: string) => {
    if (status === 'active') {
      return 'bg-emerald-100 text-emerald-800'
    }
    if (status === 'disabled') {
      return 'bg-amber-100 text-amber-800'
    }
    return 'bg-red-100 text-red-800'
  }

  const getProviderBadge = (provider: string) => {
    if (provider === 'email') return 'bg-blue-100 text-blue-800'
    if (provider === 'google') return 'bg-red-100 text-red-800'
    if (provider === 'github') return 'bg-slate-100 text-slate-800'
    if (provider === 'microsoft') return 'bg-cyan-100 text-cyan-800'
    return 'bg-slate-100 text-slate-800'
  }

  const totalPages = Math.ceil(total / limit)

  return (
    <div className="space-y-6">
      {/* Filter Bar */}
      <UserFilterBar
        filters={filters}
        onFiltersChange={handleFiltersChange}
        onApply={handleApplyFilters}
        onClear={handleClearFilters}
        showFilters={showFilters}
        onToggle={() => setShowFilters(!showFilters)}
      />

      {/* Users List */}
      <div className="bg-white rounded-xl border border-slate-200 overflow-hidden">
        <div className="px-6 py-4 border-b border-slate-200 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Users className="w-5 h-5 text-slate-600" />
            <h2 className="font-semibold text-slate-900">
              Users {total > 0 && <span className="text-slate-500">({total.toLocaleString()})</span>}
            </h2>
          </div>
        </div>

        {loading ? (
          <div className="flex items-center justify-center py-12">
            <div className="flex items-center gap-3">
              <Loader2 className="w-6 h-6 animate-spin text-emerald-700" />
              <span className="text-slate-600">Loading users...</span>
            </div>
          </div>
        ) : error ? (
          <div className="p-12 text-center">
            <p className="text-red-600 mb-2">Error loading users</p>
            <p className="text-sm text-slate-500">{error}</p>
          </div>
        ) : users.length === 0 ? (
          <div className="p-12 text-center">
            <Users className="w-12 h-12 text-slate-300 mx-auto mb-3" />
            <p className="text-slate-500 mb-1">No users found</p>
            <p className="text-sm text-slate-400">
              Try adjusting your filters or check back later
            </p>
          </div>
        ) : (
          <>
            {/* Table Header */}
            <div className="grid grid-cols-12 gap-4 px-6 py-3 bg-slate-50 border-b border-slate-200 text-xs font-semibold text-slate-700 uppercase tracking-wider">
              <div className="col-span-3">User</div>
              <div className="col-span-2">Status</div>
              <div className="col-span-2">Provider</div>
              <div className="col-span-2">Created</div>
              <div className="col-span-2">Last Sign In</div>
              <div className="col-span-1"></div>
            </div>

            {/* Users List */}
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="divide-y divide-slate-100"
            >
              {users.map((user) => (
                <div
                  key={user.user_id}
                  className="grid grid-cols-12 gap-4 px-6 py-4 items-center hover:bg-slate-50 transition"
                >
                  {/* User Info */}
                  <div className="col-span-3">
                    <div className="flex items-center gap-3">
                      <div className="flex-shrink-0 w-10 h-10 bg-emerald-100 rounded-full flex items-center justify-center">
                        <Mail className="w-5 h-5 text-emerald-700" />
                      </div>
                      <div className="min-w-0">
                        <div className="font-medium text-slate-900 truncate">
                          {user.name || 'No name'}
                        </div>
                        <div className="text-sm text-slate-500 truncate" title={user.email}>
                          {user.email}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Status */}
                  <div className="col-span-2">
                    <span
                      className={`px-2 py-1 text-xs font-medium rounded ${getStatusBadge(user.status)}`}
                    >
                      {user.status}
                    </span>
                  </div>

                  {/* Provider */}
                  <div className="col-span-2">
                    <span
                      className={`px-2 py-1 text-xs font-medium rounded ${getProviderBadge(user.auth_provider)}`}
                    >
                      {user.auth_provider}
                    </span>
                  </div>

                  {/* Created At */}
                  <div className="col-span-2">
                    <div className="flex items-center gap-2 text-sm text-slate-600">
                      <Calendar className="w-4 h-4 text-slate-400 flex-shrink-0" />
                      <span>{formatDate(user.created_at)}</span>
                    </div>
                  </div>

                  {/* Last Sign In */}
                  <div className="col-span-2">
                    <div className="flex items-center gap-2 text-sm text-slate-600">
                      <Shield className="w-4 h-4 text-slate-400 flex-shrink-0" />
                      <span>{formatDate(user.last_sign_in_at)}</span>
                    </div>
                  </div>

                  {/* Actions */}
                  <div className="col-span-1 flex justify-end">
                    <button
                      onClick={() => {
                        // TODO: Navigate to user detail page
                        console.log('View user:', user.user_id)
                      }}
                      className="text-sm text-emerald-700 hover:text-emerald-800 font-medium"
                    >
                      View
                    </button>
                  </div>
                </div>
              ))}
            </motion.div>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="px-6 py-4 border-t border-slate-200 flex items-center justify-between">
                <div className="text-sm text-slate-600">
                  Showing {page * limit + 1} to {Math.min((page + 1) * limit, total)} of {total} users
                </div>
                <div className="flex items-center gap-2">
                  <button
                    onClick={() => setPage(Math.max(0, page - 1))}
                    disabled={page === 0}
                    className="p-2 border border-slate-300 rounded-lg hover:bg-slate-50 transition disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <ChevronLeft className="w-4 h-4 text-slate-600" />
                  </button>
                  <span className="text-sm text-slate-600">
                    Page {page + 1} of {totalPages}
                  </span>
                  <button
                    onClick={() => setPage(Math.min(totalPages - 1, page + 1))}
                    disabled={page >= totalPages - 1}
                    className="p-2 border border-slate-300 rounded-lg hover:bg-slate-50 transition disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <ChevronRight className="w-4 h-4 text-slate-600" />
                  </button>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { useRouter } from 'next/navigation'
import Link from 'next/link'
import { motion } from 'framer-motion'
import { LogOut, Users, Loader2 } from 'lucide-react'
import { UserList } from '@/features/auth-users/components/UserList'

interface Developer {
  id: string
  email: string
  name: string
  organization?: string
}

export default function UsersPage() {
  const router = useRouter()
  const [developer, setDeveloper] = useState<Developer | null>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const token = localStorage.getItem('accessToken')
    if (!token) {
      router.push('/login')
      return
    }

    fetchDeveloperData()
  }, [router])

  const fetchDeveloperData = async () => {
    try {
      const token = localStorage.getItem('accessToken')
      const res = await fetch('/api/developer/me', {
        headers: { Authorization: `Bearer ${token}` },
      })

      if (!res.ok) {
        if (res.status === 401) {
          localStorage.clear()
          router.push('/login')
        }
        return
      }

      const data = await res.json()
      setDeveloper(data.developer)
    } catch (err) {
      console.error('Failed to fetch developer:', err)
    } finally {
      setLoading(false)
    }
  }

  const handleLogout = () => {
    localStorage.clear()
    router.push('/')
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-[#F3F5F7] flex items-center justify-center">
        <div className="flex items-center gap-3">
          <Loader2 className="w-6 h-6 animate-spin text-emerald-700" />
          <span className="text-slate-600">Loading...</span>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-[#F3F5F7]">
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@400;500;600;700&display=swap');
        :root { --font-sans: 'Plus Jakarta Sans', ui-sans-serif, system-ui, sans-serif; }
        .font-jakarta { font-family: var(--font-sans); }
      `}</style>

      <nav className="bg-white border-b border-slate-200">
        <div className="mx-auto max-w-[1180px] px-4 py-4 flex items-center justify-between">
          <Link href="/" className="flex items-center gap-3">
            <div className="grid h-9 w-9 place-items-center rounded-lg bg-emerald-700 text-white shadow">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M4 12c5 0 4-8 10-8 0 3 6 3 6 8s-6 5-6 8c-6 0-5-8-10-8Z" fill="currentColor" />
              </svg>
            </div>
            <span className="font-jakarta text-xl font-semibold tracking-tight text-slate-900">nextmavens</span>
          </Link>

          <div className="flex items-center gap-4">
            <span className="text-sm text-slate-600">{developer?.name}</span>
            <button
              onClick={handleLogout}
              className="flex items-center gap-2 text-sm text-slate-600 hover:text-slate-900"
            >
              <LogOut className="w-4 h-4" />
              Logout
            </button>
          </div>
        </div>
      </nav>

      <main className="mx-auto max-w-[1180px] px-4 py-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="space-y-6"
        >
          <div>
            <div className="flex items-center gap-3 mb-2">
              <Users className="w-6 h-6 text-emerald-700" />
              <h1 className="text-3xl font-semibold text-slate-900">User Management</h1>
            </div>
            <p className="text-slate-600">
              View and manage all users in the system
            </p>
          </div>

          <UserList />
        </motion.div>
      </main>
    </div>
  )
}
