     1→import jwt from 'jsonwebtoken'
     2→import crypto from 'crypto'
     3→import { NextRequest } from 'next/server'
     4→
     5→const JWT_SECRET = process.env.JWT_SECRET || 'nextmavens-portal-secret'
     6→const REFRESH_SECRET = process.env.REFRESH_SECRET || 'nextmavens-refresh-secret'
     7→
     8→export interface Developer {
     9→  id: string
    10→  email: string
    11→  name: string
    12→  organization?: string
    13→}
    14→
    15→export function generateAccessToken(developer: Developer): string {
    16→  return jwt.sign(
    17→    { id: developer.id, email: developer.email },
    18→    JWT_SECRET,
    19→    { expiresIn: '1h' }
    20→  )
    21→}
    22→
    23→export function generateRefreshToken(developerId: string): string {
    24→  return jwt.sign(
    25→    { id: developerId },
    26→    REFRESH_SECRET,
    27→    { expiresIn: '7d' }
    28→  )
    29→}
    30→
    31→export function verifyAccessToken(token: string): Developer {
    32→  try {
    33→    return jwt.verify(token, JWT_SECRET) as Developer
    34→  } catch {
    35→    throw new Error('Invalid token')
    36→  }
    37→}
    38→
    39→export function generateSlug(name: string): string {
    40→  return name
    41→    .toLowerCase()
    42→    .replace(/[^a-z0-9]+/g, '-')
    43→    .replace(/^-|-$/g, '')
    44→    .substring(0, 50)
    45→}
    46→
    47→export function generateApiKey(type: 'public' | 'secret' = 'public'): string {
    48→  const prefix = type === 'public' ? 'nm_live_pk_' : 'nm_live_sk_'
    49→  const key = Buffer.from(crypto.randomBytes(32)).toString('hex')
    50→  return `${prefix}${key}`
    51→}
    52→
    53→export function hashApiKey(key: string): string {
    54→  return crypto.createHash('sha256').update(key).digest('hex')
    55→}
    56→
    57→export async function authenticateRequest(req: NextRequest): Promise<Developer> {
    58→  const authHeader = req.headers.get('authorization')
    59→  if (!authHeader || !authHeader.startsWith('Bearer ')) {
    60→    throw new Error('No token provided')
    61→  }
    62→  const token = authHeader.substring(7)
    63→  return verifyAccessToken(token)
    64→}
