/**
 * Auth User Management Types
 * Types for the auth service user management integration
 */

/**
 * User account status
 */
export type UserStatus = 'active' | 'disabled' | 'deleted'

/**
 * Authentication provider type
 */
export type AuthProvider = 'email' | 'google' | 'github' | 'microsoft'

/**
 * Base user interface from auth service
 */
export interface User {
  user_id: string
  email: string
  name?: string
  created_at: string
  updated_at: string
  last_sign_in_at?: string
  sign_in_count: number
  auth_provider: AuthProvider
  user_metadata: Record<string, unknown>
  status: UserStatus
}

/**
 * Request to disable a user
 */
export interface DisableUserRequest {
  userId: string
  reason?: string
}

/**
 * Request to enable (re-enable) a user
 */
export interface EnableUserRequest {
  userId: string
}

/**
 * Response from disable/enable user operations
 */
export interface UserStatusResponse {
  user_id: string
  status: UserStatus
  updated_at: string
}

/**
 * User list query parameters
 */
export interface UserListQuery {
  limit?: number
  offset?: number
  search?: string
  status?: UserStatus
  auth_provider?: AuthProvider
  created_after?: string
  created_before?: string
  last_sign_in_after?: string
  last_sign_in_before?: string
  sort_by?: 'created_at' | 'last_sign_in_at' | 'email' | 'name'
  sort_order?: 'asc' | 'desc'
}

/**
 * Paginated user list response
 */
export interface UserListResponse {
  users: User[]
  total: number
  limit: number
  offset: number
}

/**
 * User detail response
 */
export interface UserDetailResponse extends User {}

/**
 * Request to update user metadata
 */
export interface UpdateUserMetadataRequest {
  userId: string
  metadata: Record<string, unknown>
}

/**
 * Request to delete a user
 */
export interface DeleteUserRequest {
  userId: string
  reason?: string
}

/**
 * Delete user response
 */
export interface DeleteUserResponse {
  user_id: string
  deleted: boolean
  deleted_at: string
}

/**
 * Request to reset user password
 */
export interface ResetPasswordRequest {
  userId: string
  email?: string
}

/**
 * Reset password response
 */
export interface ResetPasswordResponse {
  user_id: string
  email_sent: boolean
  message: string
}

/**
 * Auth service API error response
 */
export interface AuthServiceError {
  error: string
  message: string
  code?: string
  details?: Record<string, unknown>
}

/**
 * Auth Service API Client
 * Client for interacting with the auth service for user management
 */

import type {
  User,
  UserListQuery,
  UserListResponse,
  UserDetailResponse,
  DisableUserRequest,
  EnableUserRequest,
  UserStatusResponse,
  UpdateUserMetadataRequest,
  DeleteUserRequest,
  DeleteUserResponse,
  ResetPasswordRequest,
  ResetPasswordResponse,
  AuthServiceError,
} from '@/lib/types/auth-user.types'

/**
 * Auth service configuration
 */
interface AuthServiceConfig {
  baseUrl: string
  apiKey: string
}

/**
 * Auth service API client
 */
export class AuthServiceClient {
  private config: AuthServiceConfig

  constructor(config: AuthServiceConfig) {
    this.config = config
  }

  /**
   * Make an authenticated request to the auth service
   */
  private async request<T>(
    endpoint: string,
    options: RequestInit = {}
  ): Promise<T> {
    const url = `${this.config.baseUrl}${endpoint}`

    const headers: HeadersInit = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${this.config.apiKey}`,
      ...options.headers,
    }

    try {
      const response = await fetch(url, {
        ...options,
        headers,
      })

      if (!response.ok) {
        const error: AuthServiceError = await response.json().catch(() => ({
          error: 'Unknown error',
          message: `HTTP ${response.status}: ${response.statusText}`,
        }))
        throw new AuthServiceApiClientError(error.message, error)
      }

      return await response.json()
    } catch (error) {
      if (error instanceof AuthServiceApiClientError) {
        throw error
      }
      throw new AuthServiceApiClientError(
        'Failed to connect to auth service',
        {
          error: 'NetworkError',
          message: error instanceof Error ? error.message : 'Unknown error',
        }
      )
    }
  }

  /**
   * List all users with optional filtering
   */
  async listUsers(query: UserListQuery = {}): Promise<UserListResponse> {
    const params = new URLSearchParams()

    if (query.limit) params.append('limit', String(query.limit))
    if (query.offset) params.append('offset', String(query.offset))
    if (query.search) params.append('search', query.search)
    if (query.status) params.append('status', query.status)
    if (query.auth_provider) params.append('auth_provider', query.auth_provider)
    if (query.created_after) params.append('created_after', query.created_after)
    if (query.created_before) params.append('created_before', query.created_before)
    if (query.last_sign_in_after) params.append('last_sign_in_after', query.last_sign_in_after)
    if (query.last_sign_in_before) params.append('last_sign_in_before', query.last_sign_in_before)
    if (query.sort_by) params.append('sort_by', query.sort_by)
    if (query.sort_order) params.append('sort_order', query.sort_order)

    const queryString = params.toString()
    return this.request<UserListResponse>(`/users${queryString ? `?${queryString}` : ''}`)
  }

  /**
   * Get a single user by ID
   */
  async getUser(userId: string): Promise<UserDetailResponse> {
    return this.request<UserDetailResponse>(`/users/${userId}`)
  }

  /**
   * Disable a user account
   */
  async disableUser(request: DisableUserRequest): Promise<UserStatusResponse> {
    return this.request<UserStatusResponse>(`/users/${request.userId}/disable`, {
      method: 'POST',
      body: JSON.stringify({ reason: request.reason }),
    })
  }

  /**
   * Enable (re-enable) a user account
   */
  async enableUser(request: EnableUserRequest): Promise<UserStatusResponse> {
    return this.request<UserStatusResponse>(`/users/${request.userId}/enable`, {
      method: 'POST',
    })
  }

  /**
   * Update user metadata
   */
  async updateUserMetadata(request: UpdateUserMetadataRequest): Promise<UserDetailResponse> {
    return this.request<UserDetailResponse>(`/users/${request.userId}/metadata`, {
      method: 'PATCH',
      body: JSON.stringify({ metadata: request.metadata }),
    })
  }

  /**
   * Delete a user account
   */
  async deleteUser(request: DeleteUserRequest): Promise<DeleteUserResponse> {
    return this.request<DeleteUserResponse>(`/users/${request.userId}`, {
      method: 'DELETE',
      body: JSON.stringify({ reason: request.reason }),
    })
  }

  /**
   * Reset user password (sends reset email)
   */
  async resetPassword(request: ResetPasswordRequest): Promise<ResetPasswordResponse> {
    return this.request<ResetPasswordResponse>(`/users/${request.userId}/reset-password`, {
      method: 'POST',
      body: JSON.stringify({ email: request.email }),
    })
  }
}

/**
 * Custom error class for auth service API client errors
 */
export class AuthServiceApiClientError extends Error {
  public readonly code?: string
  public readonly details?: Record<string, unknown>

  constructor(message: string, errorResponse: AuthServiceError) {
    super(message)
    this.name = 'AuthServiceApiClientError'
    this.code = errorResponse.code
    this.details = errorResponse.details
  }
}

/**
 * Create a singleton auth service client instance
 * Reads configuration from environment variables
 */
export function createAuthServiceClient(): AuthServiceClient {
  const baseUrl = process.env.AUTH_SERVICE_URL || 'http://localhost:3001'
  const apiKey = process.env.AUTH_SERVICE_API_KEY || ''

  if (!apiKey) {
    throw new Error('AUTH_SERVICE_API_KEY environment variable is required')
  }

  return new AuthServiceClient({ baseUrl, apiKey })
}

/**
 * Default auth service client instance
 */
export const authServiceClient = createAuthServiceClient()

/**
 * API Route: List Users
 * GET /api/auth/users - List all users with optional filtering
 */

import { NextRequest, NextResponse } from 'next/server'
import { authenticateRequest } from '@/lib/auth'
import { authServiceClient } from '@/lib/api/auth-service-client'
import type { UserListQuery } from '@/lib/types/auth-user.types'

export async function GET(req: NextRequest) {
  try {
    // Authenticate the request
    await authenticateRequest(req)

    // Parse query parameters
    const searchParams = req.nextUrl.searchParams
    const query: UserListQuery = {
      limit: searchParams.get('limit') ? parseInt(searchParams.get('limit')!) : undefined,
      offset: searchParams.get('offset') ? parseInt(searchParams.get('offset')!) : undefined,
      search: searchParams.get('search') || undefined,
      status: searchParams.get('status') as any || undefined,
      auth_provider: searchParams.get('auth_provider') as any || undefined,
      created_after: searchParams.get('created_after') || undefined,
      created_before: searchParams.get('created_before') || undefined,
      last_sign_in_after: searchParams.get('last_sign_in_after') || undefined,
      last_sign_in_before: searchParams.get('last_sign_in_before') || undefined,
      sort_by: searchParams.get('sort_by') as any || undefined,
      sort_order: searchParams.get('sort_order') as any || undefined,
    }

    // Call auth service
    const response = await authServiceClient.listUsers(query)

    return NextResponse.json(response)
  } catch (error) {
    console.error('Error listing users:', error)

    if (error instanceof Error && error.message === 'No token provided') {
      return NextResponse.json(
        { error: 'Unauthorized', message: 'Authentication required' },
        { status: 401 }
      )
    }

    if (error instanceof Error && error.message === 'Invalid token') {
      return NextResponse.json(
        { error: 'Unauthorized', message: 'Invalid authentication token' },
        { status: 401 }
      )
    }

    return NextResponse.json(
      { error: 'Internal Server Error', message: 'Failed to list users' },
      { status: 500 }
    )
  }
}

