     1→#!/usr/bin/env node
     2→/**
     3→ * PRD Utilities - Safe JSON Update with Rollback
     4→ *
     5→ * Provides safe PRD file updates with automatic backup and rollback.
     6→ * - Creates backup before update
     7→ * - Validates JSON before committing
     8→ * - Rolls back on error
     9→ * - Transaction-like behavior for PRD updates
    10→ *
    11→ * Usage:
    12→ *   const prdUtils = require('./prd-utils.js');
    13→ *   await prdUtils.updatePRD('docs/prd-feature.json', (prd) => {
    14→ *     prd.userStories[0].passes = true;
    15→ *     return prd;
    16→ *   });
    17→ *
    18→ * @module prd-utils
    19→ */
    20→
    21→const fs = require('fs');
    22→const path = require('path');
    23→
    24→// Backup directory
    25→const BACKUP_DIR = '.claude/.prd-backups';
    26→
    27→// Maximum number of backups to keep per PRD
    28→const MAX_BACKUPS = 10;
    29→
    30→/**
    31→ * Create a backup of a file
    32→ *
    33→ * @param {string} filePath - Path to file to backup
    34→ * @returns {string} Path to backup file
    35→ */
    36→function createBackup(filePath) {
    37→  const backupDir = path.join(path.dirname(filePath), BACKUP_DIR);
    38→  const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    39→  const filename = path.basename(filePath);
    40→  const backupPath = path.join(backupDir, `${filename}.${timestamp}.bak`);
    41→
    42→  // Create backup directory if it doesn't exist
    43→  if (!fs.existsSync(backupDir)) {
    44→    fs.mkdirSync(backupDir, { recursive: true });
    45→  }
    46→
    47→  // Copy file to backup location
    48→  fs.copyFileSync(filePath, backupPath);
    49→
    50→  // Clean old backups (keep only MAX_BACKUPS most recent)
    51→  cleanOldBackups(filePath);
    52→
    53→  return backupPath;
    54→}
    55→
    56→/**
    57→ * Clean old backups for a file
    58→ *
    59→ * @param {string} originalPath - Original file path
    60→ */
    61→function cleanOldBackups(originalPath) {
    62→  const backupDir = path.join(path.dirname(originalPath), BACKUP_DIR);
    63→  const filename = path.basename(originalPath);
    64→
    65→  if (!fs.existsSync(backupDir)) {
    66→    return;
    67→  }
    68→
    69→  // Get all backup files for this PRD
    70→  const backupFiles = fs.readdirSync(backupDir)
    71→    .filter(f => f.startsWith(filename) && f.endsWith('.bak'))
    72→    .map(f => ({
    73→      name: f,
    74→      path: path.join(backupDir, f),
    75→      time: fs.statSync(path.join(backupDir, f)).mtime.getTime()
    76→    }))
    77→    .sort((a, b) => b.time - a.time); // Sort by time descending (newest first)
    78→
    79→  // Remove backups beyond MAX_BACKUPS
    80→  if (backupFiles.length > MAX_BACKUPS) {
    81→    const toDelete = backupFiles.slice(MAX_BACKUPS);
    82→    toDelete.forEach(file => {
    83→      fs.unlinkSync(file.path);
    84→    });
    85→  }
    86→}
    87→
    88→/**
    89→ * Rollback to a backup file
    90→ *
    91→ * @param {string} originalPath - Original file path
    92→ * @param {string} backupPath - Backup file path
    93→ * @returns {boolean} True if rollback succeeded
    94→ */
    95→function rollback(originalPath, backupPath) {
    96→  try {
    97→    fs.copyFileSync(backupPath, originalPath);
    98→    return true;
    99→  } catch (error) {
   100→    console.error(`Rollback failed: ${error.message}`);
   101→    return false;
   102→  }
   103→}
   104→
   105→/**
   106→ * Validate PRD JSON structure
   107→ *
   108→ * @param {object} prd - PRD object to validate
   109→ * @returns {object} Validation result {valid: boolean, errors: string[]}
   110→ */
   111→function validatePRD(prd) {
   112→  const errors = [];
   113→
   114→  // Required top-level fields
   115→  const requiredFields = ['project', 'branchName', 'description', 'memorialFile', 'userStories'];
   116→  for (const field of requiredFields) {
   117→    if (!prd[field]) {
   118→      errors.push(`Missing required field: ${field}`);
   119→    }
   120→  }
   121→
   122→  // Validate userStories array
   123→  if (!Array.isArray(prd.userStories)) {
   124→    errors.push('userStories must be an array');
   125→  } else {
   126→    prd.userStories.forEach((story, index) => {
   127→      const storyPrefix = `userStories[${index}]`;
   128→
   129→      // Required story fields
   130→      const requiredStoryFields = ['id', 'title', 'description', 'acceptanceCriteria', 'mavenSteps', 'mcpTools', 'priority', 'passes', 'notes'];
   131→      for (const field of requiredStoryFields) {
   132→        if (story[field] === undefined) {
   133→          errors.push(`${storyPrefix}: missing required field: ${field}`);
   134→        }
   135→      }
   136→
   137→      // Validate mavenSteps is array
   138→      if (story.mavenSteps && !Array.isArray(story.mavenSteps)) {
   139→        errors.push(`${storyPrefix}: mavenSteps must be an array`);
   140→      }
   141→
   142→      // Validate mcpTools is object
   143→      if (story.mcpTools && typeof story.mcpTools !== 'object') {
   144→        errors.push(`${storyPrefix}: mcpTools must be an object`);
   145→      }
   146→
   147→      // Validate acceptanceCriteria is array
   148→      if (story.acceptanceCriteria && !Array.isArray(story.acceptanceCriteria)) {
   149→        errors.push(`${storyPrefix}: acceptanceCriteria must be an array`);
   150→      }
   151→
   152→      // Validate priority is number >= 1
   153→      if (typeof story.priority !== 'number' || story.priority < 1) {
   154→        errors.push(`${storyPrefix}: priority must be a number >= 1`);
   155→      }
   156→
   157→      // Validate passes is boolean
   158→      if (typeof story.passes !== 'boolean') {
   159→        errors.push(`${storyPrefix}: passes must be a boolean`);
   160→      }
   161→    });
   162→  }
   163→
   164→  return {
   165→    valid: errors.length === 0,
   166→    errors
   167→  };
   168→}
   169→
   170→/**
   171→ * Update a PRD file with automatic backup and rollback
   172→ *
   173→ * @param {string} filePath - Path to PRD JSON file
   174→ * @param {function} updateFn - Function that takes the PRD object and returns the updated version
   175→ * @param {object} options - Options {createBackup: true, validate: true}
   176→ * @returns {object} Result {success: boolean, backupPath: string, error: string}
   177→ */
   178→function updatePRD(filePath, updateFn, options = {}) {
   179→  const opts = {
   180→    createBackup: true,
   181→    validate: true,
   182→    ...options
   183→  };
   184→
   185→  let backupPath = null;
   186→
   187→  try {
   188→    // Check if file exists
   189→    if (!fs.existsSync(filePath)) {
   190→      return {
   191→        success: false,
   192→        error: `File not found: ${filePath}`
   193→      };
   194→    }
   195→
   196→    // Create backup
   197→    if (opts.createBackup) {
   198→      backupPath = createBackup(filePath);
   199→    }
   200→
   201→    // Read current PRD
   202→    const content = fs.readFileSync(filePath, 'utf-8');
   203→    let prd;
   204→
   205→    try {
   206→      prd = JSON.parse(content);
   207→    } catch (parseError) {
   208→      return {
   209→        success: false,
   210→        error: `Invalid JSON in ${filePath}: ${parseError.message}`,
   211→        backupPath
   212→      };
   213→    }
   214→
   215→    // Apply update function
   216→    let updatedPRD;
   217→    try {
   218→      updatedPRD = updateFn(prd);
   219→    } catch (updateError) {
   220→      // Rollback on update function error
   221→      if (backupPath) {
   222→        rollback(filePath, backupPath);
   223→      }
   224→      return {
   225→        success: false,
   226→        error: `Update function failed: ${updateError.message}`,
   227→        backupPath
   228→      };
   229→    }
   230→
   231→    // Validate updated PRD
   232→    if (opts.validate) {
   233→      const validation = validatePRD(updatedPRD);
   234→      if (!validation.valid) {
   235→        // Rollback on validation failure
   236→        if (backupPath) {
   237→          rollback(filePath, backupPath);
   238→        }
   239→        return {
   240→          success: false,
   241→          error: `Validation failed:\n${validation.errors.join('\n')}`,
   242→          backupPath
   243→        };
   244→      }
   245→    }
   246→
   247→    // Write updated PRD
   248→    const updatedContent = JSON.stringify(updatedPRD, null, 2);
   249→    fs.writeFileSync(filePath, updatedContent, 'utf-8');
   250→
   251→    return {
   252→      success: true,
   253→      backupPath
   254→    };
   255→
   256→  } catch (error) {
   257→    // Rollback on unexpected error
   258→    if (backupPath) {
   259→      rollback(filePath, backupPath);
   260→    }
   261→    return {
   262→      success: false,
   263→      error: error.message,
   264→      backupPath
   265→    };
   266→  }
   267→}
   268→
   269→/**
   270→ * Mark a story as passed in a PRD
   271→ *
   272→ * @param {string} filePath - Path to PRD JSON file
   273→ * @param {string} storyId - Story ID to mark as passed
   274→ * @returns {object} Result {success: boolean, error: string}
   275→ */
   276→function markStoryPassed(filePath, storyId) {
   277→  return updatePRD(filePath, (prd) => {
   278→    const story = prd.userStories.find(s => s.id === storyId);
   279→    if (!story) {
   280→      throw new Error(`Story ${storyId} not found`);
   281→    }
   282→    story.passes = true;
   283→    return prd;
   284→  });
   285→}
   286→
   287→/**
   288→ * Update story notes in a PRD
   289→ *
   290→ * @param {string} filePath - Path to PRD JSON file
   291→ * @param {string} storyId - Story ID to update
   292→ * @param {string} notes - New notes content
   293→ * @returns {object} Result {success: boolean, error: string}
   294→ */
   295→function updateStoryNotes(filePath, storyId, notes) {
   296→  return updatePRD(filePath, (prd) => {
   297→    const story = prd.userStories.find(s => s.id === storyId);
   298→    if (!story) {
   299→      throw new Error(`Story ${storyId} not found`);
   300→    }
   301→    story.notes = notes;
   302→    return prd;
   303→  });
   304→}
   305→
   306→/**
   307→ * Get all backup files for a PRD
   308→ *
   309→ * @param {string} filePath - Original PRD file path
   310→ * @returns {array} Array of backup file info {path, timestamp}
   311→ */
   312→function listBackups(filePath) {
   313→  const backupDir = path.join(path.dirname(filePath), BACKUP_DIR);
   314→  const filename = path.basename(filePath);
   315→
   316→  if (!fs.existsSync(backupDir)) {
   317→    return [];
   318→  }
   319→
   320→  const backupFiles = fs.readdirSync(backupDir)
   321→    .filter(f => f.startsWith(filename) && f.endsWith('.bak'))
   322→    .map(f => {
   323→      const backupPath = path.join(backupDir, f);
   324→      const stats = fs.statSync(backupPath);
   325→      // Extract timestamp from filename
   326→      const timestampMatch = f.match(/\.(\d{4}-\d{2}-\d{2}T[\d-]+)\.bak$/);
   327→      return {
   328→        path: backupPath,
   329→        filename: f,
   330→        timestamp: timestampMatch ? timestampMatch[1] : stats.mtime.toISOString(),
   331→        mtime: stats.mtime
   332→      };
   333→    })
   334→    .sort((a, b) => b.mtime - a.mtime); // Sort by time descending
   335→
   336→  return backupFiles;
   337→}
   338→
   339→/**
   340→ * Restore a PRD from a backup
   341→ *
   342→ * @param {string} originalPath - Original PRD file path
   343→ * @param {string} backupFilename - Backup filename to restore
   344→ * @returns {object} Result {success: boolean, error: string}
   345→ */
   346→function restoreBackup(originalPath, backupFilename) {
   347→  const backupDir = path.join(path.dirname(originalPath), BACKUP_DIR);
   348→  const backupPath = path.join(backupDir, backupFilename);
   349→
   350→  if (!fs.existsSync(backupPath)) {
   351→    return {
   352→      success: false,
   353→      error: `Backup not found: ${backupFilename}`
   354→    };
   355→  }
   356→
   357→  try {
   358→    // Create backup of current file before restoring
   359→    const currentBackup = createBackup(originalPath);
   360→
   361→    // Copy backup to original location
   362→    fs.copyFileSync(backupPath, originalPath);
   363→
   364→    return {
   365→      success: true,
   366→      previousBackup: currentBackup
   367→    };
   368→  } catch (error) {
   369→    return {
   370→      success: false,
   371→      error: error.message
   372→    };
   373→  }
   374→}
   375→
   376→// Export all functions
   377→module.exports = {
   378→  updatePRD,
   379→  markStoryPassed,
   380→  updateStoryNotes,
   381→  validatePRD,
   382→  createBackup,
   383→  rollback,
   384→  listBackups,
   385→  restoreBackup,
   386→  BACKUP_DIR,
   387→  MAX_BACKUPS
   388→};
   389→
   390→// Allow running as a script for testing
   391→if (require.main === module) {
   392→  const args = process.argv.slice(2);
   393→
   394→  if (args.length === 0) {
   395→    console.log('PRD Utilities - Safe JSON Update with Rollback');
   396→    console.log('');
   397→    console.log('Usage:');
   398→    console.log('  node prd-utils.js <prd-file> <command> [args]');
   399→    console.log('');
   400→    console.log('Commands:');
   401→    console.log('  validate              - Validate PRD structure');
   402→    console.log('  mark-passed <story-id> - Mark story as passed');
   403→    console.log('  list-backups          - List all backups');
   404→    console.log('  restore <backup-file>  - Restore from backup');
   405→    console.log('');
   406→    process.exit(0);
   407→  }
   408→
   409→  const [prdFile, command, ...cmdArgs] = args;
   410→
   411→  switch (command) {
   412→    case 'validate': {
   413→      const content = fs.readFileSync(prdFile, 'utf-8');
   414→      const prd = JSON.parse(content);
   415→      const result = validatePRD(prd);
   416→      if (result.valid) {
   417→        console.log('✅ PRD is valid');
   418→        process.exit(0);
   419→      } else {
   420→        console.log('❌ PRD validation failed:');
   421→        result.errors.forEach(e => console.log(`  - ${e}`));
   422→        process.exit(1);
   423→      }
   424→    }
   425→
   426→    case 'mark-passed': {
   427→      const storyId = cmdArgs[0];
   428→      const result = markStoryPassed(prdFile, storyId);
   429→      if (result.success) {
   430→        console.log(`✅ Marked ${storyId} as passed`);
   431→        console.log(`   Backup: ${result.backupPath}`);
   432→      } else {
   433→        console.log(`❌ Failed: ${result.error}`);
   434→        process.exit(1);
   435→      }
   436→      break;
   437→    }
   438→
   439→    case 'list-backups': {
   440→      const backups = listBackups(prdFile);
   441→      if (backups.length === 0) {
   442→        console.log('No backups found');
   443→      } else {
   444→        console.log(`Backups for ${prdFile}:`);
   445→        backups.forEach(b => {
   446→          console.log(`  ${b.filename} (${b.timestamp})`);
   447→        });
   448→      }
   449→      break;
   450→    }
   451→
   452→    case 'restore': {
   453→      const backupFile = cmdArgs[0];
   454→      const result = restoreBackup(prdFile, backupFile);
   455→      if (result.success) {
   456→        console.log(`✅ Restored from ${backupFile}`);
   457→      } else {
   458→        console.log(`❌ Failed: ${result.error}`);
   459→        process.exit(1);
   460→      }
   461→      break;
   462→    }
   463→
   464→    default:
   465→      console.log(`Unknown command: ${command}`);
   466→      process.exit(1);
   467→  }
   468→}
