     1→#!/usr/bin/env node
     2→/**
     3→ * Pre-Task Hook: Validates PRD files and MCP availability before spawning Maven Flow agents
     4→ *
     5→ * This hook runs before Task tool calls to Maven Flow specialist agents.
     6→ * It validates:
     7→ * 1. PRD files exist in docs/ directory
     8→ * 2. Required MCPs (from prompt) are available in the environment
     9→ *
    10→ * Agents validated:
    11→ * - development-agent
    12→ * - refactor-agent
    13→ * - quality-agent
    14→ * - security-agent
    15→ * - design-agent
    16→ *
    17→ * Usage: Called by Claude Code via PreToolUse hook in flow.md
    18→ *
    19→ * Exit codes:
    20→ * - 0: Allow the tool (validation passed)
    21→ * - 3: Block the tool (validation failed)
    22→ */
    23→
    24→const fs = require('fs');
    25→const path = require('path');
    26→const os = require('os');
    27→
    28→// Maven Flow specialist agent types that require validation
    29→const MAVEN_FLOW_AGENTS = [
    30→  'development-agent',
    31→  'refactor-agent',
    32→  'quality-agent',
    33→  'security-agent',
    34→  'design-agent'
    35→];
    36→
    37→// Common MCP server names (lowercase for matching)
    38→const COMMON_MCPS = [
    39→  'supabase',
    40→  'chrome-devtools',
    41→  'web-search-prime',
    42→  'web-reader',
    43→  'playwright',
    44→  'wrangler',
    45→  'vercel',
    46→  'figma',
    47→  '4-5v-mcp',
    48→  'memory-keeper',
    49→  'filesystem',
    50→  'sequential-thinking'
    51→];
    52→
    53→/**
    54→ * Extract MCP names from agent prompt
    55→ * Looks for patterns like "Use these MCPs: supabase, chrome-devtools"
    56→ */
    57→function extractMCPsFromPrompt(prompt) {
    58→  if (!prompt) return new Set();
    59→
    60→  const mcps = new Set();
    61→  const lowerPrompt = prompt.toLowerCase();
    62→
    63→  // Pattern 1: "Use these MCPs: supabase, chrome-devtools"
    64→  const useMCPsMatch = lowerPrompt.match(/use these mcps?:\s*([^\n]+)/i);
    65→  if (useMCPsMatch) {
    66→    const names = useMCPsMatch[1]
    67→      .split(/[,;\s]+/)
    68→      .map(n => n.trim().toLowerCase())
    69→      .filter(n => COMMON_MCPS.includes(n));
    70→    names.forEach(n => mcps.add(n));
    71→  }
    72→
    73→  // Pattern 2: Direct mentions of "supabase MCP", "chrome-devtools MCP", etc.
    74→  for (const mcp of COMMON_MCPS) {
    75→    const regex = new RegExp(`\\b${mcp.replace(/-/g, '-')}\\s+mcp\\b`, 'i');
    76→    if (regex.test(lowerPrompt)) {
    77→      mcps.add(mcp);
    78→    }
    79→  }
    80→
    81→  // Pattern 3: "required MCPs: ..." or "MCPs required: ..."
    82→  const requiredMCPsMatch = lowerPrompt.match(/(?:required|mcp.*?required):\s*([^\n]+)/i);
    83→  if (requiredMCPsMatch) {
    84→    const names = requiredMCPsMatch[1]
    85→      .split(/[,;\s]+/)
    86→      .map(n => n.trim().toLowerCase())
    87→      .filter(n => COMMON_MCPS.includes(n));
    88→    names.forEach(n => mcps.add(n));
    89→  }
    90→
    91→  return mcps;
    92→}
    93→
    94→/**
    95→ * Get available MCPs from Claude Code settings
    96→ * Checks multiple possible settings file locations
    97→ */
    98→function getAvailableMCPs(workingDir) {
    99→  const availableMCPs = new Set();
   100→
   101→  // Possible settings file locations
   102→  const possibleSettingsPaths = [
   103→    path.join(workingDir, '.claude', 'settings.json'),
   104→    path.join(os.homedir(), '.claude', 'settings.json'),
   105→    path.join(os.homedir(), '.config', 'claude-code', 'settings.json'),
   106→    path.join(os.homedir(), '.config', 'claude', 'settings.json'),
   107→    path.join(process.env.XDG_CONFIG_HOME || '', 'claude-code', 'settings.json')
   108→  ].filter(p => fs.existsSync(p));
   109→
   110→  for (const settingsPath of possibleSettingsPaths) {
   111→    try {
   112→      const settings = JSON.parse(fs.readFileSync(settingsPath, 'utf-8'));
   113→
   114→      // Check for mcpServers key
   115→      if (settings.mcpServers) {
   116→        Object.keys(settings.mcpServers).forEach(mcpName => {
   117→          availableMCPs.add(mcpName.toLowerCase());
   118→        });
   119→      }
   120→
   121→      // Check for mcp key (alternative format)
   122→      if (settings.mcp) {
   123→        Object.keys(settings.mcp).forEach(mcpName => {
   124→          availableMCPs.add(mcpName.toLowerCase());
   125→        });
   126→      }
   127→
   128→      // Check for enabledMCPs key
   129→      if (settings.enabledMCPs) {
   130→        settings.enabledMCPs.forEach(mcpName => {
   131→          availableMCPs.add(mcpName.toLowerCase());
   132→        });
   133→      }
   134→    } catch (e) {
   135→      // Continue to next path on error
   136→    }
   137→  }
   138→
   139→  // Check CLAUDE_AVAILABLE_TOOLS env var (set by Claude Code)
   140→  if (process.env.CLAUDE_AVAILABLE_TOOLS) {
   141→    try {
   142→      const tools = JSON.parse(process.env.CLAUDE_AVAILABLE_TOOLS);
   143→      // Tool names might be like "mcp__supabase__query"
   144→      tools.forEach(tool => {
   145→        const match = tool.match(/mcp__([^_]+)(?:__|$)/);
   146→        if (match) {
   147→          availableMCPs.add(match[1].toLowerCase());
   148→        }
   149→      });
   150→    } catch (e) {
   151→      // Ignore parse errors
   152→    }
   153→  }
   154→
   155→  return availableMCPs;
   156→}
   157→
   158→function main() {
   159→  try {
   160→    // Read JSON from STDIN (Claude Code hooks pass input via stdin)
   161→    const stdinBuffer = fs.readFileSync(0, 'utf-8');
   162→    let input;
   163→
   164→    if (stdinBuffer.trim()) {
   165→      try {
   166→        input = JSON.parse(stdinBuffer);
   167→      } catch (parseError) {
   168→        // Input is not valid JSON, exit silently
   169→        process.exit(0);
   170→      }
   171→    } else {
