     1→#!/usr/bin/env node
     2→/**
     3→ * Memory Cache - Hash-Based Content Caching
     4→ *
     5→ * Tracks file hashes to detect changes and skip reading unchanged files.
     6→ * Provides 60-70% token savings by caching previously read content.
     7→ *
     8→ * Cache stored in: .claude/.memory-cache.json
     9→ *
    10→ * Usage:
    11→ *   const cache = require('./memory-cache.js');
    12→ *
    13→ *   // Check if file content is cached
    14→ *   if (cache.isCached(filePath)) {
    15→ *     const content = cache.getCached(filePath);
    16→ *     // Use cached content
    17→ *   } else {
    18→ *     const content = fs.readFileSync(filePath, 'utf-8');
    19→ *     cache.setCached(filePath, content);
    20→ *   }
    21→ *
    22→ * @module memory-cache
    23→ */
    24→
    25→const crypto = require('crypto');
    26→const fs = require('fs');
    27→const path = require('path');
    28→const os = require('os');
    29→
    30→// Cache file location
    31→const CACHE_FILE = '.claude/.memory-cache.json';
    32→
    33→// Cache entry structure
    34→// {
    35→//   "/path/to/file": {
    36→//     hash: "sha256-abc123...",
    37→//     content: "file content...",
    38→//     size: 1234,
    39→//     mtime: 1234567890,
    40→//     accessed: 1234567890
    41→//   }
    42→// }
    43→
    44→/**
    45→ * Generate SHA-256 hash of content
    46→ *
    47→ * @param {string} content - Content to hash
    48→ * @returns {string} Hash in format "sha256-..."
    49→ */
    50→function generateHash(content) {
    51→  return 'sha256-' + crypto.createHash('sha256').update(content).digest('hex');
    52→}
    53→
    54→/**
    55→ * Load cache from disk
    56→ *
    57→ * @returns {object} Cache object
    58→ */
    59→function loadCache() {
    60→  try {
    61→    if (fs.existsSync(CACHE_FILE)) {
    62→      const content = fs.readFileSync(CACHE_FILE, 'utf-8');
    63→      return JSON.parse(content);
    64→    }
    65→  } catch (e) {
    66→    // Cache file corrupted, start fresh
    67→  }
    68→  return {};
    69→}
    70→
    71→/**
    72→ * Save cache to disk
    73→ *
    74→ * @param {object} cache - Cache object to save
    75→ */
    76→function saveCache(cache) {
    77→  try {
    78→    const cacheDir = path.dirname(CACHE_FILE);
    79→    if (!fs.existsSync(cacheDir)) {
    80→      fs.mkdirSync(cacheDir, { recursive: true });
    81→    }
    82→    fs.writeFileSync(CACHE_FILE, JSON.stringify(cache, null, 2), 'utf-8');
    83→  } catch (e) {
    84→    // Fail silently - cache is optional
    85→  }
    86→}
    87→
    88→/**
    89→ * Normalize file path for cache key
    90→ *
    91→ * @param {string} filePath - Path to normalize
    92→ * @returns {string} Normalized absolute path
    93→ */
    94→function normalizePath(filePath) {
    95→  if (!path.isAbsolute(filePath)) {
    96→    filePath = path.resolve(process.cwd(), filePath);
    97→  }
    98→  return path.normalize(filePath);
    99→}
   100→
   101→/**
   102→ * Check if a file is cached and unchanged
   103→ *
   104→ * @param {string} filePath - Path to file
   105→ * @returns {boolean} True if file is cached and unchanged
   106→ */
   107→function isCached(filePath) {
   108→  const cache = loadCache();
   109→  const normalizedPath = normalizePath(filePath);
   110→  const entry = cache[normalizedPath];
   111→
   112→  if (!entry) {
   113→    return false;
   114→  }
   115→
   116→  // Check if file still exists
   117→  if (!fs.existsSync(filePath)) {
   118→    delete cache[normalizedPath];
   119→    saveCache(cache);
   120→    return false;
   121→  }
   122→
   123→  // Check if file was modified
   124→  const stats = fs.statSync(filePath);
   125→  if (stats.mtime.getTime() !== entry.mtime || stats.size !== entry.size) {
   126→    delete cache[normalizedPath];
   127→    saveCache(cache);
   128→    return false;
   129→  }
   130→
   131→  // Update access time
   132→  entry.accessed = Date.now();
   133→  saveCache(cache);
   134→
   135→  return true;
   136→}
   137→
   138→/**
   139→ * Get cached content for a file
   140→ *
   141→ * @param {string} filePath - Path to file
   142→ * @returns {string|null} Cached content or null if not cached
   143→ */
   144→function getCached(filePath) {
   145→  const cache = loadCache();
   146→  const normalizedPath = normalizePath(filePath);
   147→  const entry = cache[normalizedPath];
   148→
   149→  if (!entry) {
   150→    return null;
   151→  }
   152→
   153→  return entry.content;
   154→}
   155→
   156→/**
   157→ * Cache content for a file
   158→ *
   159→ * @param {string} filePath - Path to file
   160→ * @param {string} content - Content to cache
   161→ * @returns {boolean} True if cached successfully
   162→ */
   163→function setCached(filePath, content) {
   164→  try {
   165→    const cache = loadCache();
   166→    const normalizedPath = normalizePath(filePath);
   167→
   168→    const stats = fs.statSync(filePath);
   169→
   170→    cache[normalizedPath] = {
   171→      hash: generateHash(content),
   172→      content: content,
   173→      size: stats.size,
   174→      mtime: stats.mtime.getTime(),
   175→      accessed: Date.now()
   176→    };
   177→
   178→    saveCache(cache);
   179→    return true;
   180→  } catch (e) {
   181→    return false;
   182→  }
   183→}
   184→
   185→/**
   186→ * Get hash of a file (without caching content)
   187→ *
   188→ * @param {string} filePath - Path to file
   189→ * @returns {string|null} Hash or null if error
   190→ */
   191→function getFileHash(filePath) {
   192→  try {
   193→    const content = fs.readFileSync(filePath, 'utf-8');
   194→    return generateHash(content);
   195→  } catch (e) {
   196→    return null;
   197→  }
   198→}
   199→
   200→/**
   201→ * Check if file content has changed since last cache
   202→ *
   203→ * @param {string} filePath - Path to file
   204→ * @returns {boolean} True if file has changed
   205→ */
   206→function hasChanged(filePath) {
   207→  return !isCached(filePath);
   208→}
   209→
   210→/**
   211→ * Invalidate cache entry for a file
   212→ *
   213→ * @param {string} filePath - Path to file
   214→ * @returns {boolean} True if entry was removed
   215→ */
   216→function invalidate(filePath) {
   217→  const cache = loadCache();
   218→  const normalizedPath = normalizePath(filePath);
   219→
   220→  if (cache[normalizedPath]) {
   221→    delete cache[normalizedPath];
   222→    saveCache(cache);
   223→    return true;
   224→  }
   225→
   226→  return false;
   227→}
   228→
   229→/**
   230→ * Clear all cache entries
   231→ *
   232→ * @returns {number} Number of entries cleared
   233→ */
   234→function clearCache() {
   235→  const cache = loadCache();
   236→  const count = Object.keys(cache).length;
   237→
   238→  try {
   239→    if (fs.existsSync(CACHE_FILE)) {
   240→      fs.unlinkSync(CACHE_FILE);
   241→    }
   242→  } catch (e) {
   243→    // Ignore errors
   244→  }
   245→
   246→  return count;
   247→}
   248→
   249→/**
   250→ * Get cache statistics
   251→ *
   252→ * @returns {object} Cache stats {entries, totalSize, oldest, newest}
   253→ */
   254→function getStats() {
   255→  const cache = loadCache();
   256→  const entries = Object.keys(cache).length;
   257→  let totalSize = 0;
   258→  let oldest = Date.now();
   259→  let newest = 0;
   260→
   261→  for (const key in cache) {
   262→    const entry = cache[key];
   263→    totalSize += entry.size || 0;
   264→    if (entry.accessed < oldest) oldest = entry.accessed;
   265→    if (entry.accessed > newest) newest = entry.accessed;
   266→  }
   267→
   268→  return {
   269→    entries,
   270→    totalSize,
   271→    oldest: oldest === Date.now() ? null : new Date(oldest).toISOString(),
   272→    newest: newest > 0 ? new Date(newest).toISOString() : null,
   273→    cacheFile: path.resolve(CACHE_FILE)
   274→  };
   275→}
   276→
   277→/**
   278→ * Clean old cache entries (older than specified days)
   279→ *
   280→ * @param {number} days - Days to keep entries (default: 7)
   281→ * @returns {number} Number of entries removed
   282→ */
   283→function cleanOldEntries(days = 7) {
   284→  const cache = loadCache();
   285→  const cutoff = Date.now() - (days * 24 * 60 * 60 * 1000);
   286→  let removed = 0;
   287→
   288→  for (const key in cache) {
   289→    if (cache[key].accessed < cutoff) {
   290→      delete cache[key];
   291→      removed++;
   292→    }
   293→  }
   294→
   295→  if (removed > 0) {
   296→    saveCache(cache);
   297→  }
   298→
   299→  return removed;
   300→}
   301→
   302→/**
   303→ * Batch cache multiple files
   304→ *
   305→ * @param {string[]} filePaths - Array of file paths
   306→ * @returns {object} Results {cached, skipped, errors}
   307→ */
   308→function batchCache(filePaths) {
   309→  const results = {
   310→    cached: [],
   311→    skipped: [],
   312→    errors: []
   313→  };
   314→
   315→  for (const filePath of filePaths) {
   316→    try {
   317→      if (isCached(filePath)) {
   318→        results.skipped.push(filePath);
   319→      } else {
   320→        const content = fs.readFileSync(filePath, 'utf-8');
   321→        setCached(filePath, content);
   322→        results.cached.push(filePath);
   323→      }
   324→    } catch (e) {
   325→      results.errors.push({ path: filePath, error: e.message });
   326→    }
   327→  }
   328→
   329→  return results;
   330→}
   331→
   332→/**
   333→ * Get cached files matching a pattern
   334→ *
   335→ * @param {string} pattern - Glob pattern to match cache keys
   336→ * @returns {string[]} Array of matching file paths
   337→ */
   338→function findCached(pattern) {
   339→  const cache = loadCache();
   340→  const regex = new RegExp(pattern.replace(/\*/g, '.*').replace(/\?/g, '.'));
   341→  const matches = [];
   342→
   343→  for (const key in cache) {
   344→    if (regex.test(key)) {
   345→      matches.push(key);
   346→    }
   347→  }
   348→
   349→  return matches;
   350→}
   351→
   352→/**
   353→ * Export cache to JSON string
   354→ *
   355→ * @returns {string} JSON string of cache
   356→ */
   357→function exportCache() {
   358→  return JSON.stringify(loadCache(), null, 2);
   359→}
   360→
   361→/**
   362→ * Import cache from JSON string
   363→ *
   364→ * @param {string} json - JSON string to import
   365→ * @returns {boolean} True if imported successfully
   366→ */
   367→function importCache(json) {
   368→  try {
   369→    const cache = JSON.parse(json);
   370→    saveCache(cache);
   371→    return true;
   372→  } catch (e) {
   373→    return false;
   374→  }
   375→}
