'use client'

import { useState, useEffect } from 'react'
import { Loader2, Mail, Shield, Calendar, Clock, Edit2, Check, X, Key } from 'lucide-react'
import type {
  UserDetail,
  MetadataEditorState,
} from './types'

interface UserDetailProps {
  userId: string
}

export function UserDetail({ userId }: UserDetailProps) {
  const [user, setUser] = useState<UserDetail | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [metadataState, setMetadataState] = useState<MetadataEditorState>({
    isEditing: false,
    jsonError: null,
    editedMetadata: '',
  })

  useEffect(() => {
    fetchUserDetail()
  }, [userId])

  const fetchUserDetail = async () => {
    setLoading(true)
    setError(null)

    try {
      const token = localStorage.getItem('accessToken')
      if (!token) {
        throw new Error('No authentication token found')
      }

      const res = await fetch(`/api/admin/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      })

      if (!res.ok) {
        if (res.status === 401) {
          throw new Error('Authentication expired')
        }
        if (res.status === 404) {
          throw new Error('User not found')
        }
        throw new Error('Failed to fetch user details')
      }

      const data = await res.json()
      setUser(data.user)
      setMetadataState({
        isEditing: false,
        jsonError: null,
        editedMetadata: JSON.stringify(data.user.user_metadata, null, 2),
      })
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Failed to fetch user details'
      setError(message)
      console.error('Failed to fetch user details:', err)
    } finally {
      setLoading(false)
    }
  }

  const handleStartEditMetadata = () => {
    if (!user) return
    setMetadataState({
      isEditing: true,
      jsonError: null,
      editedMetadata: JSON.stringify(user.user_metadata, null, 2),
    })
  }

  const handleCancelEditMetadata = () => {
    if (!user) return
    setMetadataState({
      isEditing: false,
      jsonError: null,
      editedMetadata: JSON.stringify(user.user_metadata, null, 2),
    })
  }

  const handleSaveMetadata = async () => {
    if (!user) return

    try {
      const parsed = JSON.parse(metadataState.editedMetadata)
      setMetadataState(prev => ({ ...prev, jsonError: null }))

      const token = localStorage.getItem('accessToken')
      if (!token) {
        throw new Error('No authentication token found')
      }

      const res = await fetch(`/api/admin/users/${userId}`, {
        method: 'PATCH',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_metadata: parsed,
        }),
      })

      if (!res.ok) {
        throw new Error('Failed to update user metadata')
      }

      const data = await res.json()
      setUser(data.user)
      setMetadataState({
        isEditing: false,
        jsonError: null,
        editedMetadata: JSON.stringify(data.user.user_metadata, null, 2),
      })
    } catch (err) {
      if (err instanceof SyntaxError) {
        setMetadataState(prev => ({
          ...prev,
          jsonError: 'Invalid JSON format',
        }))
      } else {
        const message = err instanceof Error ? err.message : 'Failed to save metadata'
        setMetadataState(prev => ({
          ...prev,
          jsonError: message,
        }))
      }
    }
  }

  const formatTimestamp = (timestamp: string | null) => {
    if (!timestamp) return 'Never'
    return new Date(timestamp).toLocaleString()
  }

  const getRoleBadgeColor = (role: string) => {
    if (role === 'admin') return 'bg-red-100 text-red-800'
    if (role === 'operator') return 'bg-blue-100 text-blue-800'
    return 'bg-slate-100 text-slate-800'
  }

  const getAuthProviderLabel = (provider: string) => {
    if (provider === 'oauth_google') return 'Google OAuth'
    if (provider === 'oauth_github') return 'GitHub OAuth'
    if (provider === 'oauth_gitlab') return 'GitLab OAuth'
    return 'Email & Password'
  }

  if (loading) {
    return (
      <div className="bg-white rounded-xl border border-slate-200 p-12">
        <div className="flex flex-col items-center justify-center">
          <Loader2 className="w-8 h-8 text-slate-400 animate-spin mb-3" />
          <p className="text-slate-500">Loading user details...</p>
        </div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="bg-white rounded-xl border border-slate-200 p-12">
        <div className="text-center">
          <p className="text-red-600 font-medium mb-2">Error loading user</p>
          <p className="text-slate-500 text-sm mb-4">{error}</p>
          <button
            onClick={fetchUserDetail}
            className="px-4 py-2 bg-slate-900 text-white rounded-lg hover:bg-slate-800 transition"
          >
            Retry
          </button>
        </div>
      </div>
    )
  }

  if (!user) {
    return (
      <div className="bg-white rounded-xl border border-slate-200 p-12">
        <div className="text-center">
          <p className="text-slate-500">User not found</p>
        </div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <div className="bg-white rounded-xl border border-slate-200 overflow-hidden">
        <div className="px-6 py-4 border-b border-slate-200">
          <h2 className="font-semibold text-slate-900">User Information</h2>
        </div>

        <div className="p-6">
          <div className="flex items-start gap-6 mb-6">
            <div className="w-16 h-16 bg-slate-100 rounded-full flex items-center justify-center flex-shrink-0">
              <Mail className="w-8 h-8 text-slate-400" />
            </div>
            <div className="flex-1">
              <h3 className="text-xl font-semibold text-slate-900 mb-1">
                {user.name || 'No name provided'}
              </h3>
              <p className="text-slate-600 mb-2">{user.email}</p>
              <div className="flex items-center gap-2">
                <span className={`px-2 py-1 text-xs font-medium rounded ${getRoleBadgeColor(user.role)}`}>
                  {user.role.charAt(0).toUpperCase() + user.role.slice(1)}
                </span>
                {user.organization && (
                  <span className="px-2 py-1 text-xs font-medium rounded bg-slate-100 text-slate-700">
                    {user.organization}
                  </span>
                )}
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h4 className="text-sm font-semibold text-slate-700 mb-3">Basic Information</h4>
              <dl className="space-y-3">
                <div className="flex items-start gap-3">
                  <Shield className="w-4 h-4 text-slate-400 mt-0.5 flex-shrink-0" />
                  <div className="flex-1 min-w-0">
                    <dt className="text-xs text-slate-500">User ID</dt>
                    <dd className="text-sm text-slate-900 font-mono truncate">{user.id}</dd>
                  </div>
                </div>

                <div className="flex items-start gap-3">
                  <Calendar className="w-4 h-4 text-slate-400 mt-0.5 flex-shrink-0" />
                  <div>
                    <dt className="text-xs text-slate-500">Created At</dt>
                    <dd className="text-sm text-slate-900">{formatTimestamp(user.created_at)}</dd>
                  </div>
                </div>

                <div className="flex items-start gap-3">
                  <Clock className="w-4 h-4 text-slate-400 mt-0.5 flex-shrink-0" />
                  <div>
                    <dt className="text-xs text-slate-500">Updated At</dt>
                    <dd className="text-sm text-slate-900">{formatTimestamp(user.updated_at)}</dd>
                  </div>
                </div>
              </dl>
            </div>

            <div>
              <h4 className="text-sm font-semibold text-slate-700 mb-3">Authentication</h4>
              <dl className="space-y-3">
                <div className="flex items-start gap-3">
                  <Key className="w-4 h-4 text-slate-400 mt-0.5 flex-shrink-0" />
                  <div>
                    <dt className="text-xs text-slate-500">Auth Provider</dt>
                    <dd className="text-sm text-slate-900">{getAuthProviderLabel(user.auth_provider)}</dd>
                  </div>
                </div>

                {user.auth_info && (
                  <>
                    <div className="flex items-start gap-3">
                      <Calendar className="w-4 h-4 text-slate-400 mt-0.5 flex-shrink-0" />
                      <div>
                        <dt className="text-xs text-slate-500">Last Sign In</dt>
                        <dd className="text-sm text-slate-900">
                          {formatTimestamp(user.auth_info.last_login_at)}
                        </dd>
                      </div>
                    </div>

                    <div className="flex items-start gap-3">
                      <Shield className="w-4 h-4 text-slate-400 mt-0.5 flex-shrink-0" />
                      <div>
                        <dt className="text-xs text-slate-500">Sign In Count</dt>
                        <dd className="text-sm text-slate-900">{user.auth_info.sign_in_count}</dd>
                      </div>
                    </div>
                  </>
                )}
              </dl>
            </div>
          </div>
        </div>
      </div>

      <div className="bg-white rounded-xl border border-slate-200 overflow-hidden">
        <div className="px-6 py-4 border-b border-slate-200 flex items-center justify-between">
          <h2 className="font-semibold text-slate-900">User Metadata</h2>
          {!metadataState.isEditing && (
            <button
              onClick={handleStartEditMetadata}
              className="flex items-center gap-2 px-3 py-1.5 text-sm font-medium text-slate-700 hover:bg-slate-100 rounded-lg transition"
            >
              <Edit2 className="w-4 h-4" />
              Edit
            </button>
          )}
        </div>

        <div className="p-6">
          {metadataState.isEditing ? (
            <div>
              <textarea
                value={metadataState.editedMetadata}
                onChange={(e) => setMetadataState(prev => ({ ...prev, editedMetadata: e.target.value }))}
                className="w-full h-64 p-4 font-mono text-sm bg-slate-900 text-emerald-400 rounded-lg resize-none focus:outline-none focus:ring-2 focus:ring-slate-400"
              />
              {metadataState.jsonError && (
                <p className="mt-2 text-sm text-red-600">{metadataState.jsonError}</p>
              )}
              <div className="flex items-center gap-3 mt-4">
                <button
                  onClick={handleSaveMetadata}
                  className="flex items-center gap-2 px-4 py-2 bg-slate-900 text-white rounded-lg hover:bg-slate-800 transition"
                >
                  <Check className="w-4 h-4" />
                  Save Changes
                </button>
                <button
                  onClick={handleCancelEditMetadata}
                  className="flex items-center gap-2 px-4 py-2 bg-slate-100 text-slate-700 rounded-lg hover:bg-slate-200 transition"
                >
                  <X className="w-4 h-4" />
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <div className="bg-slate-900 rounded-lg p-4 overflow-x-auto">
              <pre className="text-xs text-emerald-400">
                {JSON.stringify(user.user_metadata, null, 2)}
              </pre>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

