     1→/**
     2→ * Backups Data Layer Service
     3→ *
     4→ * Provides CRUD operations for the backups table in the control_plane schema.
     5→ * All functions use parameterized queries for SQL injection prevention.
     6→ *
     7→ * US-003: Create Backup History Table - Step 7: Data Layer
     8→ */
     9→
    10→import { v4 as uuidv4 } from 'uuid';
    11→import { query } from '@nextmavens/audit-logs-database';
    12→import type {
    13→  Backup,
    14→  BackupType,
    15→  CreateBackupInput,
    16→  BackupQuery,
    17→  BackupResponse,
    18→  BackupStats,
    19→} from '@nextmavens/audit-logs-database';
    20→
    21→/**
    22→ * Validation constants
    23→ */
    24→const VALIDATION = {
    25→  /** Maximum file_id length */
    26→  MAX_FILE_ID_LENGTH: 500,
    27→  /** Minimum file_id length */
    28→  MIN_FILE_ID_LENGTH: 1,
    29→  /** Maximum backup size in bytes (10GB) */
    30→  MAX_BACKUP_SIZE: 10 * 1024 * 1024 * 1024,
    31→  /** Minimum backup size (0 bytes allowed for empty backups) */
    32→  MIN_BACKUP_SIZE: 0,
    33→  /** Default expiration period in milliseconds (30 days) */
    34→  DEFAULT_EXPIRATION_MS: 30 * 24 * 60 * 60 * 1000,
    35→  /** Maximum limit for pagination */
    36→  MAX_LIMIT: 1000,
    37→  /** Default limit for pagination */
    38→  DEFAULT_LIMIT: 50,
    39→  /** Maximum offset for pagination */
    40→  MAX_OFFSET: 100000,
    41→} as const;
    42→
    43→/**
    44→ * Custom error class for backup operations
    45→ */
    46→export class BackupError extends Error {
    47→  constructor(message: string, public readonly code?: string) {
    48→    super(message);
    49→    this.name = 'BackupError';
    50→  }
    51→}
    52→
    53→/**
    54→ * Validate project_id format
    55→ */
    56→function validateProjectId(projectId: string): void {
    57→  if (typeof projectId !== 'string') {
    58→    throw new BackupError('Project ID must be a string', 'INVALID_PROJECT_ID');
    59→  }
    60→  if (projectId.trim().length === 0) {
    61→    throw new BackupError('Project ID cannot be empty', 'INVALID_PROJECT_ID');
    62→  }
    63→}
    64→
    65→/**
    66→ * Validate backup type
    67→ */
    68→function validateBackupType(type: BackupType): void {
    69→  if (typeof type !== 'string') {
    70→    throw new BackupError('Backup type must be a string', 'INVALID_BACKUP_TYPE');
    71→  }
    72→  const validTypes = ['database', 'storage', 'logs'];
    73→  if (!validTypes.includes(type)) {
    74→    throw new BackupError('Backup type must be one of: database, storage, logs', 'INVALID_BACKUP_TYPE');
    75→  }
    76→}
    77→
    78→/**
    79→ * Validate file_id
    80→ */
    81→function validateFileId(fileId: string): void {
    82→  if (typeof fileId !== 'string') {
    83→    throw new BackupError('File ID must be a string', 'INVALID_FILE_ID');
    84→  }
    85→  if (fileId.trim().length === 0) {
    86→    throw new BackupError('File ID cannot be empty', 'INVALID_FILE_ID');
    87→  }
    88→  if (fileId.length > VALIDATION.MAX_FILE_ID_LENGTH) {
    89→    throw new BackupError(
    90→      `File ID cannot exceed ${VALIDATION.MAX_FILE_ID_LENGTH} characters`,
    91→      'INVALID_FILE_ID'
    92→    );
    93→  }
    94→}
    95→
    96→/**
    97→ * Validate backup size
    98→ */
    99→function validateBackupSize(size: number): void {
   100→  if (typeof size !== 'number') {
   101→    throw new BackupError('Backup size must be a number', 'INVALID_SIZE');
   102→  }
   103→  if (!Number.isInteger(size)) {
   104→    throw new BackupError('Backup size must be an integer', 'INVALID_SIZE');
   105→  }
   106→  if (size < VALIDATION.MIN_BACKUP_SIZE) {
   107→    throw new BackupError(
   108→      `Backup size must be at least ${VALIDATION.MIN_BACKUP_SIZE} bytes`,
   109→      'INVALID_SIZE'
   110→    );
   111→  }
   112→  if (size > VALIDATION.MAX_BACKUP_SIZE) {
   113→    throw new BackupError(
   114→      `Backup size cannot exceed ${VALIDATION.MAX_BACKUP_SIZE} bytes (10GB)`,
   115→      'INVALID_SIZE'
   116→    );
   117→  }
   118→}
   119→
   120→/**
   121→ * Validate pagination parameters
   122→ */
   123→function validatePagination(limit?: number, offset?: number): void {
   124→  if (limit !== undefined) {
   125→    if (!Number.isInteger(limit)) {
   126→      throw new BackupError('Limit must be an integer', 'INVALID_PAGINATION');
   127→    }
   128→    if (limit < 1) {
   129→      throw new BackupError('Limit must be at least 1', 'INVALID_PAGINATION');
   130→    }
   131→    if (limit > VALIDATION.MAX_LIMIT) {
   132→      throw new BackupError(`Limit cannot exceed ${VALIDATION.MAX_LIMIT}`, 'INVALID_PAGINATION');
   133→    }
   134→  }
   135→
   136→  if (offset !== undefined) {
   137→    if (!Number.isInteger(offset)) {
   138→      throw new BackupError('Offset must be an integer', 'INVALID_PAGINATION');
   139→    }
   140→    if (offset < 0) {
   141→      throw new BackupError('Offset must be non-negative', 'INVALID_PAGINATION');
   142→    }
   143→    if (offset > VALIDATION.MAX_OFFSET) {
   144→      throw new BackupError(`Offset cannot exceed ${VALIDATION.MAX_OFFSET}`, 'INVALID_PAGINATION');
   145→    }
   146→  }
   147→}
   148→
   149→/**
   150→ * Validate date range
   151→ */
   152→function validateDateRange(date: Date, fieldName: string): void {
   153→  if (!(date instanceof Date) || isNaN(date.getTime())) {
   154→    throw new BackupError(`${fieldName} must be a valid Date object`, 'INVALID_DATE');
   155→  }
   156→}
   157→
   158→/**
   159→ * Create a new backup record
   160→ *
   161→ * @param input - Backup creation input data
   162→ * @returns The created backup record
   163→ *
   164→ * @example
   165→ * ```typescript
   166→ * const backup = await createBackup({
   167→ *   project_id: 'proj-123',
   168→ *   type: BackupType.DATABASE,
   169→ *   file_id: 'telegram-file-123',
   170→ *   size: 1024000,
   171→ * });
   172→ * ```
   173→ */
   174→export async function createBackup(input: CreateBackupInput): Promise<Backup> {
   175→  // Validate inputs
   176→  validateProjectId(input.project_id);
   177→  validateBackupType(input.type);
   178→  validateFileId(input.file_id);
   179→  validateBackupSize(input.size);
   180→
   181→  if (input.expires_at) {
   182→    validateDateRange(input.expires_at, 'expires_at');
   183→  }
   184→
   185→  // Generate a unique backup ID
   186→  const id = uuidv4();
   187→
   188→  // Calculate expiration date (30 days from now if not provided)
   189→  const expiresAt = input.expires_at
   190→    ? input.expires_at
   191→    : new Date(Date.now() + VALIDATION.DEFAULT_EXPIRATION_MS);
   192→
   193→  // Insert backup record
   194→  const queryText = `
   195→    INSERT INTO control_plane.backups (
   196→      id,
   197→      project_id,
   198→      type,
   199→      file_id,
   200→      size,
   201→      expires_at
   202→    ) VALUES ($1, $2, $3, $4, $5, $6)
   203→    RETURNING
   204→      id,
   205→      project_id,
   206→      type,
   207→      file_id,
   208→      size,
   209→      created_at,
   210→      expires_at
   211→  `;
   212→
   213→  const values = [
   214→    id,
   215→    input.project_id,
   216→    input.type,
   217→    input.file_id,
   218→    input.size,
   219→    expiresAt,
   220→  ];
   221→
   222→  try {
   223→    const result = await query(queryText, values);
   224→    const row = result.rows[0];
   225→
   226→    if (!row) {
   227→      throw new BackupError('Failed to create backup record', 'CREATE_FAILED');
   228→    }
   229→
   230→    return {
   231→      id: row.id,
   232→      project_id: row.project_id,
   233→      type: row.type as BackupType,
   234→      file_id: row.file_id,
   235→      size: row.size,
   236→      created_at: row.created_at,
   237→      expires_at: row.expires_at,
   238→    };
   239→  } catch (error) {
   240→    console.error('Failed to create backup:', error);
   241→    if (error instanceof BackupError) {
   242→      throw error;
   243→    }
   244→    throw new BackupError('Failed to create backup record', 'DATABASE_ERROR');
   245→  }
   246→}
   247→
   248→/**
   249→ * Query backups by project with optional filtering
   250→ *
   251→ * @param projectId - The project ID to query backups for
   252→ * @param options - Optional query parameters for filtering and pagination
   253→ * @returns Paginated backup results
   254→ *
   255→ * @example
   256→ * ```typescript
   257→ * // Get all backups for a project
   258→ * const result = await queryByProject('proj-123');
   259→ *
   260→ * // Get database backups with pagination
   261→ * const filtered = await queryByProject('proj-123', {
   262→ *   type: BackupType.DATABASE,
   263→ *   limit: 20,
   264→ *   offset: 0,
   265→ * });
   266→ * ```
   267→ */
   268→export async function queryByProject(
   269→  projectId: string,
   270→  options: Omit<BackupQuery, 'project_id'> = {}
   271→): Promise<BackupResponse> {
   272→  // Validate project ID
   273→  validateProjectId(projectId);
   274→
   275→  // Validate pagination
   276→  const limit = options.limit ?? VALIDATION.DEFAULT_LIMIT;
   277→  const offset = options.offset ?? 0;
   278→  validatePagination(limit, offset);
   279→
   280→  // Build query conditions
   281→  const conditions: string[] = ['project_id = $1'];
   282→  const values: unknown[] = [projectId];
   283→  let paramIndex = 2;
   284→
   285→  // Add optional filters
   286→  if (options.type) {
   287→    validateBackupType(options.type);
   288→    conditions.push(`type = $${paramIndex++}`);
   289→    values.push(options.type);
   290→  }
   291→
   292→  if (options.created_before) {
   293→    validateDateRange(options.created_before, 'created_before');
   294→    conditions.push(`created_at < $${paramIndex++}`);
   295→    values.push(options.created_before);
   296→  }
   297→
   298→  if (options.created_after) {
   299→    validateDateRange(options.created_after, 'created_after');
   300→    conditions.push(`created_at > $${paramIndex++}`);
   301→    values.push(options.created_after);
   302→  }
   303→
   304→  if (options.expires_before) {
   305→    validateDateRange(options.expires_before, 'expires_before');
   306→    conditions.push(`expires_at < $${paramIndex++}`);
   307→    values.push(options.expires_before);
   308→  }
   309→
   310→  if (options.expires_after) {
   311→    validateDateRange(options.expires_after, 'expires_after');
   312→    conditions.push(`expires_at > $${paramIndex++}`);
   313→    values.push(options.expires_after);
   314→  }
   315→
   316→  if (options.min_size !== undefined) {
   317→    validateBackupSize(options.min_size);
   318→    conditions.push(`size >= $${paramIndex++}`);
   319→    values.push(options.min_size);
   320→  }
   321→
   322→  if (options.max_size !== undefined) {
   323→    validateBackupSize(options.max_size);
   324→    conditions.push(`size <= $${paramIndex++}`);
   325→    values.push(options.max_size);
   326→  }
   327→
   328→  const whereClause = conditions.join(' AND ');
   329→
   330→  // Get total count
   331→  const countQuery = `
   332→    SELECT COUNT(*) as total
   333→    FROM control_plane.backups
   334→    WHERE ${whereClause}
   335→  `;
   336→
   337→  // Get paginated results
   338→  const dataQuery = `
   339→    SELECT
   340→      id,
   341→      project_id,
   342→      type,
   343→      file_id,
   344→      size,
   345→      created_at,
   346→      expires_at
   347→    FROM control_plane.backups
   348→    WHERE ${whereClause}
   349→    ORDER BY created_at DESC
   350→    LIMIT $${paramIndex++} OFFSET $${paramIndex++}
   351→  `;
   352→
   353→  values.push(limit, offset);
   354→
   355→  try {
   356→    // Execute queries in parallel
   357→    const [countResult, dataResult] = await Promise.all([
   358→      query(countQuery, values.slice(0, paramIndex - 2)),
   359→      query(dataQuery, values),
   360→    ]);
   361→
   362→    const countRow = countResult.rows[0];
   363→    const total = countRow ? parseInt(countRow.total || '0', 10) : 0;
   364→    const hasMore = offset + limit < total;
   365→
   366→    return {
   367→      data: dataResult.rows.map((row: Record<string, unknown>) => ({
   368→        id: row.id as string,
   369→        project_id: row.project_id as string,
   370→        type: row.type as BackupType,
   371→        file_id: row.file_id as string,
   372→        size: row.size as number,
   373→        created_at: row.created_at as Date,
   374→        expires_at: row.expires_at as Date,
   375→      })),
   376→      total,
   377→      limit,
   378→      offset,
   379→      has_more: hasMore,
   380→    };
   381→  } catch (error) {
   382→    console.error('Failed to query backups:', error);
   383→    throw new BackupError('Failed to retrieve backup records', 'DATABASE_ERROR');
   384→  }
   385→}
   386→
   387→/**
   388→ * Get a single backup by ID
   389→ *
   390→ * @param id - The backup ID
   391→ * @returns The backup record or null if not found
   392→ *
   393→ * @example
   394→ * ```typescript
   395→ * const backup = await getBackupById('backup-123');
   396→ * if (backup) {
   397→ *   console.log('Backup size:', backup.size);
   398→ * }
   399→ * ```
   400→ */
   401→export async function getBackupById(id: string): Promise<Backup | null> {
   402→  if (typeof id !== 'string') {
   403→    throw new BackupError('Backup ID must be a string', 'INVALID_BACKUP_ID');
   404→  }
   405→  if (id.trim().length === 0) {
   406→    throw new BackupError('Backup ID cannot be empty', 'INVALID_BACKUP_ID');
   407→  }
   408→
   409→  const queryText = `
   410→    SELECT
   411→      id,
   412→      project_id,
   413→      type,
   414→      file_id,
   415→      size,
   416→      created_at,
   417→      expires_at
   418→    FROM control_plane.backups
   419→    WHERE id = $1
   420→  `;
   421→
   422→  try {
   423→    const result = await query(queryText, [id]);
   424→
   425→    if (result.rows.length === 0) {
   426→      return null;
   427→    }
   428→
   429→    const row = result.rows[0];
   430→
   431→    if (!row) {
   432→      return null;
   433→    }
   434→
   435→    return {
   436→      id: row.id,
   437→      project_id: row.project_id,
   438→      type: row.type as BackupType,
   439→      file_id: row.file_id,
   440→      size: row.size,
   441→      created_at: row.created_at,
   442→      expires_at: row.expires_at,
   443→    };
   444→  } catch (error) {
   445→    console.error('Failed to get backup by ID:', error);
   446→    throw new BackupError('Failed to retrieve backup record', 'DATABASE_ERROR');
   447→  }
   448→}
   449→
   450→/**
   451→ * Update backup file_id and size
   452→ *
   453→ * @param id - The backup ID
   454→ * @param updates - Fields to update (file_id and/or size)
   455→ * @returns The updated backup record or null if not found
   456→ *
   457→ * @example
   458→ * ```typescript
   459→ * const updated = await updateBackup('backup-123', {
   460→ *   file_id: 'new-file-id',
   461→ *   size: 2048000,
   462→ * });
   463→ * ```
   464→ */
   465→export async function updateBackup(
   466→  id: string,
   467→  updates: Partial<Pick<Backup, 'file_id' | 'size'>>
   468→): Promise<Backup | null> {
   469→  if (typeof id !== 'string') {
   470→    throw new BackupError('Backup ID must be a string', 'INVALID_BACKUP_ID');
   471→  }
   472→  if (id.trim().length === 0) {
   473→    throw new BackupError('Backup ID cannot be empty', 'INVALID_BACKUP_ID');
   474→  }
   475→
   476→  // Validate updates
   477→  const updatesList: string[] = [];
   478→  const values: unknown[] = [id];
   479→  let paramIndex = 2;
   480→
   481→  if (updates.file_id !== undefined) {
   482→    validateFileId(updates.file_id);
   483→    updatesList.push(`file_id = $${paramIndex++}`);
   484→    values.push(updates.file_id);
   485→  }
   486→
   487→  if (updates.size !== undefined) {
   488→    validateBackupSize(updates.size);
   489→    updatesList.push(`size = $${paramIndex++}`);
   490→    values.push(updates.size);
   491→  }
   492→
   493→  if (updatesList.length === 0) {
   494→    throw new BackupError('No fields to update', 'NO_UPDATES');
   495→  }
   496→
   497→  const queryText = `
   498→    UPDATE control_plane.backups
   499→    SET ${updatesList.join(', ')}
   500→    WHERE id = $1
   501→    RETURNING
   502→      id,
   503→      project_id,
   504→      type,
   505→      file_id,
   506→      size,
   507→      created_at,
   508→      expires_at
   509→  `;
   510→
   511→  try {
   512→    const result = await query(queryText, values);
   513→
   514→    if (result.rows.length === 0) {
   515→      return null;
   516→    }
   517→
   518→    const row = result.rows[0];
   519→
   520→    if (!row) {
   521→      return null;
   522→    }
   523→
   524→    return {
   525→      id: row.id,
   526→      project_id: row.project_id,
   527→      type: row.type as BackupType,
   528→      file_id: row.file_id,
   529→      size: row.size,
   530→      created_at: row.created_at,
   531→      expires_at: row.expires_at,
   532→    };
   533→  } catch (error) {
   534→    console.error('Failed to update backup:', error);
   535→    throw new BackupError('Failed to update backup record', 'DATABASE_ERROR');
   536→  }
   537→}
   538→
   539→/**
   540→ * Delete a backup by ID
   541→ *
   542→ * @param id - The backup ID
   543→ * @returns True if deleted, false if not found
   544→ *
   545→ * @example
   546→ * ```typescript
   547→ * const deleted = await deleteBackup('backup-123');
   548→ * if (deleted) {
   549→ *   console.log('Backup deleted successfully');
   550→ * }
   551→ * ```
   552→ */
   553→export async function deleteBackup(id: string): Promise<boolean> {
   554→  if (typeof id !== 'string') {
   555→    throw new BackupError('Backup ID must be a string', 'INVALID_BACKUP_ID');
   556→  }
   557→  if (id.trim().length === 0) {
   558→    throw new BackupError('Backup ID cannot be empty', 'INVALID_BACKUP_ID');
   559→  }
   560→
   561→  const queryText = `
   562→    DELETE FROM control_plane.backups
   563→    WHERE id = $1
   564→    RETURNING id
   565→  `;
   566→
   567→  try {
   568→    const result = await query(queryText, [id]);
   569→    return result.rows.length > 0;
   570→  } catch (error) {
   571→    console.error('Failed to delete backup:', error);
   572→    throw new BackupError('Failed to delete backup record', 'DATABASE_ERROR');
   573→  }
   574→}
   575→
   576→/**
   577→ * Query backups by type and date range
   578→ *
   579→ * @param projectId - The project ID
   580→ * @param type - The backup type
   581→ * @param startDate - Start of date range
   582→ * @param endDate - End of date range
   583→ * @param options - Optional pagination parameters
   584→ * @returns Paginated backup results
   585→ *
   586→ * @example
   587→ * ```typescript
   588→ * const result = await queryByTypeAndDateRange(
   589→ *   'proj-123',
   590→ *   BackupType.DATABASE,
   591→ *   new Date('2026-01-01'),
   592→ *   new Date('2026-01-31'),
   593→ *   { limit: 20 }
   594→ * );
   595→ * ```
   596→ */
   597→export async function queryByTypeAndDateRange(
   598→  projectId: string,
   599→  type: BackupType,
   600→  startDate: Date,
   601→  endDate: Date,
   602→  options: { limit?: number; offset?: number } = {}
   603→): Promise<BackupResponse> {
   604→  validateProjectId(projectId);
   605→  validateBackupType(type);
   606→  validateDateRange(startDate, 'startDate');
   607→  validateDateRange(endDate, 'endDate');
   608→
   609→  if (startDate >= endDate) {
   610→    throw new BackupError('Start date must be before end date', 'INVALID_DATE_RANGE');
   611→  }
   612→
   613→  const limit = options.limit ?? VALIDATION.DEFAULT_LIMIT;
   614→  const offset = options.offset ?? 0;
   615→  validatePagination(limit, offset);
   616→
   617→  const queryText = `
   618→    SELECT
   619→      id,
   620→      project_id,
   621→      type,
   622→      file_id,
   623→      size,
   624→      created_at,
   625→      expires_at
   626→    FROM control_plane.backups
   627→    WHERE project_id = $1
   628→      AND type = $2
   629→      AND created_at >= $3
   630→      AND created_at <= $4
   631→    ORDER BY created_at DESC
   632→    LIMIT $5 OFFSET $6
   633→  `;
   634→
   635→  const countQuery = `
   636→    SELECT COUNT(*) as total
   637→    FROM control_plane.backups
   638→    WHERE project_id = $1
   639→      AND type = $2
   640→      AND created_at >= $3
   641→      AND created_at <= $4
   642→  `;
   643→
   644→  const values = [projectId, type, startDate, endDate, limit, offset];
   645→
   646→  try {
   647→    const [countResult, dataResult] = await Promise.all([
   648→      query(countQuery, [projectId, type, startDate, endDate]),
   649→      query(queryText, values),
   650→    ]);
   651→
   652→    const countRow = countResult.rows[0];
   653→    const total = countRow ? parseInt(countRow.total || '0', 10) : 0;
   654→    const hasMore = offset + limit < total;
   655→
   656→    return {
   657→      data: dataResult.rows.map((row: Record<string, unknown>) => ({
   658→        id: row.id as string,
   659→        project_id: row.project_id as string,
   660→        type: row.type as BackupType,
   661→        file_id: row.file_id as string,
   662→        size: row.size as number,
   663→        created_at: row.created_at as Date,
   664→        expires_at: row.expires_at as Date,
   665→      })),
   666→      total,
   667→      limit,
   668→      offset,
   669→      has_more: hasMore,
   670→    };
   671→  } catch (error) {
   672→    console.error('Failed to query backups by type and date range:', error);
   673→    throw new BackupError('Failed to retrieve backup records', 'DATABASE_ERROR');
   674→  }
   675→}
   676→
   677→/**
   678→ * Get backup statistics for a project
   679→ *
   680→ * @param projectId - The project ID
   681→ * @returns Backup statistics
   682→ *
   683→ * @example
   684→ * ```typescript
   685→ * const stats = await getBackupStats('proj-123');
   686→ * console.log('Total backups:', stats.total_backups);
   687→ * console.log('Total size:', stats.total_size);
   688→ * ```
   689→ */
   690→export async function getBackupStats(projectId: string): Promise<BackupStats> {
   691→  validateProjectId(projectId);
   692→
   693→  const queryText = `
   694→    SELECT
   695→      COUNT(*) as total_backups,
   696→      SUM(size) as total_size,
   697→      COUNT(*) FILTER (WHERE type = 'database') as database_count,
   698→      COUNT(*) FILTER (WHERE type = 'storage') as storage_count,
   699→      COUNT(*) FILTER (WHERE type = 'logs') as logs_count,
   700→      MIN(created_at) as oldest_backup,
   701→      MAX(created_at) as newest_backup,
   702→      COUNT(*) FILTER (WHERE expires_at <= NOW() + INTERVAL '7 days' AND expires_at > NOW()) as expiring_soon
   703→    FROM control_plane.backups
   704→    WHERE project_id = $1
   705→  `;
   706→
   707→  try {
   708→    const result = await query(queryText, [projectId]);
   709→    const row = result.rows[0];
   710→
   711→    if (!row) {
   712→      return {
   713→        total_backups: 0,
   714→        total_size: 0,
   715→        by_type: {
   716→          database: 0,
   717→          storage: 0,
   718→          logs: 0,
   719→        },
   720→        expiring_soon: 0,
   721→      };
   722→    }
   723→
   724→    return {
   725→      total_backups: parseInt(row.total_backups || '0', 10),
   726→      total_size: parseInt(row.total_size || '0', 10),
   727→      by_type: {
   728→        database: parseInt(row.database_count || '0', 10),
   729→        storage: parseInt(row.storage_count || '0', 10),
   730→        logs: parseInt(row.logs_count || '0', 10),
   731→      },
   732→      oldest_backup: row.oldest_backup ? new Date(row.oldest_backup) : undefined,
   733→      newest_backup: row.newest_backup ? new Date(row.newest_backup) : undefined,
   734→      expiring_soon: parseInt(row.expiring_soon || '0', 10),
   735→    };
   736→  } catch (error) {
   737→    console.error('Failed to get backup stats:', error);
   738→    throw new BackupError('Failed to retrieve backup statistics', 'DATABASE_ERROR');
   739→  }
   740→}
   741→
   742→/**
   743→ * Delete expired backups for a project
   744→ *
   745→ * @param projectId - The project ID
   746→ * @returns Count of deleted backups
   747→ *
   748→ * @example
   749→ * ```typescript
   750→ * const count = await deleteExpiredBackups('proj-123');
   751→ * console.log(`Deleted ${count} expired backups`);
   752→ * ```
   753→ */
   754→export async function deleteExpiredBackups(projectId: string): Promise<number> {
   755→  validateProjectId(projectId);
   756→
   757→  const queryText = `
   758→    DELETE FROM control_plane.backups
   759→    WHERE project_id = $1
   760→      AND expires_at < NOW()
   761→    RETURNING id
   762→  `;
   763→
   764→  try {
   765→    const result = await query(queryText, [projectId]);
   766→    return result.rows.length;
   767→  } catch (error) {
   768→    console.error('Failed to delete expired backups:', error);
   769→    throw new BackupError('Failed to delete expired backups', 'DATABASE_ERROR');
   770→  }
   771→}
