    20→/**
    21→ * Restore validation constants
    22→ */
    23→const VALIDATION = {
    24→  /** Maximum backup size for synchronous restore (100MB) */
    25→  MAX_SYNC_RESTORE_SIZE: 100 * 1024 * 1024,
    26→  /** Maximum allowed restore time in milliseconds (2 hours) */
    27→  MAX_RESTORE_TIME: 2 * 60 * 60 * 1000,
    28→  /** Maximum file_id length */
    29→  MAX_FILE_ID_LENGTH: 500,
    30→  /** Minimum file_id length */
    31→  MIN_FILE_ID_LENGTH: 1,
    32→} as const;
    33→
    34→/**
    35→ * Result of a restore operation
    36→ */
    37→export interface RestoreResult {
    38→  /** Whether the restore was successful */
    39→  success: boolean;
    40→
    41→  /** Job ID if restore was queued (for large backups) */
    42→  job_id?: string;
    43→
    44→  /** Status of the restore operation */
    45→  status: 'completed' | 'queued' | 'failed';
    46→
    47→  /** Error message if failed */
    48→  error?: string;
    49→
    50→  /** Number of tables restored */
    51→  tables_restored?: number;
    52→
    53→  /** Size of backup in bytes */
    54→  backup_size?: number;
    55→
    56→  /** Duration of restore in milliseconds */
    57→  duration_ms?: number;
    58→
    59→  /** Warning about data overwrite */
    60→  warning: string;
    61→}
    62→
    63→/**
    64→ * Restore request options
    65→ */
    66→export interface RestoreOptions {
    67→  /** Backup ID to restore (takes precedence over file_id) */
    68→  backup_id?: string;
    69→
    70→  /** Telegram file ID to restore directly */
    71→  file_id?: string;
    72→
    73→  /** Project ID (required for verification) */
    74→  project_id: string;
    75→
    76→  /** Whether to force restore without confirmation */
    77→  force?: boolean;
    78→
    79→  /** Whether to use async processing (for large backups) */
    80→  async?: boolean;
    81→}
    82→
    83→/**
    84→ * Custom error class for restore operations
    85→ */
    86→export class RestoreError extends Error {
    87→  constructor(message: string, public readonly code?: string) {
    88→    super(message);
    89→    this.name = 'RestoreError';
    90→  }
    91→}
    92→
    93→/**
    94→ * Validate file_id format
    95→ */
    96→function validateFileId(fileId: string): void {
    97→  if (typeof fileId !== 'string') {
    98→    throw new RestoreError('File ID must be a string', 'INVALID_FILE_ID');
    99→  }
   100→  if (fileId.trim().length === 0) {
   101→    throw new RestoreError('File ID cannot be empty', 'INVALID_FILE_ID');
   102→  }
   103→  if (fileId.length > VALIDATION.MAX_FILE_ID_LENGTH) {
   104→    throw new RestoreError(
   105→      `File ID cannot exceed ${VALIDATION.MAX_FILE_ID_LENGTH} characters`,
   106→      'INVALID_FILE_ID'
   107→    );
   108→  }
   109→}
   110→
   111→/**
   112→ * Validate backup_id format
   113→ */
   114→function validateBackupId(backupId: string): void {
   115→  if (typeof backupId !== 'string') {
   116→    throw new RestoreError('Backup ID must be a string', 'INVALID_BACKUP_ID');
   117→  }
   118→  if (backupId.trim().length === 0) {
   119→    throw new RestoreError('Backup ID cannot be empty', 'INVALID_BACKUP_ID');
   120→  }
   121→  // UUID format validation
   122→  const uuidRegex =
   123→    /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
   124→  if (!uuidRegex.test(backupId)) {
   125→    throw new RestoreError('Backup ID must be a valid UUID', 'INVALID_BACKUP_ID');
   126→  }
   127→}
   128→
   129→/**
   130→ * Validate project_id format
   131→ */
   132→function validateProjectId(projectId: string): void {
   133→  if (typeof projectId !== 'string') {
   134→    throw new RestoreError('Project ID must be a string', 'INVALID_PROJECT_ID');
   135→  }
   136→  if (projectId.trim().length === 0) {
   137→    throw new RestoreError('Project ID cannot be empty', 'INVALID_PROJECT_ID');
   138→  }
   139→  // Alphanumeric, hyphens, underscores only
   140→  const projectIdRegex = /^[a-zA-Z0-9_-]+$/;
   141→  if (!projectIdRegex.test(projectId)) {
   142→    throw new RestoreError('Project ID contains invalid characters', 'INVALID_PROJECT_ID');
   143→  }
   144→}
