   146→/**
   147→ * Get backup record by backup_id or file_id
   148→ *
   149→ * @param backupId - Optional backup ID
   150→ * @param fileId - Optional file ID
   151→ * @returns Backup record or null
   152→ */
   153→async function getBackupRecord(
   154→  backupId: string | undefined,
   155→  _fileId: string | undefined
   156→): Promise<Backup | null> {
   157→  if (backupId) {
   158→    return await getBackupById(backupId);
   159→  }
   160→  // If only file_id is provided, we cannot look up in database
   161→  // This is okay - we can still restore directly from Telegram
   162→  return null;
   163→}
