   223→/**
   224→ * Decompress backup file if needed
   225→ *
   226→ * @param compressedPath - Path to compressed file
   227→ * @returns Path to decompressed file
   228→ */
   229→async function decompressBackup(compressedPath: string): Promise<string> {
   230→  if (!compressedPath.endsWith('.gz')) {
   231→    return compressedPath;
   232→  }
   233→
   234→  const decompressedPath = compressedPath.replace('.gz', '');
   235→
   236→  await pipeline(
   237→    createReadStream(compressedPath),
   238→    createGunzip(),
   239→    createWriteStream(decompressedPath)
   240→  );
   241→
   242→  // Clean up compressed file
   243→  await unlink(compressedPath);
   244→
   245→  return decompressedPath;
   246→}
