   248→/**
   249→ * Restore SQL dump to database
   250→ *
   251→ * Executes psql command to restore the database from a SQL dump file.
   252→ *
   253→ * @param sqlFilePath - Path to the SQL dump file
   254→ * @param schemaName - The target schema name
   255→ * @returns Number of tables restored
   256→ *
   257→ * @throws RestoreError if restore fails
   258→ */
   259→async function restoreToDatabase(
   260→  sqlFilePath: string,
   261→  schemaName: string
   262→): Promise<number> {
   263→  // Get database connection info from environment
   264→  const databaseUrl = process.env.DATABASE_URL;
   265→  if (!databaseUrl) {
   266→    throw new RestoreError('DATABASE_URL environment variable is not set', 'CONFIG_ERROR');
   267→  }
   268→
   269→  // Parse connection string
   270→  const dbUrl = new URL(databaseUrl);
   271→  const dbHost = dbUrl.hostname;
   272→  const dbPort = dbUrl.port || '5432';
   273→  const dbUser = dbUrl.username;
   274→  const dbName = dbUrl.pathname.replace('/', '');
   275→  const dbPassword = dbUrl.password;
   276→
   277→  // Build psql arguments array (SECURE: no shell injection)
   278→  const psqlArgs = [
   279→    ['-h', dbHost],
   280→    ['-p', dbPort],
   281→    ['-U', dbUser],
   282→    ['-d', dbName],
   283→    ['-f', sqlFilePath],
   284→    ['--quiet'],
   285→    ['--no-psqlrc'],
   286→  ].flat();
   287→
   288→  console.log(`[Restore] Executing psql restore for schema: ${schemaName}`);
   289→
   290→  return new Promise((resolve, reject) => {
   291→    const startTime = Date.now();
   292→    let tablesRestored = 0;
   293→
   294→    const child = spawn('psql', psqlArgs, {
   295→      env: { ...process.env, PGPASSWORD: dbPassword },
   296→      stdio: ['ignore', 'pipe', 'pipe'],
   297→    });
   298→
   299→    // Set up timeout
   300→    const timeoutId = setTimeout(() => {
   301→      child.kill('SIGKILL');
   302→      reject(new RestoreError('Restore operation timed out', 'TIMEOUT'));
   303→    }, VALIDATION.MAX_RESTORE_TIME);
   304→
   305→    // Parse stdout to count tables
   306→    child.stdout.on('data', (data) => {
   307→      const output = data.toString();
   308→      // Count CREATE TABLE statements
   309→      const matches = output.match(/CREATE TABLE/g);
   310→      if (matches) {
   311→        tablesRestored = matches.length;
   312→      }
   313→    });
   314→
   315→    // Handle stderr (psql writes warnings to stderr)
   316→    child.stderr.on('data', (data) => {
   317→      const output = data.toString();
   318→      // Log non-critical warnings
   319→      if (output && !output.includes('WARNING')) {
   320→        console.log(`[Restore] psql output: ${output.trim()}`);
   321→      }
   322→    });
   323→
   324→    // Handle completion
   325→    child.on('close', (code) => {
   326→      clearTimeout(timeoutId);
   327→      const duration = Date.now() - startTime;
   328→      console.log(`[Restore] Completed in ${duration}ms, restored ${tablesRestored} tables`);
   329→
   330→      if (code === 0) {
   331→        resolve(tablesRestored);
   332→      } else {
   333→        reject(new RestoreError(`psql failed with exit code ${code}`, 'RESTORE_FAILED'));
   334→      }
   335→    });
   336→
   337→    // Handle spawn errors
   338→    child.on('error', (error) => {
   339→      clearTimeout(timeoutId);
   340→      reject(new RestoreError(`Failed to execute psql: ${error.message}`, 'EXECUTION_ERROR'));
   341→    });
   342→  });
   343→}
   344→
   345→/**
   346→ * Restore from backup
   347→ *
   348→ * Main function to restore a database backup. Supports both synchronous
   349→ * and asynchronous restoration based on backup size.
   350→ *
   351→ * @param options - Restore options
   352→ * @returns Restore result with status and details
   353→ *
   354→ * @example
   355→ * ```typescript
   356→ * // Restore by backup ID (synchronous for small backups)
   357→ * const result = await restoreFromBackup({
   358→ *   backup_id: 'backup-123',
   359→ *   project_id: 'proj-abc',
   360→ * });
   361→ *
   362→ * // Restore by file ID (asynchronous for large backups)
   363→ * const result = await restoreFromBackup({
   364→ *   file_id: 'telegram-file-456',
   365→ *   project_id: 'proj-abc',
   366→ *   async: true,
   367→ * });
   368→ * ```
   369→ */
   370→export async function restoreFromBackup(options: RestoreOptions): Promise<RestoreResult> {
   371→  const startTime = Date.now();
   372→
   373→  // Validate project_id
   374→  validateProjectId(options.project_id);
   375→
   376→  // Validate that either backup_id or file_id is provided
   377→  if (!options.backup_id && !options.file_id) {
   378→    return {
   379→      success: false,
   380→      status: 'failed',
   381→      error: 'Either backup_id or file_id must be provided',
   382→      warning: 'Restoring from backup will overwrite existing data',
   383→    };
   384→  }
   385→
   386→  // Validate backup_id or file_id if provided
   387→  if (options.backup_id) {
   388→    validateBackupId(options.backup_id);
   389→  }
   390→  if (options.file_id) {
   391→    validateFileId(options.file_id);
   392→  }
   393→
   394→  // Check if force flag is set
   395→  if (!options.force) {
   396→    return {
   397→      success: false,
   398→      status: 'failed',
   399→      error: 'Restore requires force=true to confirm data overwrite',
   400→      warning: 'WARNING: This will overwrite existing data. Set force=true to proceed.',
   401→    };
   402→  }
   403→
   404→  let tempFilePath: string | null = null;
   405→  let backupRecord: Backup | null = null;
   406→
   407→  try {
   408→    // Step 1: Get backup record if backup_id provided
   409→    if (options.backup_id) {
   410→      backupRecord = await getBackupRecord(options.backup_id, options.file_id);
   411→
   412→      if (!backupRecord) {
   413→        return {
   414→          success: false,
   415→          status: 'failed',
   416→          error: 'Backup not found',
   417→          warning: 'Restoring from backup will overwrite existing data',
   418→        };
   419→      }
   420→
   421→      // Verify project ownership
   422→      if (backupRecord.project_id !== options.project_id) {
   423→        return {
   424→          success: false,
