    throw new BackupError('Backup ID cannot be empty', 'INVALID_BACKUP_ID');
  }

  const queryText = `
    SELECT
      id,
      project_id,
      type,
      file_id,
      size,
      created_at,
      expires_at
    FROM control_plane.backups
    WHERE id = $1
  `;

  try {
    const result = await query(queryText, [id]);

    if (result.rows.length === 0) {
