     1→/**
     2→ * Check Usage Limits Job Handler
     3→ *
     4→ * Periodically checks all projects against their configured quotas and:
     5→ * 1. Suspends projects exceeding hard caps
     6→ * 2. Sends warnings at 80% and 90% threshold
     7→ * 3. Tracks usage metrics for monitoring
     8→ *
     9→ * This job runs on a schedule (typically hourly) and checks all active projects.
    10→ *
    11→ * US-008: Implement Check Usage Limits Job
    12→ *
    13→ * @example
    14→ * ```typescript
    15→ * import { enqueueJob } from '@/lib/jobs/queue';
    16→ * import { checkUsageLimitsHandler } from '@/lib/jobs/handlers/check-usage-limits.handler';
    17→ *
    18→ * // Register the handler
    19→ * worker.registerHandler('check_usage_limits', checkUsageLimitsHandler);
    20→ *
    21→ * // Enqueue a check job (usually scheduled via cron)
    22→ * await enqueueJob('check_usage_limits', { check_all: true });
    23→ *
    24→ * // Or check specific project
    25→ * await enqueueJob('check_usage_limits', { project_ids: ['proj-123'] });
    26→ * ```
    27→ */
    28→
    29→import type { JobExecutionResult, JobPayload } from '@nextmavens/audit-logs-database';
    30→import { query } from '@nextmavens/audit-logs-database';
    31→import { enqueueJob } from '@/lib/jobs/queue.js';
    32→
    33→/**
    34→ * Hard cap types for quota enforcement
    35→ */
    36→export enum HardCapType {
    37→  /** Database queries per day */
    38→  DB_QUERIES_PER_DAY = 'db_queries_per_day',
    39→  /** Realtime connections */
    40→  REALTIME_CONNECTIONS = 'realtime_connections',
    41→  /** Storage uploads per day */
    42→  STORAGE_UPLOADS_PER_DAY = 'storage_uploads_per_day',
    43→  /** Function invocations per day */
    44→  FUNCTION_INVOCATIONS_PER_DAY = 'function_invocations_per_day',
    45→}
    46→
    47→/**
    48→ * Project status for suspension checks
    49→ */
    50→export enum ProjectStatus {
    51→  ACTIVE = 'ACTIVE',
    52→  SUSPENDED = 'SUSPENDED',
    53→  ARCHIVED = 'ARCHIVED',
    54→  DELETED = 'DELETED',
    55→}
    56→
    57→/**
    58→ * Check usage limits handler payload
    59→ */
    60→export interface CheckUsageLimitsPayload extends JobPayload {
    61→  /**
    62→   * Check all active projects (default for scheduled job)
    63→   */
    64→  check_all?: boolean;
    65→
    66→  /**
    67→   * Check specific projects only
    68→   */
    69→  project_ids?: string[];
    70→
    71→  /**
    72→   * Cap types to check (default: all)
    73→   */
    74→  cap_types?: HardCapType[];
    75→
    76→  /**
    77→   * Whether to actually suspend projects or just report
    78→   * Set to false for dry-run mode
    79→   */
    80→  enforce_limits?: boolean;
    81→}
    82→
    83→/**
    84→ * Project quota configuration
    85→ */
    86→export interface ProjectQuota {
    87→  /** Project ID */
    88→  project_id: string;
    89→  /** Type of cap */
    90→  cap_type: HardCapType;
    91→  /** Quota limit value */
    92→  cap_value: number;
    93→}
    94→
    95→/**
    96→ * Current usage metrics for a project
    97→ */
    98→export interface ProjectUsage {
    99→  /** Project ID */
   100→  project_id: string;
   101→  /** Database queries today */
   102→  db_queries_today: number;
   103→  /** Active realtime connections */
   104→  realtime_connections: number;
   105→  /** Storage uploads today */
   106→  storage_uploads_today: number;
   107→  /** Function invocations today */
   108→  function_invocations_today: number;
   109→}
   110→
   111→/**
   112→ * Quota check result for a single project
   113→ */
   114→export interface QuotaCheckResult {
   115→  /** Project ID */
   116→  project_id: string;
   117→  /** Whether quota was exceeded */
   118→  exceeded: boolean;
   119→  /** Actions taken */
   120→  actions: QuotaAction[];
   121→}
   122→
   123→/**
   124→ * Actions taken during quota check
   125→ */
   126→export interface QuotaAction {
   127→  /** Type of action */
   128→  action_type: 'warning_sent' | 'project_suspended' | 'no_action';
   129→  /** Cap type that triggered action */
   130→  cap_type: HardCapType;
   131→  /** Current usage value */
   132→  current_usage: number;
   133→  /** Quota limit */
   134→  quota_limit: number;
   135→  /** Usage percentage */
   136→  usage_percentage: number;
   137→  /** Timestamp of action */
   138→  timestamp: Date;
   139→}
   140→
   141→/**
   142→ * Check usage limits job result
   143→ */
   144→export interface CheckUsageLimitsResult extends Record<string, unknown> {
   145→  /** Number of projects checked */
   146→  projects_checked: number;
   147→  /** Number of projects suspended */
   148→  projects_suspended: number;
   149→  /** Number of warnings sent */
   150→  warnings_sent: number;
   151→  /** Detailed results by project */
   152→  details: QuotaCheckResult[];
   153→  /** Duration of check in milliseconds */
   154→  duration_ms: number;
   155→}
   156→
   157→/**
   158→ * Warning thresholds for quota alerts
   159→ */
   160→const WARNING_THRESHOLDS = {
   161→  WARNING_80: 0.8,
   162→  WARNING_90: 0.9,
   163→  HARD_CAP: 1.0,
   164→};
   165→
   166→/**
   167→ * Get all active projects from the database
   168→ */
   169→async function getActiveProjects(): Promise<Array<{ id: string; name: string }>> {
   170→  const queryText = `
   171→    SELECT id, name
   172→    FROM control_plane.projects
   173→    WHERE status = $1
   174→    ORDER BY name
   175→  `;
   176→
   177→  const result = await query(queryText, [ProjectStatus.ACTIVE]);
   178→  return result.rows;
   179→}
   180→
   181→/**
   182→ * Get quotas for a project
   183→ */
   184→async function getProjectQuotas(projectId: string): Promise<ProjectQuota[]> {
   185→  const queryText = `
   186→    SELECT
   187→      project_id,
   188→      cap_type,
   189→      cap_value
   190→    FROM control_plane.project_quotas
   191→    WHERE project_id = $1
   192→  `;
   193→
   194→  const result = await query(queryText, [projectId]);
   195→  return result.rows;
   196→}
   197→
   198→/**
   199→ * Get current usage for a project
   200→ *
   201→ * TODO: Implement actual usage tracking
   202→ * This should query from metrics/usage tables that track:
   203→ * - DB queries (from audit logs or dedicated metrics table)
   204→ * - Realtime connections (from realtime service)
   205→ * - Storage uploads (from storage service)
   206→ * - Function invocations (from function execution logs)
   207→ */
   208→async function getProjectUsage(projectId: string): Promise<ProjectUsage> {
   209→  // Mock implementation - in production, query actual usage metrics
   210→  const queryText = `
   211→    SELECT
   212→      $1 as project_id,
   213→      0 as db_queries_today,
   214→      0 as realtime_connections,
   215→      0 as storage_uploads_today,
   216→      0 as function_invocations_today
   217→  `;
   218→
   219→  const result = await query(queryText, [projectId]);
   220→  return result.rows[0] as ProjectUsage;
   221→}
   222→
   223→/**
   224→ * Map cap type to usage field
   225→ */
   226→function getUsageForCapType(usage: ProjectUsage, capType: HardCapType): number {
   227→  switch (capType) {
   228→    case HardCapType.DB_QUERIES_PER_DAY:
   229→      return usage.db_queries_today;
   230→    case HardCapType.REALTIME_CONNECTIONS:
   231→      return usage.realtime_connections;
   232→    case HardCapType.STORAGE_UPLOADS_PER_DAY:
   233→      return usage.storage_uploads_today;
   234→    case HardCapType.FUNCTION_INVOCATIONS_PER_DAY:
   235→      return usage.function_invocations_today;
   236→    default:
   237→      return 0;
   238→  }
   239→}
   240→
   241→/**
   242→ * Suspend a project for exceeding quota
   243→ */
   244→async function suspendProject(
   245→  projectId: string,
   246→  capType: HardCapType,
   247→  currentUsage: number,
   248→  quotaLimit: number
   249→): Promise<void> {
   250→  const queryText = `
   251→    UPDATE control_plane.projects
   252→    SET status = $1,
   253→        updated_at = NOW()
   254→    WHERE id = $2
   255→  `;
   256→
   257→  await query(queryText, [ProjectStatus.SUSPENDED, projectId]);
   258→
   259→  // TODO: Send notification to project owner
   260→  console.log(
   261→    `[CheckUsageLimits] Suspended project ${projectId} for exceeding ${capType}: ${currentUsage}/${quotaLimit}`
   262→  );
   263→}
   264→
   265→/**
   266→ * Send warning notification for approaching quota
   267→ */
   268→async function sendWarning(
   269→  projectId: string,
   270→  capType: HardCapType,
   271→  currentUsage: number,
   272→  quotaLimit: number,
   273→  _threshold: number
   274→): Promise<void> {
   275→  // TODO: Implement notification sending
   276→  const percentage = Math.round((currentUsage / quotaLimit) * 100);
   277→  console.log(
   278→    `[CheckUsageLimits] Warning for project ${projectId}: ${capType} at ${percentage}% (${currentUsage}/${quotaLimit})`
   279→  );
   280→}
   281→
   282→/**
   283→ * Check a single project against its quotas
   284→ */
   285→async function checkProjectQuotas(
   286→  projectId: string,
   287→  enforceLimits: boolean
   288→): Promise<QuotaCheckResult> {
   289→  const actions: QuotaAction[] = [];
   290→  let exceeded = false;
   291→
   292→  // Get project quotas
   293→  const quotas = await getProjectQuotas(projectId);
   294→
   295→  // Get current usage
   296→  const usage = await getProjectUsage(projectId);
   297→
   298→  // Check each quota
   299→  for (const quota of quotas) {
   300→    const currentUsage = getUsageForCapType(usage, quota.cap_type);
   301→    const usagePercentage = currentUsage / quota.cap_value;
   302→
   303→    // Check if hard cap exceeded
   304→    if (usagePercentage >= WARNING_THRESHOLDS.HARD_CAP) {
   305→      exceeded = true;
   306→      actions.push({
   307→        action_type: enforceLimits ? 'project_suspended' : 'no_action',
   308→        cap_type: quota.cap_type,
   309→        current_usage: currentUsage,
   310→        quota_limit: quota.cap_value,
   311→        usage_percentage: usagePercentage,
   312→        timestamp: new Date(),
   313→      });
   314→
   315→      if (enforceLimits) {
   316→        await suspendProject(
   317→          projectId,
   318→          quota.cap_type,
   319→          currentUsage,
   320→          quota.cap_value
   321→        );
   322→      }
   323→    }
   324→    // Check 90% warning threshold
   325→    else if (usagePercentage >= WARNING_THRESHOLDS.WARNING_90) {
   326→      actions.push({
   327→        action_type: 'warning_sent',
   328→        cap_type: quota.cap_type,
   329→        current_usage: currentUsage,
   330→        quota_limit: quota.cap_value,
   331→        usage_percentage: usagePercentage,
   332→        timestamp: new Date(),
   333→      });
   334→
   335→      await sendWarning(
   336→        projectId,
   337→        quota.cap_type,
   338→        currentUsage,
   339→        quota.cap_value,
   340→        WARNING_THRESHOLDS.WARNING_90
   341→      );
   342→    }
   343→    // Check 80% warning threshold
   344→    else if (usagePercentage >= WARNING_THRESHOLDS.WARNING_80) {
   345→      actions.push({
   346→        action_type: 'warning_sent',
   347→        cap_type: quota.cap_type,
   348→        current_usage: currentUsage,
   349→        quota_limit: quota.cap_value,
   350→        usage_percentage: usagePercentage,
   351→        timestamp: new Date(),
   352→      });
   353→
   354→      await sendWarning(
   355→        projectId,
   356→        quota.cap_type,
   357→        currentUsage,
   358→        quota.cap_value,
   359→        WARNING_THRESHOLDS.WARNING_80
   360→      );
   361→    }
   362→  }
   363→
   364→  return {
   365→    project_id: projectId,
   366→    exceeded,
   367→    actions,
   368→  };
   369→}
   370→
   371→/**
   372→ * Check Usage Limits Job Handler
   373→ *
   374→ * Checks projects against their configured quotas and takes action:
   375→ * - Suspends projects exceeding hard caps (100%)
   376→ * - Sends warnings at 90% threshold
   377→ * - Sends warnings at 80% threshold
   378→ *
   379→ * @param payload - Job payload with optional filters
   380→ * @returns Promise resolving to job execution result
   381→ */
   382→export async function checkUsageLimitsHandler(
   383→  payload: JobPayload
   384→): Promise<JobExecutionResult> {
   385→  const config = payload as CheckUsageLimitsPayload;
   386→
   387→  console.log('[CheckUsageLimits] Starting quota check run');
   388→
   389→  const startTime = Date.now();
   390→  const enforceLimits = config.enforce_limits !== false; // Default to true
   391→
   392→  try {
   393→    // Determine which projects to check
   394→    let projectIds: string[] = [];
   395→
   396→    if (config.project_ids && config.project_ids.length > 0) {
   397→      // Check specific projects
   398→      projectIds = config.project_ids;
   399→    } else if (config.check_all !== false) {
   400→      // Check all active projects (default)
   401→      const projects = await getActiveProjects();
   402→      projectIds = projects.map((p) => p.id);
   403→    }
   404→
   405→    console.log(`[CheckUsageLimits] Checking ${projectIds.length} projects`);
   406→
   407→    // Check each project
   408→    const details: QuotaCheckResult[] = [];
   409→    let projectsSuspended = 0;
   410→    let warningsSent = 0;
   411→
   412→    for (const projectId of projectIds) {
   413→      const result = await checkProjectQuotas(projectId, enforceLimits);
   414→      details.push(result);
   415→
   416→      // Count actions
   417→      for (const action of result.actions) {
   418→        if (action.action_type === 'project_suspended') {
   419→          projectsSuspended++;
   420→        } else if (action.action_type === 'warning_sent') {
   421→          warningsSent++;
   422→        }
   423→      }
   424→    }
   425→
   426→    const duration = Date.now() - startTime;
   427→
   428→    // Prepare result
   429→    const result: CheckUsageLimitsResult = {
   430→      projects_checked: projectIds.length,
   431→      projects_suspended: projectsSuspended,
   432→      warnings_sent: warningsSent,
   433→      details,
   434→      duration_ms: duration,
   435→    };
   436→
   437→    console.log(
   438→      `[CheckUsageLimits] Completed: ${projectIds.length} projects checked, ${projectsSuspended} suspended, ${warningsSent} warnings sent (${duration}ms)`
   439→    );
   440→
   441→    return {
   442→      success: true,
   443→      data: result,
   444→    };
   445→  } catch (error) {
   446→    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
   447→    console.error('[CheckUsageLimits] Failed:', errorMessage);
   448→
   449→    return {
   450→      success: false,
   451→      error: errorMessage,
   452→    };
   453→  }
   454→}
   455→
   456→/**
   457→ * Convenience function to enqueue a check_usage_limits job
   458→ *
   459→ * @param options - Check options (check all projects or specific ones)
   460→ * @returns Promise resolving to the job ID
   461→ *
   462→ * @example
   463→ * ```typescript
   464→ * import { enqueueCheckUsageLimitsJob } from '@/lib/jobs/handlers/check-usage-limits.handler';
   465→ *
   466→ * // Check all projects
   467→ * await enqueueCheckUsageLimitsJob({ checkAll: true });
   468→ *
   469→ * // Check specific project
   470→ * await enqueueCheckUsageLimitsJob({ projectIds: ['proj-123'] });
   471→ *
   472→ * // Dry run (don't actually suspend)
   473→ * await enqueueCheckUsageLimitsJob({ checkAll: true, enforceLimits: false });
   474→ * ```
   475→ */
   476→export async function enqueueCheckUsageLimitsJob(
   477→  options: {
   478→    checkAll?: boolean;
   479→    projectIds?: string[];
   480→    enforceLimits?: boolean;
   481→  } = {}
   482→): Promise<string> {
   483→  const payload: CheckUsageLimitsPayload = {
   484→    check_all: options.checkAll !== false,
   485→    project_ids: options.projectIds,
   486→    enforce_limits: options.enforceLimits,
   487→  };
   488→
   489→  const result = await enqueueJob('check_usage_limits', payload);
   490→
   491→  return result.id;
   492→}
