     1→/**
     2→ * Rotate Key Job Integration Test
     3→ *
     4→ * Integration test for the rotate_key job handler to verify:
     5→ * - Handler is properly registered with the worker
     6→ * - Job can be enqueued and processed
     7→ * - Database operations work correctly (expires_at is set)
     8→ * - End-to-end job execution flow
     9→ *
    10→ * US-005: Implement Rotate Key Job - Step 7: Data Layer & Integration
    11→ *
    12→ * Usage:
    13→ *   pnpm test src/lib/jobs/__tests__/rotate-key.integration.test.ts
    14→ */
    15→
    16→import { describe, it, expect, beforeAll, afterAll, beforeEach, afterEach } from '@jest/globals';
    17→import { query } from '@nextmavens/audit-logs-database';
    18→import { JobWorker } from '../worker.js';
    19→import { enqueueJob } from '../queue.js';
    20→import { rotateKeyHandler } from '../handlers/rotate-key.handler.js';
    21→import { JobStatus } from '@nextmavens/audit-logs-database';
    22→
    23→/**
    24→ * Test helper to create a test API key
    25→ */
    26→async function createTestApiKey(): Promise<number> {
    27→  const result = await query<{ id: number }>(
    28→    `
    29→    INSERT INTO control_plane.api_keys (
    30→      project_id,
    31→      key_type,
    32→      key_prefix,
    33→      key_hash,
    34→      scopes,
    35→      rate_limit
    36→    ) VALUES ($1, $2, $3, $4, $5, $6)
    37→    RETURNING id
    38→    `,
    39→    [
    40→      1, // project_id
    41→      'test',
    42→      'test_key',
    43→      'hash_' + Math.random().toString(36).substring(7),
    44→      ['read', 'write'],
    45→      1000,
    46→    ]
    47→  );
    48→
    49→  if (result.rows.length === 0) {
    50→    throw new Error('Failed to create test API key');
    51→  }
    52→
    53→  return result.rows[0].id;
    54→}
    55→
    56→/**
    57→ * Test helper to clean up test data
    58→ */
    59→async function cleanupTestData() {
    60→  await query(`
    61→    DELETE FROM control_plane.jobs
    62→    WHERE type = 'rotate_key'
    63→      AND payload->>'key_id' LIKE 'test-%'
    64→  `);
    65→
    66→  await query(`
    67→    DELETE FROM control_plane.api_keys
    68→    WHERE key_prefix LIKE 'test%'
    69→  `);
    70→}
    71→
    72→/**
    73→ * Test helper to get API key by ID
    74→ */
    75→async function getApiKey(keyId: number) {
    76→  const result = await query<{
    77→    id: number;
    78→    project_id: number;
    79→    key_type: string;
    80→    key_prefix: string;
    81→    scopes: string[];
    82→    rate_limit: number | null;
    83→    expires_at: Date | null;
    84→    created_at: Date;
    85→    updated_at: Date;
    86→  }>(
    87→    `
    88→    SELECT *
    89→    FROM control_plane.api_keys
    90→    WHERE id = $1
    91→    `,
    92→    [keyId]
    93→  );
    94→
    95→  return result.rows[0];
    96→}
    97→
    98→/**
    99→ * Test helper to get job by ID
   100→ */
   101→async function getJob(jobId: string) {
   102→  const result = await query<{
   103→    id: string;
   104→    type: string;
   105→    payload: Record<string, unknown>;
   106→    status: string;
   107→    attempts: number;
   108→    max_attempts: number;
   109→    last_error: string | null;
   110→    scheduled_at: Date;
   111→    started_at: Date | null;
   112→    completed_at: Date | null;
   113→    created_at: Date;
   114→  }>(
   115→    `
   116→    SELECT *
   117→    FROM control_plane.jobs
   118→    WHERE id = $1
   119→    `,
   120→    [jobId]
   121→  );
   122→
   123→  return result.rows[0];
   124→}
   125→
   126→describe('US-005: Rotate Key Job Integration Tests', () => {
   127→  let worker: JobWorker;
   128→
   129→  beforeAll(async () => {
   130→    // Create api_keys table for testing if it doesn't exist
   131→    await query(`
   132→      CREATE TABLE IF NOT EXISTS control_plane.api_keys (
   133→        id SERIAL PRIMARY KEY,
   134→        project_id INTEGER NOT NULL,
   135→        key_type TEXT NOT NULL DEFAULT 'api',
   136→        key_prefix TEXT NOT NULL,
   137→        key_hash TEXT NOT NULL,
   138→        scopes TEXT[] DEFAULT ARRAY['read']::TEXT[],
   139→        rate_limit INTEGER DEFAULT NULL,
   140→        expires_at TIMESTAMPTZ DEFAULT NULL,
   141→        created_at TIMESTAMPTZ NOT NULL DEFAULT NOW(),
   142→        updated_at TIMESTAMPTZ NOT NULL DEFAULT NOW()
   143→      )
   144→    `);
   145→
   146→    // Create indexes if they don't exist
   147→    await query(`
   148→      CREATE INDEX IF NOT EXISTS idx_api_keys_project_id
   149→      ON control_plane.api_keys(project_id)
   150→    `);
   151→
   152→    await query(`
   153→      CREATE INDEX IF NOT EXISTS idx_api_keys_expires_at
   154→      ON control_plane.api_keys(expires_at)
   155→    `);
   156→
   157→    // Create worker instance
   158→    worker = new JobWorker({
   159→      pollInterval: 100, // Fast polling for tests
   160→      maxConcurrentJobs: 1,
   161→      timeout: 5000,
   162→    });
   163→
   164→    // Register the rotate_key handler
   165→    worker.registerHandler('rotate_key', rotateKeyHandler);
   166→
   167→    // Start the worker
   168→    await worker.start();
   169→
   170→    // Give worker time to start
   171→    await new Promise((resolve) => setTimeout(resolve, 100));
   172→  });
   173→
   174→  afterAll(async () => {
   175→    // Stop the worker
   176→    await worker.stop();
   177→
   178→    // Clean up test data
   179→    await cleanupTestData();
   180→  });
   181→
   182→  beforeEach(async () => {
   183→    await cleanupTestData();
   184→  });
   185→
   186→  afterEach(async () => {
   187→    await cleanupTestData();
   188→  });
   189→
   190→  describe('AC1: rotate_key job handler is registered with the worker', () => {
   191→    it('should have rotate_key handler registered', () => {
   192→      const stats = worker.getStats();
   193→      expect(stats.registeredHandlers).toBeGreaterThan(0);
   194→      expect(stats.isRunning).toBe(true);
   195→    });
   196→  });
   197→
