    24→/**
    25→ * Test helper to clean up test jobs
    26→ */
    27→async function cleanupTestJobs() {
    28→  await query(`
    29→    DELETE FROM control_plane.jobs
    30→    WHERE type = 'test_job'
    31→      OR type = 'failing_job'
    32→      OR type = 'slow_job'
    33→  `);
    34→}
    35→
    36→/**
    37→ * Test helper to get job by ID
    38→ */
    39→async function getJob(jobId: string) {
    40→  const result = await query<{
    41→    id: string;
    42→    type: string;
    43→    payload: Record<string, unknown>;
    44→    status: string;
    45→    attempts: number;
    46→    max_attempts: number;
    47→    last_error: string | null;
    48→    scheduled_at: Date;
    49→    started_at: Date | null;
    50→    completed_at: Date | null;
    51→    created_at: Date;
    52→  }>(
    53→    `
    54→    SELECT *
    55→    FROM control_plane.jobs
    56→    WHERE id = $1
    57→    `,
    58→    [jobId]
    59→  );
    60→
    61→  return result.rows[0];
    62→}
    63→
    64→/**
    65→ * Test helper to wait for job completion
    66→ */
    67→async function waitForJob(jobId: string, maxWait = 5000): Promise<void> {
    68→  const startTime = Date.now();
    69→  while (Date.now() - startTime < maxWait) {
    70→    const job = await getJob(jobId);
    71→    if (
    72→      job &&
    73→      (job.status === JobStatus.COMPLETED ||
    74→        job.status === JobStatus.FAILED ||
    75→        job.status === JobStatus.PENDING)
    76→    ) {
    77→      // If job is pending but scheduled in the past, it might be picked up soon
    78→      if (job.status === JobStatus.PENDING && job.scheduled_at <= new Date()) {
    79→        await new Promise((resolve) => setTimeout(resolve, 100));
    80→        continue;
    81→      }
    82→      return;
    83→    }
    84→    await new Promise((resolve) => setTimeout(resolve, 100));
    85→  }
    86→  throw new Error(`Job ${jobId} did not complete within ${maxWait}ms`);
    87→}
