    33→/**
    34→ * Test helper to verify index usage
    35→ */
    36→async function getIndexUsage(): Promise<{
    37→  status_index_exists: boolean;
    38→  scheduled_at_index_exists: boolean;
    39→  composite_index_exists: boolean;
    40→}> {
    41→  const result = await query(`
    42→    SELECT
    43→      indexname,
    44→      tablename
    45→    FROM pg_indexes
    46→    WHERE schemaname = 'control_plane'
    47→      AND tablename = 'jobs'
    48→      AND (
    49→        indexname = 'idx_jobs_status'
    50→        OR indexname = 'idx_jobs_scheduled_at'
    51→        OR indexname = 'idx_jobs_status_scheduled_at'
    52→      )
    53→  `);
    54→
    55→  const indexes = {
    56→    status_index_exists: false,
    57→    scheduled_at_index_exists: false,
    58→    composite_index_exists: false,
    59→  };
    60→
    61→  for (const row of result.rows) {
    62→    if (row.indexname === 'idx_jobs_status') {
    63→      indexes.status_index_exists = true;
    64→    }
    65→    if (row.indexname === 'idx_jobs_scheduled_at') {
    66→      indexes.scheduled_at_index_exists = true;
    67→    }
    68→    if (row.indexname === 'idx_jobs_status_scheduled_at') {
    69→      indexes.composite_index_exists = true;
    70→    }
    71→  }
    72→
    73→  return indexes;
    74→}
    75→
    76→/**
    77→ * Test helper to check if a query uses an index
    78→ * Uses EXPLAIN ANALYZE to verify index usage
    79→ */
    80→async function verifyIndexUsage(
    81→  queryText: string,
    82→  values: unknown[] = []
    83→): Promise<{ uses_index: boolean; plan: string }> {
    84→  const explainQuery = `EXPLAIN ${queryText}`;
    85→  const result = await query(explainQuery, values);
    86→  const plan = result.rows.map((row) => JSON.stringify(row)).join('\n');
    87→
    88→  // Check if the plan mentions index scans
    89→  const usesIndex =
    90→    plan.includes('Index Scan') ||
    91→    plan.includes('Bitmap Index Scan') ||
    92→    plan.includes('idx_jobs');
    93→
    94→  return { uses_index: usesIndex, plan };
    95→}
