     1→/**
     2→ * Job Worker System
     3→ *
     4→ * Processes background jobs from the queue with support for:
     5→ * - Polling for pending jobs
     6→ * - Executing job handlers
     7→ * - Retry with exponential backoff
     8→ * - Graceful shutdown
     9→ *
    10→ * US-003: Create Job Worker
    11→ *
    12→ * @example
    13→ * ```typescript
    14→ * import { JobWorker } from '@/lib/jobs/worker';
    15→ * import { JobType } from '@nextmavens/audit-logs-database';
    16→ *
    17→ * // Create worker with custom options
    18→ * const worker = new JobWorker({
    19→ *   pollInterval: 5000,
    20→ *   maxConcurrentJobs: 5,
    21→ *   timeout: 30000
    22→ * });
    23→ *
    24→ * // Register handlers
    25→ * worker.registerHandler(JobType.PROVISION_PROJECT, async (payload) => {
    26→ *   // Handle provisioning logic
    27→ *   return { success: true, data: { projectId: payload.project_id } };
    28→ * });
    29→ *
    30→ * // Start processing jobs
    31→ * await worker.start();
    32→ *
    33→ * // Graceful shutdown
    34→ * process.on('SIGTERM', async () => {
    35→ *   await worker.stop();
    36→ * });
    37→ * ```
    38→ */
    39→
    40→import type {
    41→  Job,
    42→  JobHandler,
    43→  JobHandlerRegistry,
    44→  JobPayload,
    45→  WorkerOptions,
    46→} from '@nextmavens/audit-logs-database';
    47→import { JobStatus } from '@nextmavens/audit-logs-database';
    48→import { query } from '@nextmavens/audit-logs-database';
    49→
    50→/**
    51→ * Default worker configuration
    52→ */
    53→const DEFAULT_WORKER_OPTIONS: Required<WorkerOptions> = {
    54→  pollInterval: 5000, // 5 seconds
    55→  maxConcurrentJobs: 3,
    56→  timeout: 60000, // 1 minute
    57→};
    58→
    59→/**
    60→ * Retry configuration constants
    61→ */
    62→const RETRY_CONFIG = {
    63→  baseDelay: 1000, // 1 second
    64→  maxDelay: 60000, // 60 seconds
    65→  multiplier: 2,
    66→} as const;
    67→
    68→/**
    69→ * Calculate exponential backoff delay
    70→ *
    71→ * @param attempts - Number of attempts made so far
    72→ * @returns Delay in milliseconds
    73→ *
    74→ * @example
    75→ * ```typescript
    76→ * calculateBackoff(1); // 1000ms
    77→ * calculateBackoff(2); // 2000ms
    78→ * calculateBackoff(3); // 4000ms
    79→ * calculateBackoff(10); // 60000ms (capped)
    80→ * ```
    81→ */
    82→function calculateBackoff(attempts: number): number {
    83→  const delay = RETRY_CONFIG.baseDelay * Math.pow(RETRY_CONFIG.multiplier, attempts - 1);
    84→  return Math.min(delay, RETRY_CONFIG.maxDelay);
    85→}
    86→
    87→/**
    88→ * Job Worker Class
    89→ *
    90→ * Manages background job processing with:
    91→ * - Polling for pending jobs
    92→ * - Executing registered handlers
    93→ * - Retry logic with exponential backoff
    94→ * - Graceful shutdown
    95→ */
    96→export class JobWorker {
    97→  private handlers: JobHandlerRegistry = {};
    98→  private options: Required<WorkerOptions>;
    99→  private isRunning = false;
   100→  private pollTimer: NodeJS.Timeout | null = null;
   101→  private runningJobs = new Map<string, Promise<void>>();
   102→  private shutdownTimeout = 30000; // 30 seconds to complete running jobs
   103→
   104→  /**
   105→   * Create a new JobWorker instance
   106→   *
   107→   * @param options - Worker configuration options
   108→   */
   109→  constructor(options: WorkerOptions = {}) {
   110→    this.options = { ...DEFAULT_WORKER_OPTIONS, ...options };
   111→  }
   112→
   113→  /**
   114→   * Register a job handler for a specific job type
   115→   *
   116→   * @param jobType - The type of job this handler processes
   117→   * @param handler - The handler function to execute
   118→   *
   119→   * @example
   120→   * ```typescript
   121→   * worker.registerHandler('provision_project', async (payload) => {
   122→   *   const result = await provisionProject(payload.project_id);
   123→   *   return { success: true, data: result };
   124→   * });
   125→   * ```
   126→   */
   127→  registerHandler(jobType: string, handler: JobHandler): void {
   128→    this.handlers[jobType] = handler;
   129→  }
   130→
   131→  /**
   132→   * Register multiple job handlers at once
   133→   *
   134→   * @param handlers - Object mapping job types to handlers
   135→   *
   136→   * @example
   137→   * ```typescript
   138→   * worker.registerHandlers({
   139→   *   'provision_project': provisionHandler,
   140→   *   'rotate_key': rotateKeyHandler,
   141→   *   'deliver_webhook': webhookHandler
   142→   * });
   143→   * ```
   144→   */
   145→  registerHandlers(handlers: JobHandlerRegistry): void {
   146→    Object.assign(this.handlers, handlers);
   147→  }
   148→
   149→  /**
   150→   * Start polling for jobs
   151→   *
   152→   * Begins the polling loop and sets up signal handlers for graceful shutdown
   153→   */
   154→  async start(): Promise<void> {
   155→    if (this.isRunning) {
   156→      console.warn('[JobWorker] Worker is already running');
   157→      return;
   158→    }
   159→
   160→    this.isRunning = true;
   161→    console.log('[JobWorker] Starting job worker');
   162→
   163→    // Set up signal handlers for graceful shutdown
   164→    this.setupSignalHandlers();
   165→
   166→    // Start polling loop
   167→    await this.poll();
   168→  }
   169→
   170→  /**
   171→   * Stop polling for jobs and wait for running jobs to complete
   172→   *
   173→   * @param timeout - Maximum time to wait for jobs to complete (ms)
   174→   */
   175→  async stop(timeout?: number): Promise<void> {
   176→    if (!this.isRunning) {
   177→      console.warn('[JobWorker] Worker is not running');
   178→      return;
   179→    }
   180→
   181→    console.log('[JobWorker] Stopping job worker');
   182→    this.isRunning = false;
   183→
   184→    // Clear poll timer
   185→    if (this.pollTimer) {
   186→      clearTimeout(this.pollTimer);
   187→      this.pollTimer = null;
   188→    }
   189→
   190→    // Wait for running jobs to complete
   191→    const shutdownTimeoutMs = timeout ?? this.shutdownTimeout;
   192→    const startTime = Date.now();
   193→
   194→    while (this.runningJobs.size > 0) {
   195→      const elapsed = Date.now() - startTime;
   196→      if (elapsed >= shutdownTimeoutMs) {
   197→        console.warn(
   198→          `[JobWorker] Shutdown timeout exceeded, ${this.runningJobs.size} jobs still running`
   199→        );
   200→        break;
   201→      }
   202→
   203→      console.log(`[JobWorker] Waiting for ${this.runningJobs.size} jobs to complete`);
   204→      await new Promise((resolve) => setTimeout(resolve, 500));
   205→    }
   206→
   207→    console.log('[JobWorker] Worker stopped');
   208→  }
   209→
   210→  /**
   211→   * Set up signal handlers for graceful shutdown
   212→   */
   213→  private setupSignalHandlers(): void {
   214→    const shutdownHandler = async (signal: string) => {
   215→      console.log(`[JobWorker] Received ${signal}, initiating graceful shutdown`);
   216→      try {
   217→        await this.stop();
   218→        // Close database connections
   219→        const { closeDatabase } = await import('@nextmavens/audit-logs-database');
   220→        await closeDatabase();
   221→        process.exit(0);
   222→      } catch (error) {
   223→        console.error('[JobWorker] Error during shutdown:', error);
   224→        process.exit(1);
   225→      }
   226→    };
   227→
   228→    process.once('SIGTERM', () => shutdownHandler('SIGTERM'));
   229→    process.once('SIGINT', () => shutdownHandler('SIGINT'));
   230→  }
   231→
   232→  /**
   233→   * Poll for pending jobs
   234→   *
   235→   * Queries the database for jobs that are:
   236→   * - In PENDING status
   237→   * - Scheduled for now or in the past
   238→   * - Ordered by priority (DESC) and scheduled_at (ASC)
   239→   *
   240→   * Uses FOR UPDATE SKIP LOCKED to prevent multiple workers from processing the same job
   241→   */
   242→  private async poll(): Promise<void> {
   243→    if (!this.isRunning) {
   244→      return;
   245→    }
   246→
   247→    try {
   248→      // Check if we can process more jobs
   249→      if (this.runningJobs.size < this.options.maxConcurrentJobs) {
   250→        const jobs = await this.fetchPendingJobs();
   251→
   252→        for (const job of jobs) {
   253→          if (this.runningJobs.size >= this.options.maxConcurrentJobs) {
   254→            break;
   255→          }
   256→
   257→          // Process job asynchronously
   258→          const jobPromise = this.processJob(job);
   259→          this.runningJobs.set(job.id, jobPromise);
   260→
   261→          // Clean up completed jobs
   262→          jobPromise.finally(() => {
   263→            this.runningJobs.delete(job.id);
   264→          });
   265→        }
   266→      }
   267→    } catch (error) {
   268→      console.error('[JobWorker] Error during poll:', error);
   269→    }
   270→
   271→    // Schedule next poll
   272→    if (this.isRunning) {
   273→      this.pollTimer = setTimeout(() => {
   274→        this.poll();
   275→      }, this.options.pollInterval);
   276→    }
   277→  }
   278→
   279→  /**
   280→   * Fetch pending jobs from the database
   281→   *
   282→   * @returns Array of pending jobs
   283→   */
   284→  private async fetchPendingJobs(): Promise<Job[]> {
   285→    const queryText = `
   286→      SELECT *
   287→      FROM control_plane.jobs
   288→      WHERE status = $1
   289→        AND scheduled_at <= NOW()
   290→      ORDER BY
   291→        (payload->>'priority')::int DESC,
   292→        scheduled_at ASC
   293→      LIMIT $2
   294→      FOR UPDATE SKIP LOCKED
   295→    `;
   296→
   297→    const values = [
   298→      JobStatus.PENDING,
   299→      this.options.maxConcurrentJobs - this.runningJobs.size,
   300→    ];
   301→
   302→    try {
   303→      const result = await query(queryText, values);
   304→
   305→      // Define database row type
   306→      type DbJobRow = {
   307→        id: string;
   308→        type: string;
   309→        payload: Record<string, unknown>;
   310→        status: string;
   311→        attempts: number;
   312→        max_attempts: number;
   313→        last_error: string | null;
   314→        scheduled_at: Date;
   315→        started_at: Date | null;
   316→        completed_at: Date | null;
   317→        created_at: Date;
   318→      };
   319→
   320→      return result.rows.map((row: DbJobRow) => ({
   321→        id: row.id,
   322→        type: row.type,
   323→        payload: row.payload as JobPayload,
   324→        status: row.status as JobStatus,
   325→        attempts: row.attempts,
   326→        max_attempts: row.max_attempts,
   327→        last_error: row.last_error,
   328→        scheduled_at: row.scheduled_at,
   329→        started_at: row.started_at,
   330→        completed_at: row.completed_at,
   331→        created_at: row.created_at,
   332→      }));
   333→    } catch (error) {
   334→      console.error('[JobWorker] Error fetching pending jobs:', error);
   335→      return [];
   336→    }
   337→  }
   338→
   339→  /**
   340→   * Process a single job
   341→   *
   342→   * @param job - The job to process
   343→   */
   344→  private async processJob(job: Job): Promise<void> {
   345→    const startTime = Date.now();
   346→    console.log(`[JobWorker] Processing job ${job.id} (type: ${job.type})`);
   347→
   348→    try {
   349→      // Update job status to RUNNING
   350→      await this.updateJobStatus(job.id, JobStatus.RUNNING, null, new Date());
   351→
   352→      // Get handler for job type
   353→      const handler = this.handlers[job.type];
   354→      if (!handler) {
   355→        throw new Error(`No handler registered for job type: ${job.type}`);
   356→      }
   357→
   358→      // Execute handler with timeout
   359→      const result = await this.executeWithTimeout(handler, job.payload);
   360→
   361→      // Update job status to COMPLETED
   362→      await this.updateJobStatus(
   363→        job.id,
   364→        JobStatus.COMPLETED,
   365→        null,
   366→        null,
   367→        new Date(),
   368→        result.data
   369→      );
   370→
   371→      const duration = Date.now() - startTime;
   372→      console.log(`[JobWorker] Job ${job.id} completed in ${duration}ms`);
   373→    } catch (error) {
   374→      const duration = Date.now() - startTime;
   375→      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
   376→
   377→      console.error(`[JobWorker] Job ${job.id} failed after ${duration}ms:`, errorMessage);
   378→
   379→      // Determine if job should be retried
   380→      const shouldRetry = job.attempts < job.max_attempts;
   381→
   382→      if (shouldRetry) {
   383→        // Calculate retry delay with exponential backoff
   384→        const delay = calculateBackoff(job.attempts + 1);
   385→        const scheduledAt = new Date(Date.now() + delay);
   386→
   387→        console.log(
   388→          `[JobWorker] Retrying job ${job.id} in ${delay}ms (attempt ${job.attempts + 1}/${job.max_attempts})`
   389→        );
   390→
   391→        // Update job status to PENDING with new scheduled time
   392→        await this.updateJobStatus(
   393→          job.id,
   394→          JobStatus.PENDING,
   395→          errorMessage,
   396→          null,
   397→          null,
   398→          null,
   399→          scheduledAt
   400→        );
   401→      } else {
   402→        // Mark job as FAILED
   403→        console.log(`[JobWorker] Job ${job.id} failed permanently after ${job.max_attempts} attempts`);
   404→
   405→        await this.updateJobStatus(
   406→          job.id,
   407→          JobStatus.FAILED,
   408→          errorMessage,
   409→          null,
   410→          new Date()
   411→        );
   412→      }
   413→    }
   414→  }
   415→
   416→  /**
   417→   * Execute a handler with a timeout
   418→   *
   419→   * @param handler - The handler function to execute
   420→   * @param payload - The job payload
   421→   * @returns Promise resolving to the handler result
   422→   * @throws Error if timeout is exceeded
   423→   */
   424→  private async executeWithTimeout<T>(
   425→    handler: (payload: JobPayload) => Promise<T>,
   426→    payload: JobPayload
   427→  ): Promise<T> {
   428→    return new Promise((resolve, reject) => {
   429→      const timer = setTimeout(() => {
   430→        reject(new Error(`Job execution timeout after ${this.options.timeout}ms`));
   431→      }, this.options.timeout);
   432→
   433→      handler(payload)
   434→        .then(resolve)
   435→        .catch(reject)
   436→        .finally(() => {
   437→          clearTimeout(timer);
   438→        });
   439→    });
   440→  }
   441→
   442→  /**
   443→   * Update job status in the database
   444→   *
   445→   * @param jobId - The job ID
   446→   * @param status - The new status
   447→   * @param error - Error message (if any)
   448→   * @param startedAt - Job start timestamp (optional)
   449→   * @param completedAt - Job completion timestamp (optional)
   450→   * @param data - Additional result data (optional)
   451→   * @param scheduledAt - Rescheduled timestamp (optional)
   452→   */
   453→  private async updateJobStatus(
   454→    jobId: string,
   455→    status: JobStatus,
   456→    error: string | null = null,
   457→    startedAt: Date | null = null,
   458→    completedAt: Date | null = null,
   459→    data: Record<string, unknown> | null = null,
   460→    scheduledAt: Date | null = null
   461→  ): Promise<void> {
   462→    const queryText = `
   463→      UPDATE control_plane.jobs
   464→      SET
   465→        status = $1,
   466→        attempts = CASE
   467→          WHEN $2 IS NOT NULL THEN attempts + 1
   468→          ELSE attempts
   469→        END,
   470→        last_error = $2,
   471→        started_at = COALESCE($3, started_at),
   472→        completed_at = COALESCE($4, completed_at),
   473→        payload = CASE
   474→          WHEN $5 IS NOT NULL THEN payload || $5
   475→          ELSE payload
   476→        END,
   477→        scheduled_at = COALESCE($6, scheduled_at)
   478→      WHERE id = $7
   479→    `;
   480→
   481→    const values = [
   482→      status,
   483→      error,
   484→      startedAt,
   485→      completedAt,
   486→      data ? JSON.stringify(data) : null,
   487→      scheduledAt,
   488→      jobId,
   489→    ];
   490→
   491→    try {
   492→      await query(queryText, values);
   493→    } catch (error) {
   494→      console.error(`[JobWorker] Error updating job ${jobId} status:`, error);
   495→      throw error;
   496→    }
   497→  }
   498→
   499→  /**
   500→   * Get current worker statistics
   501→   *
   502→   * @returns Object with worker stats
   503→   */
   504→  getStats(): { isRunning: boolean; runningJobs: number; registeredHandlers: number } {
   505→    return {
   506→      isRunning: this.isRunning,
   507→      runningJobs: this.runningJobs.size,
   508→      registeredHandlers: Object.keys(this.handlers).length,
   509→    };
   510→  }
   511→}
   512→
   513→/**
   514→ * Global worker instance
   515→ * Provides a singleton for convenient worker management
   516→ */
   517→let globalWorker: JobWorker | null = null;
   518→
   519→/**
   520→ * Get or create the global job worker instance
   521→ *
   522→ * @param options - Worker configuration options (only used on first call)
   523→ * @returns The global JobWorker instance
   524→ *
   525→ * @example
   526→ * ```typescript
   527→ * import { getJobWorker } from '@/lib/jobs/worker';
   528→ *
   529→ * // Get or create worker
   530→ * const worker = getJobWorker({ pollInterval: 10000 });
   531→ *
   532→ * // Register handlers
   533→ * worker.registerHandler('provision_project', provisionHandler);
   534→ *
   535→ * // Start worker
   536→ * await worker.start();
   537→ * ```
   538→ */
   539→export function getJobWorker(options?: WorkerOptions): JobWorker {
   540→  if (!globalWorker) {
   541→    globalWorker = new JobWorker(options);
   542→  }
   543→  return globalWorker;
   544→}
   545→
   546→/**
   547→ * Start the global job worker
   548→ *
   549→ * @param options - Worker configuration options
   550→ * @returns Promise resolving when worker is started
   551→ *
   552→ * @example
   553→ * ```typescript
   554→ * import { startWorker } from '@/lib/jobs/worker';
   555→ *
   556→ * // Start worker with default options
   557→ * await startWorker();
   558→ *
   559→ * // Start worker with custom options
   560→ * await startWorker({ pollInterval: 3000, maxConcurrentJobs: 5 });
   561→ * ```
   562→ */
   563→export async function startWorker(options?: WorkerOptions): Promise<void> {
   564→  const worker = getJobWorker(options);
   565→  await worker.start();
   566→}
   567→
   568→/**
   569→ * Stop the global job worker
   570→ *
   571→ * @param timeout - Maximum time to wait for jobs to complete
   572→ * @returns Promise resolving when worker is stopped
   573→ *
   574→ * @example
   575→ * ```typescript
   576→ * import { stopWorker } from '@/lib/jobs/worker';
   577→ *
   578→ * await stopWorker();
   579→ * ```
   580→ */
   581→export async function stopWorker(timeout?: number): Promise<void> {
   582→  if (globalWorker) {
   583→    await globalWorker.stop(timeout);
   584→  }
   585→}
