     1→/**
     2→ * Job Queue System
     3→ *
     4→ * Provides a centralized job queue for enqueueing background jobs.
     5→ * Jobs are stored in the control_plane.jobs table and processed by workers.
     6→ *
     7→ * US-002: Create Job Queue System
     8→ *
     9→ * @example
    10→ * ```typescript
    11→ * import { enqueueJob } from '@/lib/jobs/queue';
    12→ *
    13→ * // Enqueue a job with default options
    14→ * const jobId = await enqueueJob('provision_project', { project_id: 'proj-123' });
    15→ *
    16→ * // Enqueue a job with scheduling and retry options
    17→ * const scheduledJobId = await enqueueJob('rotate_key', { key_id: 'key-456' }, {
    18→ *   delay: 60000, // 1 minute delay
    19→ *   maxAttempts: 5,
    20→ *   priority: 10
    21→ * });
    22→ * ```
    23→ */
    24→
    25→import { v4 as uuidv4 } from 'uuid';
    26→import type {
    27→  JobType,
    28→  JobPayload,
    29→} from '@nextmavens/audit-logs-database';
    30→import { JobStatus } from '@nextmavens/audit-logs-database';
    31→import { query } from '@nextmavens/audit-logs-database';
    32→
    33→/**
    34→ * Job enqueuement options
    35→ * Provides fine-grained control over job scheduling and retry behavior
    36→ */
    37→export interface EnqueueJobOptions {
    38→  /**
    39→   * Delay before job execution (in milliseconds)
    40→   * If specified, the job will be scheduled for the future
    41→   * @default 0 (execute immediately)
    42→   */
    43→  delay?: number;
    44→
    45→  /**
    46→   * Maximum number of execution attempts
    47→   * Includes the initial attempt plus retries
    48→   * @default 3
    49→   */
    50→  maxAttempts?: number;
    51→
    52→  /**
    53→   * Job priority level
    54→   * Higher values indicate higher priority
    55→   * Workers should process higher priority jobs first
    56→   * @default 0
    57→   */
    58→  priority?: number;
    59→
    60→  /**
    61→   * Custom scheduled timestamp
    62→   * If provided, takes precedence over delay
    63→   * Useful for specific scheduling requirements
    64→   */
    65→  scheduledAt?: Date;
    66→}
    67→
    68→/**
    69→ * Validation constants
    70→ */
    71→const VALIDATION = {
    72→  /** Maximum allowed maxAttempts value */
    73→  MAX_MAX_ATTEMPTS: 100,
    74→  /** Minimum allowed maxAttempts value */
    75→  MIN_MAX_ATTEMPTS: 1,
    76→  /** Maximum allowed delay in milliseconds (24 hours) */
    77→  MAX_DELAY: 24 * 60 * 60 * 1000,
    78→  /** Minimum allowed delay (0 = immediate) */
    79→  MIN_DELAY: 0,
    80→  /** Maximum allowed priority value */
    81→  MAX_PRIORITY: 1000,
    82→  /** Minimum allowed priority value */
    83→  MIN_PRIORITY: 0,
    84→} as const;
    85→
    86→/**
    87→ * Validate job type string
    88→ */
    89→function validateJobType(type: string): void {
    90→  if (typeof type !== 'string') {
    91→    throw new Error('Job type must be a string');
    92→  }
    93→  if (type.trim().length === 0) {
    94→    throw new Error('Job type cannot be empty');
    95→  }
    96→  if (type.length > 100) {
    97→    throw new Error('Job type cannot exceed 100 characters');
    98→  }
    99→  // Allow only alphanumeric, underscore, and hyphen
   100→  if (!/^[a-zA-Z0-9_-]+$/.test(type)) {
   101→    throw new Error('Job type can only contain alphanumeric characters, underscores, and hyphens');
   102→  }
   103→}
   104→
   105→/**
   106→ * Validate job payload
   107→ */
   108→function validateJobPayload(payload: JobPayload): void {
   109→  if (typeof payload !== 'object' || payload === null) {
   110→    throw new Error('Job payload must be an object');
   111→  }
   112→  // Check payload size when serialized (max 1MB)
   113→  const serialized = JSON.stringify(payload);
   114→  if (serialized.length > 1024 * 1024) {
   115→    throw new Error('Job payload size cannot exceed 1MB');
   116→  }
   117→}
   118→
   119→/**
   120→ * Validate maxAttempts value
   121→ */
   122→function validateMaxAttempts(maxAttempts: number): void {
   123→  if (!Number.isInteger(maxAttempts)) {
   124→    throw new Error('maxAttempts must be an integer');
   125→  }
   126→  if (maxAttempts < VALIDATION.MIN_MAX_ATTEMPTS) {
   127→    throw new Error(`maxAttempts must be at least ${VALIDATION.MIN_MAX_ATTEMPTS}`);
   128→  }
   129→  if (maxAttempts > VALIDATION.MAX_MAX_ATTEMPTS) {
   130→    throw new Error(`maxAttempts cannot exceed ${VALIDATION.MAX_MAX_ATTEMPTS}`);
   131→  }
   132→}
   133→
   134→/**
   135→ * Validate delay value
   136→ */
   137→function validateDelay(delay: number): void {
   138→  if (!Number.isInteger(delay)) {
   139→    throw new Error('delay must be an integer');
   140→  }
   141→  if (delay < VALIDATION.MIN_DELAY) {
   142→    throw new Error(`delay must be at least ${VALIDATION.MIN_DELAY}`);
   143→  }
   144→  if (delay > VALIDATION.MAX_DELAY) {
   145→    throw new Error(`delay cannot exceed ${VALIDATION.MAX_DELAY} milliseconds (24 hours)`);
   146→  }
   147→}
   148→
   149→/**
   150→ * Validate priority value
   151→ */
   152→function validatePriority(priority: number): void {
   153→  if (!Number.isInteger(priority)) {
   154→    throw new Error('priority must be an integer');
   155→  }
   156→  if (priority < VALIDATION.MIN_PRIORITY) {
   157→    throw new Error(`priority must be at least ${VALIDATION.MIN_PRIORITY}`);
   158→  }
   159→  if (priority > VALIDATION.MAX_PRIORITY) {
   160→    throw new Error(`priority cannot exceed ${VALIDATION.MAX_PRIORITY}`);
   161→  }
   162→}
   163→
   164→/**
   165→ * Validate scheduled date
   166→ */
   167→function validateScheduledDate(date: Date): void {
   168→  if (!(date instanceof Date) || isNaN(date.getTime())) {
   169→    throw new Error('Scheduled date must be a valid Date object');
   170→  }
   171→  // Don't allow scheduling too far in the future (max 1 year)
   172→  const maxFuture = new Date();
   173→  maxFuture.setFullYear(maxFuture.getFullYear() + 1);
   174→  if (date > maxFuture) {
   175→    throw new Error('Cannot schedule jobs more than 1 year in the future');
   176→  }
   177→}
   178→
   179→/**
   180→ * Result of job enqueuement
   181→ */
   182→export interface EnqueueJobResult {
   183→  /**
   184→   * Unique identifier for the enqueued job
   185→   */
   186→  id: string;
   187→
   188→  /**
   189→   * Type of the job
   190→   */
   191→  type: string;
   192→
   193→  /**
   194→   * Current job status
   195→   */
   196→  status: JobStatus;
   197→
   198→  /**
   199→   * Timestamp when job is scheduled to run
   200→   */
   201→  scheduledAt: Date;
   202→
   203→  /**
   204→   * Timestamp when job was created
   205→   */
   206→  createdAt: Date;
   207→}
   208→
   209→/**
   210→ * Job Queue Class
   211→ *
   212→ * Manages job enqueuement with support for:
   213→ * - Delayed scheduling
   214→ * - Retry configuration
   215→ * - Priority handling
   216→ * - Type-safe payloads
   217→ */
   218→export class JobQueue {
   219→  /**
   220→   * Enqueue a new job for background processing
   221→   *
   222→   * @param type - The type of job to enqueue (e.g., 'provision_project', 'rotate_key')
   223→   * @param payload - Job-specific data (optional)
   224→   * @param options - Job enqueuement options (delay, maxAttempts, priority)
   225→   * @returns Promise resolving to the job ID and metadata
   226→   *
   227→   * @throws Error if database insertion fails
   228→   *
   229→   * @example
   230→   * ```typescript
   231→   * const queue = new JobQueue();
   232→   *
   233→   * // Simple job
   234→   * const result = await queue.enqueue('provision_project', { project_id: '123' });
   235→   * console.log(`Job ${result.id} enqueued for ${result.scheduledAt}`);
   236→   *
   237→   * // Delayed job with custom retry
   238→   * const result2 = await queue.enqueue(
   239→   *   'rotate_key',
   240→   *   { key_id: '456' },
   241→   *   { delay: 60000, maxAttempts: 5, priority: 10 }
   242→   * );
   243→   * ```
   244→   */
   245→  async enqueue(
   246→    type: JobType | string,
   247→    payload: JobPayload = {},
   248→    options: EnqueueJobOptions = {}
   249→  ): Promise<EnqueueJobResult> {
   250→    // Validate inputs
   251→    validateJobType(type);
   252→    validateJobPayload(payload);
   253→
   254→    // Calculate scheduled timestamp
   255→    const scheduledAt = options.scheduledAt || new Date(Date.now() + (options.delay || 0));
   256→
   257→    // Validate scheduled date
   258→    if (options.scheduledAt) {
   259→      validateScheduledDate(options.scheduledAt);
   260→    }
   261→
   262→    // Set defaults and validate
   263→    const maxAttempts = options.maxAttempts ?? 3;
   264→    const priority = options.priority ?? 0;
   265→
   266→    validateMaxAttempts(maxAttempts);
   267→    validatePriority(priority);
   268→
   269→    if (options.delay !== undefined) {
   270→      validateDelay(options.delay);
   271→    }
   272→
   273→    // Generate unique job ID
   274→    const id = uuidv4();
   275→
   276→    // Prepare payload with priority
   277→    const enrichedPayload: JobPayload = {
   278→      ...payload,
   279→      priority,
   280→    };
   281→
   282→    // Insert job into database
   283→    const queryText = `
   284→      INSERT INTO control_plane.jobs (
   285→        id,
   286→        type,
   287→        payload,
   288→        status,
   289→        attempts,
   290→        max_attempts,
   291→        scheduled_at,
   292→        created_at
   293→      )
   294→      VALUES ($1, $2, $3, $4, $5, $6, $7, NOW())
   295→      RETURNING id, type, status, scheduled_at, created_at
   296→    `;
   297→
   298→    const values = [
   299→      id,
   300→      type,
   301→      JSON.stringify(enrichedPayload),
   302→      JobStatus.PENDING,
   303→      0,
   304→      maxAttempts,
   305→      scheduledAt,
   306→    ];
   307→
   308→    try {
   309→      const result = await query(queryText, values);
   310→
   311→      if (result.rows.length === 0) {
   312→        throw new Error('Failed to create job');
   313→      }
   314→
   315→      const row = result.rows[0] as {
   316→        id: string;
   317→        type: string;
   318→        status: string;
   319→        scheduled_at: Date;
   320→        created_at: Date;
   321→      };
   322→
   323→      return {
   324→        id: row.id,
   325→        type: row.type,
   326→        status: row.status as JobStatus,
   327→        scheduledAt: row.scheduled_at,
   328→        createdAt: row.created_at,
   329→      };
   330→    } catch (error) {
   331→      // Don't expose internal error messages
   332→      if (error instanceof Error && error.message.startsWith('Failed to create job')) {
   333→        throw error;
   334→      }
   335→      throw new Error('Failed to enqueue job');
   336→    }
   337→  }
   338→
   339→  /**
   340→   * Batch enqueue multiple jobs
   341→   *
   342→   * @param jobs - Array of jobs to enqueue
   343→   * @returns Promise resolving to array of job IDs
   344→   *
   345→   * @example
   346→   * ```typescript
   347→   * const queue = new JobQueue();
   348→   * const results = await queue.enqueueBatch([
   349→   *   { type: 'provision_project', payload: { project_id: '1' } },
   350→   *   { type: 'provision_project', payload: { project_id: '2' } },
   351→   *   { type: 'rotate_key', payload: { key_id: '3' }, options: { delay: 5000 } }
   352→   * ]);
   353→   * ```
   354→   */
   355→  async enqueueBatch(
   356→    jobs: Array<{
   357→      type: JobType | string;
   358→      payload?: JobPayload;
   359→      options?: EnqueueJobOptions;
   360→    }>
   361→  ): Promise<EnqueueJobResult[]> {
   362→    const results: EnqueueJobResult[] = [];
   363→
   364→    for (const job of jobs) {
   365→      const result = await this.enqueue(
   366→        job.type,
   367→        job.payload || {},
   368→        job.options || {}
   369→      );
   370→      results.push(result);
   371→    }
   372→
   373→    return results;
   374→  }
   375→
   376→  /**
   377→   * Schedule a job for a specific future time
   378→   *
   379→   * @param type - The type of job to schedule
   380→   * @param payload - Job-specific data
   381→   * @param scheduledAt - When to execute the job
   382→   * @param options - Additional job options (maxAttempts, priority)
   383→   * @returns Promise resolving to the job ID and metadata
   384→   *
   385→   * @example
   386→   * ```typescript
   387→   * const queue = new JobQueue();
   388→   *
   389→   * // Schedule job for specific date
   390→   * const result = await queue.schedule(
   391→   *   'export_backup',
   392→   *   { project_id: '123' },
   393→   *   new Date('2026-01-30T02:00:00Z'),
   394→   *   { priority: 5 }
   395→   * );
   396→   * ```
   397→   */
   398→  async schedule(
   399→    type: JobType | string,
   400→    payload: JobPayload,
   401→    scheduledAt: Date,
   402→    options: Omit<EnqueueJobOptions, 'delay' | 'scheduledAt'> = {}
   403→  ): Promise<EnqueueJobResult> {
   404→    return this.enqueue(type, payload, {
   405→      ...options,
   406→      scheduledAt,
   407→    });
   408→  }
   409→}
   410→
   411→/**
   412→ * Global job queue instance
   413→ * Provides a singleton for convenient job enqueuement
   414→ */
   415→const jobQueue = new JobQueue();
   416→
   417→/**
   418→ * Enqueue a job for background processing
   419→ *
   420→ * This is the primary function for adding jobs to the queue.
   421→ * It uses a singleton JobQueue instance for efficiency.
   422→ *
   423→ * @param type - The type of job to enqueue
   424→ * @param payload - Job-specific data (optional)
   425→ * @param options - Job enqueuement options (optional)
   426→ * @returns Promise resolving to the job ID and metadata
   427→ *
   428→ * @example
   429→ * ```typescript
   430→ * // Simple job
   431→ * const result = await enqueueJob('provision_project', { project_id: '123' });
   432→ * console.log(`Enqueued job: ${result.id}`);
   433→ *
   434→ * // Delayed job with retries
   435→ * const result2 = await enqueueJob(
   436→ *   'rotate_key',
   437→ *   { key_id: '456' },
   438→ *   { delay: 60000, maxAttempts: 5 }
   439→ * );
   440→ * ```
   441→ */
   442→export async function enqueueJob(
   443→  type: JobType | string,
   444→  payload: JobPayload = {},
   445→  options: EnqueueJobOptions = {}
   446→): Promise<EnqueueJobResult> {
   447→  return jobQueue.enqueue(type, payload, options);
   448→}
   449→
   450→/**
   451→ * Enqueue multiple jobs in a batch
   452→ *
   453→ * @param jobs - Array of jobs to enqueue
   454→ * @returns Promise resolving to array of job results
   455→ */
   456→export async function enqueueJobBatch(
   457→  jobs: Array<{
   458→    type: JobType | string;
   459→    payload?: JobPayload;
   460→    options?: EnqueueJobOptions;
   461→  }>
   462→): Promise<EnqueueJobResult[]> {
   463→  return jobQueue.enqueueBatch(jobs);
   464→}
   465→
   466→/**
   467→ * Schedule a job for a specific time
   468→ *
   469→ * @param type - The type of job to schedule
   470→ * @param payload - Job-specific data
   471→ * @param scheduledAt - When to execute the job
   472→ * @param options - Additional job options
   473→ * @returns Promise resolving to the job result
   474→ */
   475→export async function scheduleJob(
   476→  type: JobType | string,
   477→  payload: JobPayload,
   478→  scheduledAt: Date,
   479→  options: Omit<EnqueueJobOptions, 'delay' | 'scheduledAt'> = {}
   480→): Promise<EnqueueJobResult> {
   481→  return jobQueue.schedule(type, payload, scheduledAt, options);
   482→}
