     1→/**
     2→ * Provision Project Job Handler
     3→ *
     4→ * Background job handler for provisioning new tenant projects.
     5→ * Handles database creation, schema setup, service registration, and API key generation.
     6→ *
     7→ * US-004: Implement Provision Project Job - Step 1: Foundation
     8→ *
     9→ * Features:
    10→ * - Creates tenant database with isolation
    11→ * - Creates tenant schema with proper permissions
    12→ * - Registers project with auth, realtime, and storage services
    13→ * - Generates API keys for project access
    14→ * - Implements retry logic with 5-minute intervals
    15→ * - Maximum 3 retry attempts
    16→ * - Detailed error reporting and progress tracking
    17→ */
    18→
    19→import type { JobHandler, JobExecutionResult, JobPayload } from '../../types/jobs.types.js';
    20→import type {
    21→  ProvisionProjectPayload,
    22→  ProvisionProjectResult,
    23→} from './types.js';
    24→import { ProvisionProjectStage } from './types.js';
    25→import {
    26→  verifyProjectExists as verifyProject,
    27→  validateProvisioningParams,
    28→  updateProjectProvisioningStatus,
    29→  isRegionAvailable,
    30→} from './provision-project/index.js';
    31→import {
    32→  createTenantDatabase,
    33→  createTenantSchema,
    34→} from './provision-project/index.js';
    35→import {
    36→  registerAuthService,
    37→  registerRealtimeService,
    38→  registerStorageService,
    39→} from './provision-project/index.js';
    40→import { generateApiKeys as generateProjectApiKeys } from './provision-project/index.js';
    41→
    42→/**
    43→ * Provision project job handler
    44→ *
    45→ * This handler executes the project provisioning process:
    46→ * 1. Validates the project exists and is eligible for provisioning
    47→ * 2. Creates a dedicated tenant database
    48→ * 3. Creates the tenant schema with proper permissions
    49→ * 4. Registers with auth service (if enabled)
    50→ * 5. Registers with realtime service (if enabled)
    51→ * 6. Registers with storage service (if enabled)
    52→ * 7. Generates initial API keys
    53→ * 8. Returns the provisioned infrastructure details
    54→ *
    55→ * Retry configuration:
    56→ * - Max attempts: 3
    57→ * - Retry interval: 5 minutes (300 seconds)
    58→ * - Exponential backoff for retries
    59→ *
    60→ * @param payload - Job payload containing project provisioning parameters
    61→ * @returns Job execution result with provisioned infrastructure details
    62→ */
    63→export const provisionProjectHandler: JobHandler = async (
    64→  payload: JobPayload
    65→): Promise<JobExecutionResult> => {
    66→  try {
    67→    // Validate and parse payload
    68→    const params = validatePayload(payload);
    69→
    70→    console.log(`[ProvisionProject] Starting provisioning for project: ${params.project_id}`);
    71→
    72→    // Validate region availability
    73→    if (!isRegionAvailable(params.region)) {
    74→      throw new Error(`Region not available: ${params.region}`);
    75→    }
    76→
    77→    // Initialize result tracking
    78→    const result: Partial<ProvisionProjectResult> & { services: Record<string, unknown> } = {
    79→      project_id: params.project_id,
    80→      services: {},
    81→      api_keys: [],
    82→      metadata: {
    83→        provisioned_at: new Date(),
    84→        region: params.region,
    85→        owner_id: params.owner_id,
    86→        organization_id: params.organization_id,
    87→      },
    88→    };
    89→
    90→    // Step 1: Verify project exists
    91→    await verifyProject(params.project_id);
    92→    await updateStage(params.project_id, ProvisionProjectStage.INITIALIZING);
    93→    await updateProjectProvisioningStatus(params.project_id, 'in_progress');
    94→
    95→    // Step 2: Create tenant database
    96→    const databaseDetails = await createTenantDatabase(params.project_id, params.region);
    97→    result.database = databaseDetails;
    98→    await updateStage(params.project_id, ProvisionProjectStage.CREATING_DATABASE);
    99→
   100→    // Step 3: Create tenant schema
   101→    await createTenantSchema(databaseDetails.database_name, params.project_id);
   102→    await updateStage(params.project_id, ProvisionProjectStage.CREATING_SCHEMA);
   103→
   104→    // Step 4: Register with auth service (if enabled)
   105→    if (params.services?.auth) {
   106→      result.services.auth = await registerAuthService(params);
   107→      await updateStage(params.project_id, ProvisionProjectStage.REGISTERING_AUTH);
   108→    }
   109→
   110→    // Step 5: Register with realtime service (if enabled)
   111→    if (params.services?.realtime) {
   112→      result.services.realtime = await registerRealtimeService(params);
   113→      await updateStage(params.project_id, ProvisionProjectStage.REGISTERING_REALTIME);
   114→    }
   115→
   116→    // Step 6: Register with storage service (if enabled)
   117→    if (params.services?.storage) {
   118→      result.services.storage = await registerStorageService(params);
   119→      await updateStage(params.project_id, ProvisionProjectStage.REGISTERING_STORAGE);
   120→    }
   121→
   122→    // Step 7: Generate API keys
   123→    const apiKeys = await generateProjectApiKeys(params);
   124→    result.api_keys = apiKeys.map((key) => ({
   125→      key_id: key.key_id,
   126→      key_prefix: key.key_prefix,
   127→      created_at: key.created_at,
   128→    }));
   129→    await updateStage(params.project_id, ProvisionProjectStage.GENERATING_API_KEYS);
   130→
   131→    // Step 8: Finalize
   132→    await updateStage(params.project_id, ProvisionProjectStage.FINALIZING);
   133→    await updateStage(params.project_id, ProvisionProjectStage.COMPLETED);
   134→    await updateProjectProvisioningStatus(params.project_id, 'completed');
   135→
   136→    console.log(`[ProvisionProject] Successfully provisioned project: ${params.project_id}`);
   137→
   138→    return {
   139→      success: true,
   140→      data: result as unknown as Record<string, unknown>,
   141→    };
   142→  } catch (error) {
   143→    const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
   144→
   145→    console.error(`[ProvisionProject] Failed to provision project:`, error);
   146→
   147→    // Try to update status to failed
   148→    try {
   149→      const params = payload as unknown as ProvisionProjectPayload;
   150→      if (params.project_id) {
   151→        await updateProjectProvisioningStatus(params.project_id, 'failed');
   152→      }
   153→    } catch {
   154→      // Ignore error during status update
   155→    }
   156→
   157→    return {
   158→      success: false,
   159→      error: errorMessage,
   160→    };
   161→  }
   162→};
   163→
   164→/**
   165→ * Validate and parse job payload
   166→ */
   167→function validatePayload(payload: JobPayload): ProvisionProjectPayload {
   168→  const params = payload as unknown as ProvisionProjectPayload;
   169→
   170→  // Validate required fields
   171→  if (!params.project_id || typeof params.project_id !== 'string') {
   172→    throw new Error('Invalid or missing project_id in payload');
   173→  }
   174→
   175→  if (!params.region || typeof params.region !== 'string') {
   176→    throw new Error('Invalid or missing region in payload');
   177→  }
   178→
   179→  // Validate optional fields for security
   180→  if (params.owner_id && typeof params.owner_id !== 'string') {
   181→    throw new Error('Invalid owner_id in payload');
   182→  }
   183→
   184→  if (params.organization_id && typeof params.organization_id !== 'string') {
   185→    throw new Error('Invalid organization_id in payload');
   186→  }
   187→
   188→  // Validate UUID format for optional fields if provided
   189→  const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
   190→  if (params.owner_id && !uuidRegex.test(params.owner_id)) {
   191→    throw new Error('Invalid owner_id format (must be UUID)');
   192→  }
   193→
   194→  if (params.organization_id && !uuidRegex.test(params.organization_id)) {
   195→    throw new Error('Invalid organization_id format (must be UUID)');
   196→  }
   197→
   198→  // Use validation module for detailed validation
   199→  validateProvisioningParams({
   200→    project_id: params.project_id,
   201→    region: params.region,
   202→  });
   203→
   204→  return params;
   205→}
