     1→/**
     2→ * Provision Project Job Handler
     3→ *
     4→ * Handles project provisioning by:
     5→ * 1. Creating tenant database
     6→ * 2. Creating tenant schema
     7→ * 3. Registering with services (auth, realtime, storage)
     8→ * 4. Generating API keys
     9→ *
    10→ * This job supports retry logic with:
    11→ * - Retry interval: 5 minutes (300 seconds)
    12→ * - Max attempts: 3
    13→ * - Exponential backoff for retries
    14→ *
    15→ * US-004: Implement Provision Project Job - Step 10: Security & Error Handling
    16→ *
    17→ * @example
    18→ * ```typescript
    19→ * import { enqueueJob } from '@/lib/jobs/queue';
    20→ * import { provisionProjectHandler } from '@/lib/jobs/handlers/provision-project.handler';
    21→ *
    22→ * // Register the handler
    23→ * worker.registerHandler('provision_project', provisionProjectHandler);
    24→ *
    25→ * // Enqueue a provision job
    26→ * await enqueueJob('provision_project', {
    27→ *   project_id: 'proj-123',
    28→ *   region: 'us-east-1'
    29→ * }, {
    30→ *   maxAttempts: 3
    31→ * });
    32→ * ```
    33→ */
    34→
    35→import type { JobPayload, JobExecutionResult } from '@nextmavens/audit-logs-database';
    36→import { enqueueJob } from '../queue.js';
    37→import {
    38→  createTenantDatabase,
    39→  createTenantSchema,
    40→  registerAuthService,
    41→  registerRealtimeService,
    42→  registerStorageService,
    43→  generateApiKeys,
    44→  type ProvisionProjectPayload,
    45→  type ProvisionProjectMetadata,
    46→  validateProvisionProjectPayload,
    47→  ProvisioningError,
    48→} from './provision-project/index.js';
    49→
    50→/**
    51→ * Retry configuration
    52→ * Max 3 attempts with 5-minute intervals
    53→ */
    54→const RETRY_CONFIG = {
    55→  maxAttempts: 3,
    56→  retryIntervalMs: 5 * 60 * 1000, // 5 minutes
    57→};
    58→
    59→/**
    60→ * Provision Project Job Handler
    61→ *
    62→ * Provisions a new tenant project by:
    63→ * 1. Validating the project exists and is eligible for provisioning
    64→ * 2. Creating a dedicated tenant database
    65→ * 3. Creating the tenant schema with proper permissions
    66→ * 4. Registering with auth service (if enabled)
    67→ * 5. Registering with realtime service (if enabled)
    68→ * 6. Registering with storage service (if enabled)
    69→ * 7. Generating initial API keys
    70→ * 8. Returning the provisioned infrastructure details
    71→ *
    72→ * This handler supports retry logic for transient failures such as:
    73→ * - Database connectivity issues
    74→ * - Service registration timeouts
    75→ * - Network errors during provisioning
    76→ *
    77→ * @param payload - Job payload containing project provisioning parameters
    78→ * @returns Promise resolving to job execution result with provisioned infrastructure details
    79→ *
    80→ * @throws Error if project_id is missing
    81→ * @throws Error if region is missing
    82→ * @throws Error if project not found
    83→ * @throws Error if database creation fails
    84→ * @throws Error if schema creation fails
    85→ * @throws Error if service registration fails
    86→ * @throws Error if API key generation fails
    87→ *
    88→ * @example
    89→ * ```typescript
    90→ * const result = await provisionProjectHandler({
    91→ *   project_id: 'proj-123',
    92→ *   region: 'us-east-1',
    93→ *   services: {
    94→ *     auth: true,
    95→ *     realtime: true,
    96→ *     storage: true
    97→ *   },
    98→ *   api_keys: {
    99→ *     count: 2,
   100→ *     prefix: 'proj-123'
   101→ *   }
   102→ * });
   103→ *
   104→ * if (result.success) {
   105→ *   console.log('Project provisioned:', result.data.database);
   106→ * } else {
   107→ *   console.error('Provisioning failed:', result.error);
   108→ * }
   109→ * ```
   110→ */
   111→export async function provisionProjectHandler(
   112→  payload: JobPayload
   113→): Promise<JobExecutionResult> {
   114→  // Validate payload
   115→  const params = payload as ProvisionProjectPayload;
   116→
   117→  try {
   118→    // Validate all input fields
   119→    validateProvisionProjectPayload(params);
   120→  } catch (error) {
   121→    if (error instanceof ProvisioningError) {
   122→      return {
   123→        success: false,
   124→        error: `Validation failed: ${error.message}`,
   125→      };
   126→    }
   127→    return {
   128→      success: false,
   129→      error: 'Validation failed',
   130→    };
   131→  }
   132→
   133→  console.log(`[ProvisionProject] Starting provisioning for project: ${params.project_id}`);
   134→  const startTime = Date.now();
   135→
   136→  try {
   137→    console.log(`[ProvisionProject] Provisioning project ${params.project_id} in region ${params.region}`);
   138→
   139→    // Step 1: Create tenant database
   140→    const databaseInfo = await createTenantDatabase(params.project_id);
   141→
   142→    // Step 2: Create tenant schema
   143→    const schemaInfo = await createTenantSchema(params.project_id, databaseInfo.database_name);
   144→
   145→    // Step 3-5: Register with services (if enabled)
   146→    const services: ProvisionProjectMetadata['services'] = {};
   147→
   148→    if (params.services?.auth) {
   149→      console.log(`[ProvisionProject] Registering with auth service...`);
   150→      services.auth = await registerAuthService(params.project_id, params.region);
   151→    }
   152→
   153→    if (params.services?.realtime) {
   154→      console.log(`[ProvisionProject] Registering with realtime service...`);
   155→      services.realtime = await registerRealtimeService(params.project_id, params.region);
   156→    }
   157→
   158→    if (params.services?.storage) {
   159→      console.log(`[ProvisionProject] Registering with storage service...`);
   160→      services.storage = await registerStorageService(params.project_id, params.region);
   161→    }
   162→
   163→    // Step 6: Generate API keys
   164→    const apiKeyCount = params.api_keys?.count || 1;
   165→    const apiKeyPrefix = params.api_keys?.prefix;
   166→    const apiKeys = await generateApiKeys(params.project_id, apiKeyCount, apiKeyPrefix);
   167→
   168→    // Step 7: Compile metadata
   169→    const metadata: ProvisionProjectMetadata = {
   170→      projectId: params.project_id,
   171→      database: {
   172→        ...databaseInfo,
   173→        ...schemaInfo,
   174→      },
   175→      services,
   176→      api_keys: apiKeys.map((key) => ({
   177→        key_id: key.key_id,
   178→        key_prefix: key.key_prefix,
   179→        created_at: key.created_at,
   180→      })),
   181→      metadata: {
   182→        provisioned_at: new Date(),
   183→        region: params.region,
   184→        owner_id: params.owner_id,
   185→        organization_id: params.organization_id,
   186→      },
   187→    };
   188→
   189→    const durationMs = Date.now() - startTime;
   190→
   191→    console.log(
   192→      `[ProvisionProject] Successfully provisioned project ${params.project_id} in ${durationMs}ms`
   193→    );
   194→
   195→    return {
   196→      success: true,
   197→      data: metadata,
   198→    };
   199→  } catch (error) {
   200→    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
   201→    console.error(`[ProvisionProject] Failed to provision project ${params.project_id}:`, errorMessage);
   202→
   203→    return {
   204→      success: false,
   205→      error: 'Failed to provision project',
   206→    };
   207→  }
   208→}
   209→
   210→/**
   211→ * Convenience function to enqueue a provision_project job
   212→ *
   213→ * @param projectId - The ID of the project to provision
   214→ * @param region - The target region for provisioning
   215→ * @param options - Optional configuration for provisioning
   216→ * @returns Promise resolving to the job ID
   217→ *
   218→ * @example
   219→ * ```typescript
   220→ * import { enqueueProvisionProjectJob } from '@/lib/jobs/handlers/provision-project.handler';
   221→ *
   222→ * // Basic provisioning with defaults
   223→ * const jobId1 = await enqueueProvisionProjectJob('proj-123', 'us-east-1');
   224→ *
   225→ * // Provisioning with custom options
   226→ * const jobId2 = await enqueueProvisionProjectJob('proj-456', 'eu-west-1', {
   227→ *   database: {
   228→ *     engine: 'postgresql',
   229→ *     version: '15',
   230→ *     size: 'db.t3.micro',
   231→ *     storage_gb: 20
   232→ *   },
   233→ *   services: {
   234→ *     auth: true,
   235→ *     realtime: true,
   236→ *     storage: true
   237→ *   },
   238→ *   api_keys: {
   239→ *     count: 3,
   240→ *     prefix: 'prod'
   241→ *   },
   242→ *   owner_id: 'user-123',
   243→ *   organization_id: 'org-456'
   244→ * });
   245→ * ```
   246→ */
   247→export async function enqueueProvisionProjectJob(
   248→  projectId: string,
   249→  region: string,
   250→  options?: Partial<Omit<ProvisionProjectPayload, 'project_id' | 'region'>>
   251→): Promise<string> {
   252→  const payload: ProvisionProjectPayload = {
   253→    project_id: projectId,
   254→    region,
   255→    ...options,
   256→  };
   257→
   258→  const result = await enqueueJob('provision_project', payload, {
   259→    maxAttempts: RETRY_CONFIG.maxAttempts,
   260→  });
   261→
   262→  return result.id;
   263→}
