     8→
     9→import type { JobPayload } from '@nextmavens/audit-logs-database';
    10→
    11→/**
    12→ * Provision project handler payload
    13→ */
    14→export interface ProvisionProjectPayload extends JobPayload {
    15→  /**
    16→   * The ID of the project to provision
    17→   * Must be a valid UUID or alphanumeric string with hyphens/underscores
    18→   */
    19→  project_id: string;
    20→
    21→  /**
    22→   * Target region for infrastructure deployment
    23→   * Must be a valid AWS/GCP region format (e.g., us-east-1, eu-west-1)
    24→   */
    25→  region: string;
    26→
    27→  /**
    28→   * Database configuration options
    29→   */
    30→  database?: {
    31→    /**
    32→     * Database engine type
    33→     */
    34→    engine?: 'postgresql' | 'mysql';
    35→    /**
    36→     * Database version
    37→     */
    38→    version?: string;
    39→    /**
    40→     * Instance size
    41→     */
    42→    size?: string;
    43→    /**
    44→     * Storage size in GB (1-1000)
    45→     */
    46→    storage_gb?: number;
    47→  };
    48→
    49→  /**
    50→   * Service integration flags
    51→   */
    52→  services?: {
    53→    /**
    54→     * Enable auth service integration
    55→     */
    56→    auth?: boolean;
    57→    /**
    58→     * Enable realtime service integration
    59→     */
    60→    realtime?: boolean;
    61→    /**
    62→     * Enable storage service integration
    63→     */
    64→    storage?: boolean;
    65→  };
    66→
    67→  /**
    68→   * API key generation options
    69→   */
    70→  api_keys?: {
    71→    /**
    72→     * Number of keys to generate (1-10)
    73→     */
    74→    count?: number;
    75→    /**
    76→     * Key prefix for identification (alphanumeric and hyphens only)
    77→     */
    78→    prefix?: string;
    79→  };
    80→
    81→  /**
    82→   * Optional owner/user ID
    83→   */
    84→  owner_id?: string;
    85→
    86→  /**
    87→   * Organization ID for multi-tenant support
    88→   */
    89→  organization_id?: string;
    90→}
    91→
    92→/**
    93→ * Validation error class for provisioning errors
    94→ */
    95→export class ProvisioningError extends Error {
    96→  constructor(
    97→    message: string,
    98→    public readonly field?: string,
    99→    public readonly code?: string
   100→  ) {
   101→    super(message);
   102→    this.name = 'ProvisioningError';
   103→  }
   104→}
   105→
   106→/**
   107→ * Validate provision project payload
   108→ *
   109→ * @param payload - The payload to validate
   110→ * @throws ProvisioningError if validation fails
   111→ */
   112→export function validateProvisionProjectPayload(
   113→  payload: ProvisionProjectPayload
   114→): void {
   115→  // Validate project_id
   116→  if (!payload.project_id) {
   117→    throw new ProvisioningError('project_id is required', 'project_id', 'REQUIRED');
   118→  }
   119→
   120→  const projectIdPattern = /^[a-zA-Z0-9-_]+$/;
   121→  if (!projectIdPattern.test(payload.project_id)) {
   122→    throw new ProvisioningError(
   123→      'project_id must contain only alphanumeric characters, hyphens, and underscores',
   124→      'project_id',
   125→      'INVALID_FORMAT'
   126→    );
   127→  }
   128→
   129→  if (payload.project_id.length > 64) {
   130→    throw new ProvisioningError('project_id must be less than 64 characters', 'project_id', 'TOO_LONG');
   131→  }
   132→
   133→  // Validate region
   134→  if (!payload.region) {
   135→    throw new ProvisioningError('region is required', 'region', 'REQUIRED');
   136→  }
   137→
   138→  const regionPattern = /^[a-z]{2}-[a-z]+-\d{1,2}$/;
   139→  if (!regionPattern.test(payload.region)) {
   140→    throw new ProvisioningError(
   141→      'region must be in format like us-east-1, eu-west-1',
   142→      'region',
   143→      'INVALID_FORMAT'
   144→    );
   145→  }
   146→
   147→  // Validate database options
   148→  if (payload.database?.storage_gb !== undefined) {
   149→    if (payload.database.storage_gb < 1 || payload.database.storage_gb > 1000) {
   150→      throw new ProvisioningError(
   151→        'storage_gb must be between 1 and 1000',
   152→        'database.storage_gb',
   153→        'OUT_OF_RANGE'
   154→      );
   155→    }
   156→  }
   157→
   158→  // Validate API key options
   159→  if (payload.api_keys?.count !== undefined) {
   160→    if (payload.api_keys.count < 1 || payload.api_keys.count > 10) {
   161→      throw new ProvisioningError(
   162→        'API key count must be between 1 and 10',
   163→        'api_keys.count',
   164→        'OUT_OF_RANGE'
   165→      );
   166→    }
   167→  }
   168→
   169→  if (payload.api_keys?.prefix) {
   170→    const prefixPattern = /^[a-zA-Z0-9-]+$/;
   171→    if (!prefixPattern.test(payload.api_keys.prefix)) {
   172→      throw new ProvisioningError(
   173→        'API key prefix must contain only alphanumeric characters and hyphens',
   174→        'api_keys.prefix',
   175→        'INVALID_FORMAT'
   176→      );
   177→    }
   178→
   179→    if (payload.api_keys.prefix.length > 32) {
   180→      throw new ProvisioningError(
   181→        'API key prefix must be less than 32 characters',
   182→        'api_keys.prefix',
   183→        'TOO_LONG'
   184→      );
   185→    }
   186→  }
   187→}
