     1→/**
     2→ * Job Worker Initialization
     3→ *
     4→ * Centralizes job worker setup with all handlers registered.
     5→ * Provides functions to start/stop the worker and manage scheduled jobs.
     6→ *
     7→ * US-008: Implement Check Usage Limits Job - Step 7: Integration
     8→ *
     9→ * @example
    10→ * ```typescript
    11→ * import { initializeJobsWorker, shutdownJobsWorker } from '@/lib/jobs/jobs-worker';
    12→ *
    13→ * // Start worker with all handlers registered
    14→ * await initializeJobsWorker();
    15→ *
    16→ * // Later, shutdown gracefully
    17→ * await shutdownJobsWorker();
    18→ * ```
    19→ */
    20→
    21→import { getJobWorker, type JobWorker } from './worker.js';
    22→import { provisionProjectHandler } from './handlers/provision-project.handler.js';
    23→import { checkUsageLimitsHandler } from './handlers/check-usage-limits.handler.js';
    24→import { rotateKeyHandler } from './handlers/rotate-key.handler.js';
    25→import { exportBackupHandler } from './handlers/export-backup.handler.js';
    26→import { exportLogsHandler } from './handlers/export-logs.handler.js';
    27→import { autoSuspendHandler } from './handlers/auto-suspend.handler.js';
    28→import { cleanupExpiredBackupsHandler, notifyBackupExpirationHandler } from './handlers/cleanup-backups.handler.js';
    29→import { enqueueJob } from './queue.js';
    30→import { JobType } from '@nextmavens/audit-logs-database';
    31→import { getConfig as getRetentionConfig } from '../backups/retention.config.js';
    32→
    33→/**
    34→ * Scheduled job interface
    35→ */
    36→interface ScheduledJob {
    37→  jobType: string;
    38→  intervalMs: number;
    39→  payload: Record<string, unknown>;
    40→  timerId: NodeJS.Timeout | null;
    41→}
    42→
    43→/**
    44→ * Active scheduled jobs
    45→ */
    46→const scheduledJobs: Map<string, ScheduledJob> = new Map();
    47→
    48→/**
    49→ * Get job worker with all handlers registered
    50→ *
    51→ * @returns Configured job worker instance
    52→ */
    53→export function getJobsWorker(): JobWorker {
    54→  const worker = getJobWorker({
    55→    pollInterval: 5000, // 5 seconds
    56→    maxConcurrentJobs: 5,
    57→    timeout: 300000, // 5 minutes
    58→  });
    59→
    60→  // Register all job handlers
    61→  worker.registerHandler(JobType.PROVISION_PROJECT, provisionProjectHandler);
    62→  worker.registerHandler(JobType.CHECK_USAGE_LIMITS, checkUsageLimitsHandler);
    63→  worker.registerHandler(JobType.ROTATE_KEY, rotateKeyHandler);
    64→  worker.registerHandler(JobType.EXPORT_BACKUP, exportBackupHandler);
    65→  worker.registerHandler('export_logs', exportLogsHandler);
    66→  worker.registerHandler(JobType.AUTO_SUSPEND, autoSuspendHandler);
    67→
    68→  // US-010: Register backup retention cleanup handlers
    69→  worker.registerHandler('cleanup_expired_backups', cleanupExpiredBackupsHandler);
    70→  worker.registerHandler('notify_backup_expiration', notifyBackupExpirationHandler);
    71→
    72→  return worker;
    73→}
    74→
    75→/**
    76→ * Initialize and start the job worker
    77→ *
    78→ * This function:
    79→ * 1. Gets the job worker with all handlers registered
    80→ * 2. Starts the worker polling loop
    81→ * 3. Sets up scheduled jobs (e.g., hourly usage limits check)
    82→ *
    83→ * @returns Promise that resolves when worker is started
    84→ */
    85→export async function initializeJobsWorker(): Promise<void> {
    86→  console.log('[JobsWorker] Initializing job worker...');
    87→
    88→  try {
    89→    // Get worker with handlers registered
    90→    const worker = getJobsWorker();
    91→
    92→    // Verify handlers are registered
    93→    const stats = worker.getStats();
    94→    console.log(`[JobsWorker] Registered ${stats.registeredHandlers} job handlers`);
    95→
    96→    // Start the worker
    97→    await worker.start();
    98→    console.log('[JobsWorker] Job worker started successfully');
    99→
   100→    // Set up scheduled jobs
   101→    setupScheduledJobs();
   102→    console.log('[JobsWorker] Scheduled jobs configured');
   103→  } catch (error) {
   104→    console.error('[JobsWorker] Failed to initialize job worker:', error);
   105→    throw error;
   106→  }
   107→}
   108→
   109→/**
   110→ * Setup scheduled jobs
   111→ *
   112→ * Configures recurring jobs like:
   113→ * - Hourly usage limits check
   114→ * - Daily backup cleanup (US-010)
   115→ * - Daily backup expiration notifications (US-010)
   116→ */
   117→function setupScheduledJobs(): void {
   118→  // Schedule hourly usage limits check
   119→  scheduleJob({
   120→    name: 'hourly-usage-check',
   121→    jobType: JobType.CHECK_USAGE_LIMITS,
   122→    intervalMs: 60 * 60 * 1000, // 1 hour
   123→    payload: {
   124→      check_all: true,
   125→      enforce_limits: true,
   126→    },
   127→  });
   128→
   129→  // US-010: Schedule daily backup cleanup
   130→  const retentionConfig = getRetentionConfig();
   131→  const cleanupInterval = retentionConfig.cleanupIntervalHours * 60 * 60 * 1000;
   132→
   133→  scheduleJob({
   134→    name: 'backup-cleanup',
   135→    jobType: 'cleanup_expired_backups',
   136→    intervalMs: cleanupInterval,
   137→    payload: {
   138→      batch_size: retentionConfig.cleanupBatchSize,
   139→      notify_first: false, // Notifications are sent separately
   140→      dry_run: false,
   141→    },
   142→  });
   143→
   144→  // US-010: Schedule daily backup expiration notifications
   145→  scheduleJob({
   146→    name: 'backup-expiration-notify',
   147→    jobType: 'notify_backup_expiration',
   148→    intervalMs: 24 * 60 * 60 * 1000, // Daily
   149→    payload: {
   150→      batch_size: retentionConfig.cleanupBatchSize,
   151→    },
   152→  });
   153→
   154→  console.log('[JobsWorker] Scheduled jobs configured:');
   155→  console.log('  - check_usage_limits: Every hour');
   156→  console.log(`  - cleanup_expired_backups: Every ${retentionConfig.cleanupIntervalHours} hour(s)`);
   157→  console.log('  - notify_backup_expiration: Daily');
   158→}
   159→
   160→/**
   161→ * Schedule a recurring job
   162→ *
   163→ * @param options - Job scheduling options
   164→ */
   165→interface ScheduleJobOptions {
   166→  name: string;
   167→  jobType: string;
   168→  intervalMs: number;
   169→  payload: Record<string, unknown>;
   170→}
   171→
   172→function scheduleJob(options: ScheduleJobOptions): void {
   173→  const { name, jobType, intervalMs, payload } = options;
   174→
   175→  // Clear existing timer if job is already scheduled
   176→  const existing = scheduledJobs.get(name);
   177→  if (existing?.timerId) {
   178→    clearInterval(existing.timerId);
   179→  }
   180→
   181→  // Schedule the job
   182→  const timerId = setInterval(async () => {
   183→    try {
   184→      console.log(`[JobsWorker] Executing scheduled job: ${name}`);
   185→      await enqueueJob(jobType, payload);
   186→    } catch (error) {
   187→      console.error(`[JobsWorker] Failed to execute scheduled job ${name}:`, error);
   188→    }
   189→  }, intervalMs);
   190→
   191→  // Store scheduled job
   192→  scheduledJobs.set(name, {
   193→    jobType,
   194→    intervalMs,
   195→    payload,
   196→    timerId,
   197→  });
   198→
   199→  console.log(`[JobsWorker] Scheduled job '${name}' to run every ${intervalMs}ms`);
   200→}
   201→
   202→/**
   203→ * Shutdown the job worker gracefully
   204→ *
   205→ * Stops the worker and clears all scheduled job timers
   206→ *
   207→ * @param timeout - Maximum time to wait for jobs to complete (ms)
   208→ * @returns Promise that resolves when worker is stopped
   209→ */
   210→export async function shutdownJobsWorker(timeout?: number): Promise<void> {
   211→  console.log('[JobsWorker] Shutting down job worker...');
   212→
   213→  try {
   214→    // Clear all scheduled job timers
   215→    scheduledJobs.forEach((job, name) => {
   216→      if (job.timerId) {
   217→        clearInterval(job.timerId);
   218→        console.log(`[JobsWorker] Cleared scheduled job: ${name}`);
   219→      }
   220→    });
   221→    scheduledJobs.clear();
   222→
   223→    // Stop the worker
   224→    const worker = getJobWorker();
   225→    await worker.stop(timeout);
   226→
   227→    console.log('[JobsWorker] Job worker shut down successfully');
   228→  } catch (error) {
   229→    console.error('[JobsWorker] Error during shutdown:', error);
   230→    throw error;
   231→  }
   232→}
   233→
   234→/**
   235→ * Get information about scheduled jobs
   236→ *
   237→ * @returns Array of scheduled job information
   238→ */
   239→export function getScheduledJobs(): Array<{
   240→  name: string;
   241→  jobType: string;
   242→  intervalMs: number;
   243→  intervalDescription: string;
   244→}> {
   245→  const jobs: Array<{
   246→    name: string;
   247→    jobType: string;
   248→    intervalMs: number;
   249→    intervalDescription: string;
   250→  }> = [];
   251→
   252→  scheduledJobs.forEach((job, name) => {
   253→    jobs.push({
   254→      name,
   255→      jobType: job.jobType,
   256→      intervalMs: job.intervalMs,
   257→      intervalDescription: formatInterval(job.intervalMs),
   258→    });
   259→  });
   260→
   261→  return jobs;
   262→}
   263→
   264→/**
   265→ * Format interval milliseconds into human-readable description
   266→ */
   267→function formatInterval(ms: number): string {
   268→  const seconds = Math.floor(ms / 1000);
   269→  const minutes = Math.floor(seconds / 60);
   270→  const hours = Math.floor(minutes / 60);
   271→  const days = Math.floor(hours / 24);
   272→
   273→  if (days > 0) {
   274→    return `Every ${days} day${days > 1 ? 's' : ''}`;
   275→  }
   276→  if (hours > 0) {
   277→    return `Every ${hours} hour${hours > 1 ? 's' : ''}`;
   278→  }
   279→  if (minutes > 0) {
   280→    return `Every ${minutes} minute${minutes > 1 ? 's' : ''}`;
   281→  }
   282→  return `Every ${seconds} second${seconds > 1 ? 's' : ''}`;
   283→}
