     5export class DatabaseTools {
     6  private apiKey: string;
     7  private apiUrl: string;
     8
     9  constructor(apiKey: string, apiUrl: string) {
    10    this.apiKey = apiKey;
    11    this.apiUrl = apiUrl;
    12  }
    13
    14  private buildUrl(table: string, filters: any[] = [], limit?: number, offset?: number, orderBy?: any): string {
    15    let url = `${this.apiUrl}/${table}`;
    16    const params: string[] = [];
    17
    18    // Build filters
    19    filters.forEach((f: any) => {
    20      const value = typeof f.value === 'object' ? JSON.stringify(f.value) : String(f.value);
    21
    22      switch (f.operator) {
    23        case 'eq':
    24          params.push(`${f.column}=${encodeURIComponent(value)}`);
    25          break;
    26        case 'neq':
    27          params.push(`${f.column}.neq=${encodeURIComponent(value)}`);
    28          break;
    29        case 'gt':
    30          params.push(`${f.column}.gt=${encodeURIComponent(value)}`);
    31          break;
    32        case 'gte':
    33          params.push(`${f.column}.gte=${encodeURIComponent(value)}`);
    34          break;
    35        case 'lt':
    36          params.push(`${f.column}.lt=${encodeURIComponent(value)}`);
    37          break;
    38        case 'lte':
    39          params.push(`${f.column}.lte=${encodeURIComponent(value)}`);
    40          break;
    41        case 'like':
    42          params.push(`${f.column}.like=${encodeURIComponent(value)}`);
    43          break;
    44        case 'ilike':
    45          params.push(`${f.column}.ilike=${encodeURIComponent(value)}`);
    46          break;
    47        case 'in':
    48          params.push(`${f.column}.in=${encodeURIComponent(value)}`);
    49          break;
    50      }
    51    });
    52
    53    // Add ordering
    54    if (orderBy) {
    55      const order = orderBy.ascending ? 'asc' : 'desc';
    56      params.push(`order=${orderBy.column}.${order}`);
    57    }
    58
    59    // Add pagination
    60    if (limit) params.push(`limit=${limit}`);
    61    if (offset) params.push(`offset=${offset}`);
    62
    63    if (params.length > 0) {
    64      url += '?' + params.join('&');
    65    }
    66
    67    return url;
    68  }
