    91→            }
    92→          ],
    93→          isError: true
    94→        };
    95→      }
    96→
    97→      // Handle different response formats
    98→      const results = Array.isArray(data) ? data : (data.data || data.results || data);
    99→
   100→      return {
   101→        content: [
   102→          {
   103→            type: 'text',
   104→            text: JSON.stringify({
   105→              table,
   106→              count: results.length,
   107→              data: results
   108→            }, null, 2)
   109→          }
   110→        ]
   111→      };
   112→    } catch (error: any) {
