     1→/**
     2→ * Job Queue System
     3→ *
     4→ * Provides a job queue implementation for enqueuing background jobs
     5→ * with support for scheduling, priority, and retry configuration.
     6→ *
     7→ * US-002: Create Job Queue System
     8→ */
     9→
    10→import { v4 as uuidv4 } from 'uuid';
    11→import { query } from '../pool.js';
    12→import type {
    13→  Job,
    14→  JobType,
    15→  JobPayload,
    16→  JobStatus,
    17→} from '../../types/jobs.types.js';
    18→
    19→/**
    20→ * Job queue options
    21→ */
    22→export interface JobQueueOptions {
    23→  /** Project ID for authorization and data isolation */
    24→  project_id: string;
    25→  /** Delay before job should be processed (milliseconds) */
    26→  delay?: number;
    27→  /** Maximum number of retry attempts (default: 3) */
    28→  max_attempts?: number;
    29→  /** Job priority for execution order (higher = more important) */
    30→  priority?: number;
    31→}
    32→
    33→/**
    34→ * Validation constants
    35→ */
    36→const VALIDATION = {
    37→  /** Maximum allowed max_attempts value */
    38→  MAX_MAX_ATTEMPTS: 100,
    39→  /** Minimum allowed max_attempts value */
    40→  MIN_MAX_ATTEMPTS: 1,
    41→  /** Maximum allowed delay in milliseconds (24 hours) */
    42→  MAX_DELAY: 24 * 60 * 60 * 1000,
    43→  /** Minimum allowed delay (0 = immediate) */
    44→  MIN_DELAY: 0,
    45→  /** Maximum allowed priority value */
    46→  MAX_PRIORITY: 1000,
    47→  /** Minimum allowed priority value */
    48→  MIN_PRIORITY: 0,
    49→} as const;
    50→
    51→/**
    52→ * Validate job type string
    53→ */
    54→function validateJobType(type: string): void {
    55→  if (typeof type !== 'string') {
    56→    throw new Error('Job type must be a string');
    57→  }
    58→  if (type.trim().length === 0) {
    59→    throw new Error('Job type cannot be empty');
    60→  }
    61→  if (type.length > 100) {
    62→    throw new Error('Job type cannot exceed 100 characters');
    63→  }
    64→  // Allow only alphanumeric, underscore, and hyphen
    65→  if (!/^[a-zA-Z0-9_-]+$/.test(type)) {
    66→    throw new Error('Job type can only contain alphanumeric characters, underscores, and hyphens');
    67→  }
    68→}
    69→
    70→/**
    71→ * Validate job payload
    72→ */
    73→function validateJobPayload(payload: JobPayload): void {
    74→  if (typeof payload !== 'object' || payload === null) {
    75→    throw new Error('Job payload must be an object');
    76→  }
    77→  // Check payload size when serialized (max 1MB)
    78→  const serialized = JSON.stringify(payload);
    79→  if (serialized.length > 1024 * 1024) {
    80→    throw new Error('Job payload size cannot exceed 1MB');
    81→  }
    82→}
    83→
    84→/**
    85→ * Validate max_attempts value
    86→ */
    87→function validateMaxAttempts(maxAttempts: number): void {
    88→  if (!Number.isInteger(maxAttempts)) {
    89→    throw new Error('max_attempts must be an integer');
    90→  }
    91→  if (maxAttempts < VALIDATION.MIN_MAX_ATTEMPTS) {
    92→    throw new Error(`max_attempts must be at least ${VALIDATION.MIN_MAX_ATTEMPTS}`);
    93→  }
    94→  if (maxAttempts > VALIDATION.MAX_MAX_ATTEMPTS) {
    95→    throw new Error(`max_attempts cannot exceed ${VALIDATION.MAX_MAX_ATTEMPTS}`);
    96→  }
    97→}
    98→
    99→/**
   100→ * Validate delay value
   101→ */
   102→function validateDelay(delay: number): void {
   103→  if (!Number.isInteger(delay)) {
   104→    throw new Error('delay must be an integer');
   105→  }
   106→  if (delay < VALIDATION.MIN_DELAY) {
   107→    throw new Error(`delay must be at least ${VALIDATION.MIN_DELAY}`);
   108→  }
   109→  if (delay > VALIDATION.MAX_DELAY) {
   110→    throw new Error(`delay cannot exceed ${VALIDATION.MAX_DELAY} milliseconds (24 hours)`);
   111→  }
   112→}
   113→
   114→/**
   115→ * Validate priority value
   116→ */
   117→function validatePriority(priority: number): void {
   118→  if (!Number.isInteger(priority)) {
   119→    throw new Error('priority must be an integer');
   120→  }
   121→  if (priority < VALIDATION.MIN_PRIORITY) {
   122→    throw new Error(`priority must be at least ${VALIDATION.MIN_PRIORITY}`);
   123→  }
   124→  if (priority > VALIDATION.MAX_PRIORITY) {
   125→    throw new Error(`priority cannot exceed ${VALIDATION.MAX_PRIORITY}`);
   126→  }
   127→}
   128→
   129→/**
   130→ * Validate scheduled date
   131→ */
   132→function validateScheduledDate(date: Date): void {
   133→  if (!(date instanceof Date) || isNaN(date.getTime())) {
   134→    throw new Error('Scheduled date must be a valid Date object');
   135→  }
   136→  // Don't allow scheduling too far in the future (max 1 year)
   137→  const maxFuture = new Date();
   138→  maxFuture.setFullYear(maxFuture.getFullYear() + 1);
   139→  if (date > maxFuture) {
   140→    throw new Error('Cannot schedule jobs more than 1 year in the future');
   141→  }
   142→}
   143→
   144→/**
   145→ * Job enqueue result
   146→ */
   147→export interface EnqueueJobResult {
   148→  /** The created job ID */
   149→  id: string;
   150→  /** The job type */
   151→  type: string;
   152→  /** Current job status */
   153→  status: JobStatus;
   154→  /** When the job is scheduled to run */
   155→  scheduled_at: Date;
   156→  /** When the job was created */
   157→  created_at: Date;
   158→}
   159→
   160→/**
   161→ * JobQueue class
   162→ * Manages job enqueuing with support for scheduling and retry configuration
   163→ */
   164→class JobQueueClass {
   165→  /**
   166→   * Enqueue a new job
   167→   *
   168→   * @param type - The job type identifier
   169→   * @param payload - Job data payload
   170→   * @param options - Additional job options (project_id, delay, max_attempts, priority)
   171→   * @returns The created job ID and metadata
   172→   *
   173→   * @example
   174→   * ```typescript
   175→   * const queue = new JobQueue();
   176→   * const result = await queue.enqueue('provision_project', {
   177→   *   project_id: 'proj-123',
   178→   *   region: 'us-east-1',
   179→   * }, {
   180→   *   project_id: 'proj-123',
   181→   *   delay: 5000, // 5 seconds
   182→   *   max_attempts: 3,
   183→   * });
   184→   * ```
   185→   */
   186→  async enqueue(
   187→    type: JobType | string,
   188→    payload: JobPayload = {},
   189→    options: JobQueueOptions
   190→  ): Promise<EnqueueJobResult> {
   191→    const { project_id, delay = 0, max_attempts = 3, priority } = options;
   192→
   193→    // Validate inputs
   194→    validateJobType(type);
   195→    validateJobPayload(payload);
   196→    validateMaxAttempts(max_attempts);
   197→    validateDelay(delay);
   198→
   199→    if (priority !== undefined) {
   200→      validatePriority(priority);
   201→    }
   202→
   203→    // Generate a unique job ID
   204→    const id = uuidv4();
   205→
   206→    // Calculate scheduled time
   207→    const scheduled_at = new Date(Date.now() + delay);
   208→
   209→    // Merge priority into payload
   210→    const finalPayload = priority !== undefined
   211→      ? { ...payload, priority }
   212→      : payload;
   213→
   214→    // Insert job into database
   215→    const queryText = `
   216→      INSERT INTO control_plane.jobs (
   217→        id,
   218→        project_id,
   219→        type,
   220→        payload,
   221→        status,
   222→        max_attempts,
   223→        scheduled_at
   224→      ) VALUES ($1, $2, $3, $4, 'pending', $5, $6)
   225→      RETURNING id, type, status, scheduled_at, created_at
   226→    `;
   227→
   228→    const values = [
   229→      id,
   230→      project_id,
   231→      type,
   232→      JSON.stringify(finalPayload),
   233→      max_attempts,
   234→      scheduled_at,
   235→    ];
   236→
   237→    try {
   238→      const result = await query(queryText, values);
   239→      const row = result.rows[0];
   240→
   241→      if (!row) {
   242→        throw new Error('Failed to create job');
   243→      }
   244→
   245→      return {
   246→        id: row.id,
   247→        type: row.type,
   248→        status: row.status as JobStatus,
   249→        scheduled_at: row.scheduled_at,
   250→        created_at: row.created_at,
   251→      };
   252→    } catch (error) {
   253→      // Don't expose internal error messages
   254→      if (error instanceof Error && error.message.startsWith('Failed to create job')) {
   255→        throw error;
   256→      }
   257→      throw new Error('Failed to enqueue job');
   258→    }
   259→  }
   260→
   261→  /**
   262→   * Schedule a job to run at a specific time
   263→   *
   264→   * @param type - The job type identifier
   265→   * @param scheduledAt - When to run the job
   266→   * @param payload - Job data payload
   267→   * @param options - Additional job options (project_id is required)
   268→   * @returns The created job ID and metadata
   269→   *
   270→   * @example
   271→   * ```typescript
   272→   * const queue = new JobQueue();
   273→   * const scheduledTime = new Date('2026-01-30T10:00:00Z');
   274→   * const result = await queue.schedule(
   275→   *   'backup',
   276→   *   scheduledTime,
   277→   *   { project_id: 'proj-123' },
   278→   *   { project_id: 'proj-123' }
   279→   * );
   280→   * ```
   281→   */
   282→  async schedule(
   283→    type: JobType | string,
   284→    scheduledAt: Date,
   285→    payload: JobPayload = {},
   286→    options: JobQueueOptions
   287→  ): Promise<EnqueueJobResult> {
   288→    const { project_id, max_attempts = 3, priority } = options;
   289→
   290→    // Validate inputs
   291→    validateJobType(type);
   292→    validateJobPayload(payload);
   293→    validateScheduledDate(scheduledAt);
   294→    validateMaxAttempts(max_attempts);
   295→
   296→    if (priority !== undefined) {
   297→      validatePriority(priority);
   298→    }
   299→
   300→    const id = uuidv4();
   301→
   302→    // Merge priority into payload
   303→    const finalPayload = priority !== undefined
   304→      ? { ...payload, priority }
   305→      : payload;
   306→
   307→    const queryText = `
   308→      INSERT INTO control_plane.jobs (
   309→        id,
   310→        project_id,
   311→        type,
   312→        payload,
   313→        status,
   314→        max_attempts,
   315→        scheduled_at
   316→      ) VALUES ($1, $2, $3, $4, 'pending', $5, $6)
   317→      RETURNING id, type, status, scheduled_at, created_at
   318→    `;
   319→
   320→    const values = [
   321→      id,
   322→      project_id,
   323→      type,
   324→      JSON.stringify(finalPayload),
   325→      max_attempts,
   326→      scheduledAt,
   327→    ];
   328→
   329→    try {
   330→      const result = await query(queryText, values);
   331→      const row = result.rows[0];
   332→
   333→      if (!row) {
   334→        throw new Error('Failed to create job');
   335→      }
   336→
   337→      return {
   338→        id: row.id,
   339→        type: row.type,
   340→        status: row.status as JobStatus,
   341→        scheduled_at: row.scheduled_at,
   342→        created_at: row.created_at,
   343→      };
   344→    } catch (error) {
   345→      // Don't expose internal error messages
   346→      if (error instanceof Error && error.message.startsWith('Failed to create job')) {
   347→        throw error;
   348→      }
   349→      throw new Error('Failed to schedule job');
   350→    }
   351→  }
   352→
   353→  /**
   354→   * Get a job by ID
   355→   *
   356→   * @param id - The job ID
   357→   * @returns The job or null if not found
   358→   */
   359→  async getJob(id: string): Promise<Job | null> {
   360→    const queryText = `
   361→      SELECT
   362→        id,
   363→        project_id,
   364→        type,
   365→        payload,
   366→        status,
   367→        attempts,
   368→        max_attempts,
   369→        last_error,
   370→        scheduled_at,
   371→        started_at,
   372→        completed_at,
   373→        created_at
   374→      FROM control_plane.jobs
   375→      WHERE id = $1
   376→    `;
   377→
   378→    try {
   379→      const result = await query(queryText, [id]);
   380→
   381→      if (result.rows.length === 0) {
   382→        return null;
   383→      }
   384→
   385→      const row = result.rows[0];
   386→
   387→      if (!row) {
   388→        return null;
   389→      }
   390→
   391→      return {
   392→        id: row.id,
   393→        project_id: row.project_id,
   394→        type: row.type,
   395→        payload: row.payload,
   396→        status: row.status as JobStatus,
   397→        attempts: row.attempts,
   398→        max_attempts: row.max_attempts,
   399→        last_error: row.last_error,
   400→        scheduled_at: row.scheduled_at,
   401→        started_at: row.started_at,
   402→        completed_at: row.completed_at,
   403→        created_at: row.created_at,
   404→      };
   405→    } catch (error) {
   406→      throw new Error('Failed to retrieve job');
   407→    }
   408→  }
   409→}
   410→
   411→/**
   412→ * Global job queue instance
   413→ */
   414→const jobQueue = new JobQueueClass();
   415→
   416→/**
   417→ * Enqueue a job for background processing
   418→ *
   419→ * This is a convenience function that uses the global JobQueue instance.
   420→ *
   421→ * @param type - The job type identifier
   422→ * @param payload - Job data payload
   423→ * @param options - Additional job options (project_id is required, delay, max_attempts, priority)
   424→ * @returns The created job ID and metadata
   425→ *
   426→ * @example
   427→ * ```typescript
   428→ * import { enqueueJob } from '@nextmavens/audit-logs-database';
   429→ *
   430→ * // Enqueue a job to run immediately
   431→ * const result = await enqueueJob('provision_project', {
   432→ *   project_id: 'proj-123',
   433→ *   region: 'us-east-1',
   434→ * }, {
   435→ *   project_id: 'proj-123',
   436→ * });
   437→ *
   438→ * // Enqueue a job with a delay
   439→ * const delayed = await enqueueJob('rotate_key', {
   440→ *   key_id: 'key-456',
   441→ * }, {
   442→ *   project_id: 'proj-123',
   443→ *   delay: 60000, // 1 minute
   444→ *   max_attempts: 5,
   445→ * });
   446→ *
   447→ * // Enqueue a high-priority job
   448→ * const priority = await enqueueJob('send_notification', {
   449→ *   user_id: 'user-789',
   450→ *   message: 'Project provisioned successfully',
   451→ * }, {
   452→ *   project_id: 'proj-123',
   453→ *   priority: 100, // High priority
   454→ * });
   455→ * ```
   456→ */
   457→export async function enqueueJob(
   458→  type: JobType | string,
   459→  payload: JobPayload = {},
   460→  options: JobQueueOptions
   461→): Promise<EnqueueJobResult> {
   462→  return jobQueue.enqueue(type, payload, options);
   463→}
   464→
   465→/**
   466→ * Schedule a job to run at a specific time
   467→ *
   468→ * This is a convenience function that uses the global JobQueue instance.
   469→ *
   470→ * @param type - The job type identifier
   471→ * @param scheduledAt - When to run the job
   472→ * @param payload - Job data payload
   473→ * @param options - Additional job options (project_id is required)
   474→ * @returns The created job ID and metadata
   475→ *
   476→ * @example
   477→ * ```typescript
   478→ * import { scheduleJob } from '@nextmavens/audit-logs-database';
   479→ *
   480→ * const scheduledTime = new Date('2026-01-30T10:00:00Z');
   481→ * const result = await scheduleJob('backup', scheduledTime, {
   482→ *   project_id: 'proj-123',
   483→ * }, {
   484→ *   project_id: 'proj-123',
   485→ * });
   486→ * ```
   487→ */
   488→export async function scheduleJob(
   489→  type: JobType | string,
   490→  scheduledAt: Date,
   491→  payload: JobPayload = {},
   492→  options: JobQueueOptions
   493→): Promise<EnqueueJobResult> {
   494→  return jobQueue.schedule(type, scheduledAt, payload, options);
   495→}
   496→
   497→/**
   498→ * Get a job by ID
   499→ *
   500→ * This is a convenience function that uses the global JobQueue instance.
   501→ *
   502→ * @param id - The job ID
   503→ * @returns The job or null if not found
   504→ */
   505→export async function getJob(id: string): Promise<Job | null> {
   506→  return jobQueue.getJob(id);
   507→}
   508→
   509→/**
   510→ * JobQueue class export
   511→ * Export the JobQueue class for advanced usage
   512→ */
   513→export { JobQueueClass as JobQueue };
   514→
   515→// Re-export retryJob from retry.ts
   516→export { retryJob } from './retry.js';
   517→

