     9→/**
    10→ * Configuration validation error
    11→ */
    12→export class ConfigError extends Error {
    13→  constructor(
    14→    message: string,
    15→    public readonly field?: string
    16→  ) {
    17→    super(message);
    18→    this.name = 'ConfigError';
    19→  }
    20→}
    21→
    22→/**
    23→ * Validated configuration for service endpoints
    24→ */
    25→export interface ServiceConfig {
    26→  authServiceUrl: string;
    27→  realtimeServiceUrl: string;
    28→  storageServiceUrl: string;
    29→  authServiceToken?: string;
    30→  realtimeServiceToken?: string;
    31→  storageServiceToken?: string;
    32→}
    33→
    34→/**
    35→ * Validate URL format
    36→ */
    37→function validateUrl(url: string, fieldName: string): void {
    38→  try {
    39→    const parsed = new URL(url);
    40→    if (!['http:', 'https:'].includes(parsed.protocol)) {
    41→      throw new ConfigError(`${fieldName} must use http or https protocol`, fieldName);
    42→    }
    43→  } catch {
    44→    throw new ConfigError(`${fieldName} must be a valid URL`, fieldName);
    45→  }
    46→}
    47→
    48→/**
    49→ * Get and validate service configuration
    50→ *
    51→ * @returns Validated service configuration
    52→ * @throws ConfigError if configuration is invalid
    53→ */
    54→export function getServiceConfig(): ServiceConfig {
    55→  const authServiceUrl = process.env.AUTH_SERVICE_URL || 'http://localhost:3001';
    56→  const realtimeServiceUrl = process.env.REALTIME_SERVICE_URL || 'http://localhost:3002';
    57→  const storageServiceUrl = process.env.STORAGE_SERVICE_URL || 'http://localhost:3003';
    58→
    59→  // Validate URL formats
    60→  validateUrl(authServiceUrl, 'AUTH_SERVICE_URL');
    61→  validateUrl(realtimeServiceUrl, 'REALTIME_SERVICE_URL');
    62→  validateUrl(storageServiceUrl, 'STORAGE_SERVICE_URL');
    63→
    64→  // Get optional service authentication tokens
    65→  // These tokens should be set via environment variables for service-to-service authentication
    66→  const authServiceToken = process.env.AUTH_SERVICE_TOKEN;
    67→  const realtimeServiceToken = process.env.REALTIME_SERVICE_TOKEN;
    68→  const storageServiceToken = process.env.STORAGE_SERVICE_TOKEN;
    69→
    70→  return {
    71→    authServiceUrl,
    72→    realtimeServiceUrl,
    73→    storageServiceUrl,
    74→    authServiceToken,
    75→    realtimeServiceToken,
    76→    storageServiceToken,
    77→  };
    78→}
    79→
    80→/**
    81→ * Validate database configuration
    82→ *
    83→ * @returns Database configuration
    84→ * @throws ConfigError if configuration is invalid
    85→ */
    86→export function getDatabaseConfig(): {
    87→  host: string;
    88→  port: number;
    89→} {
    90→  const host = process.env.AUDIT_LOGS_DB_HOST || 'localhost';
    91→  const portStr = process.env.AUDIT_LOGS_DB_PORT || '5432';
    92→
    93→  const port = parseInt(portStr, 10);
    94→  if (isNaN(port) || port < 1 || port > 65535) {
    95→    throw new ConfigError('AUDIT_LOGS_DB_PORT must be a valid port number (1-65535)', 'AUDIT_LOGS_DB_PORT');
    96→  }
    97→
    98→  return { host, port };
    99→}
   100→
   101→/**
   102→ * Validate all required configuration on module load
   103→ * This will throw during initialization if configuration is invalid
   104→ */
   105→export function validateConfig(): void {
   106→  try {
   107→    getServiceConfig();
   108→    getDatabaseConfig();
   109→  } catch (error) {
   110→    if (error instanceof ConfigError) {
   111→      console.error(`[ConfigError] ${error.field}: ${error.message}`);
   112→      throw error;
   113→    }
   114→    throw error;
   115→  }
   116→}
