     1→/**
     2→ * API Key Generation
     3→ *
     4→ * Handles generation of secure API keys for tenant projects.
     5→ *
     6→ * US-004: Implement Provision Project Job - Step 7: Implementation
     7→ */
     8→
     9→import { v4 as uuidv4 } from 'uuid';
    10→import { createHash, randomBytes } from 'crypto';
    11→
    12→/**
    13→ * Generate API keys
    14→ *
    15→ * Generates secure API keys for the tenant with cryptographic hashing.
    16→ *
    17→ * @param projectId - The project ID for key identification
    18→ * @param count - Number of keys to generate
    19→ * @param prefix - Optional key prefix for identification
    20→ * @returns Promise resolving to array of generated API keys
    21→ * @throws Error if key generation fails
    22→ */
    23→export async function generateApiKeys(
    24→  projectId: string,
    25→  count: number,
    26→  prefix?: string
    27→): Promise<Array<{ key_id: string; key_prefix: string; created_at: Date; key_hash: string }>> {
    28→  console.log(`[ProvisionProject] Generating ${count} API keys for project: ${projectId}`);
    29→
    30→  const keys: Array<{
    31→    key_id: string;
    32→    key_prefix: string;
    33→    created_at: Date;
    34→    key_hash: string;
    35→  }> = [];
    36→
    37→  try {
    38→    for (let i = 0; i < count; i++) {
    39→      // Generate a unique key ID
    40→      const keyId = uuidv4();
    41→      const keyPrefix = prefix || projectId;
    42→
    43→      // Generate a secure random API key
    44→      // Format: {prefix}_{timestamp}_{random}
    45→      const timestamp = Date.now();
    46→      const randomBytesHex = randomBytes(16).toString('hex');
    47→      const apiKey = `${keyPrefix}_${timestamp}_${randomBytesHex}`;
    48→
    49→      // Hash the API key for storage (we never store the actual key)
    50→      const keyHash = createHash('sha256').update(apiKey).digest('hex');
    51→
    52→      keys.push({
    53→        key_id: keyId,
    54→        key_prefix: keyPrefix,
    55→        created_at: new Date(),
    56→        key_hash: keyHash,
    57→      });
    58→
    59→      console.log(`[ProvisionProject] Generated API key: ${keyId}`);
    60→    }
    61→
    62→    console.log(`[ProvisionProject] Successfully generated ${count} API keys`);
    63→
    64→    return keys;
    65→  } catch (error) {
    66→    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
    67→    console.error(`[ProvisionProject] Failed to generate API keys:`, errorMessage);
    68→    throw new Error(`Failed to generate API keys: ${errorMessage}`);
    69→  }
    70→}
