     1→/**
     2→ * Tenant Database Creation
     3→ *
     4→ * Handles creation of tenant databases and schemas for project provisioning.
     5→ *
     6→ * US-004: Implement Provision Project Job - Step 10: Security & Error Handling
     7→ */
     8→
     9→import { query } from '@nextmavens/audit-logs-database';
    10→import { getDatabaseConfig } from './config.js';
    11→
    12→/**
    13→ * Validate and sanitize project ID for use as database name
    14→ *
    15→ * @param projectId - The project ID to validate
    16→ * @returns Sanitized database name
    17→ * @throws Error if project ID contains invalid characters
    18→ */
    19→function validateDatabaseName(projectId: string): string {
    20→  // Validate project ID format (UUID or alphanumeric with hyphens/underscores)
    21→  const validPattern = /^[a-zA-Z0-9-_]+$/;
    22→  if (!validPattern.test(projectId)) {
    23→    throw new Error('Invalid project ID format');
    24→  }
    25→
    26→  // Sanitize to prevent SQL injection
    27→  const databaseName = `tenant_${projectId}`.toLowerCase().replace(/[^a-z0-9_]/g, '_');
    28→
    29→  // Additional safety: enforce length limits
    30→  if (databaseName.length > 63) {
    31→    throw new Error('Project ID too long for database name');
    32→  }
    33→
    34→  return databaseName;
    35→}
    36→
    37→/**
    38→ * Create tenant database
    39→ *
    40→ * Creates a new PostgreSQL database for the tenant with proper encoding and locale.
    41→ * Uses parameterized query via format() to prevent SQL injection.
    42→ *
    43→ * @param projectId - The project ID to use as database name
    44→ * @returns Promise resolving to database connection details
    45→ * @throws Error if database creation fails
    46→ */
    47→export async function createTenantDatabase(
    48→  projectId: string
    49→): Promise<{ host: string; port: number; database_name: string }> {
    50→  const databaseName = validateDatabaseName(projectId);
    51→
    52→  console.log(`[ProvisionProject] Creating tenant database: ${databaseName}`);
    53→
    54→  try {
    55→    // Create the database using PostgreSQL's format() to prevent SQL injection
    56→    // We use format() which properly escapes identifiers
    57→    // Then execute the resulting SQL with EXECUTE
    58→    const queryText = `
    59→      DO $$
    60→      BEGIN
    61→        EXECUTE format(
    62→          'CREATE DATABASE %I WITH OWNER = postgres ENCODING %L LC_COLLATE = %L LC_CTYPE = %L TEMPLATE = template0 CONNECTION LIMIT = 50',
    63→          $1, 'UTF8', 'en_US.UTF-8', 'en_US.UTF-8'
    64→        );
    65→      END
    66→      $$;
    67→    `;
    68→
    69→    await query(queryText, [databaseName]);
    70→
    71→    console.log(`[ProvisionProject] Successfully created database: ${databaseName}`);
    72→
    73→    // Get validated database configuration
    74→    const dbConfig = getDatabaseConfig();
    75→
    76→    return {
    77→      host: dbConfig.host,
    78→      port: dbConfig.port,
    79→      database_name: databaseName,
    80→    };
    81→  } catch (error) {
    82→    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
    83→    console.error(`[ProvisionProject] Failed to create database: ${errorMessage}`);
    84→    throw new Error('Failed to create tenant database');
    85→  }
    86→}
    87→
    88→/**
    89→ * Create tenant schema
    90→ *
    91→ * Creates a schema within the tenant database with proper permissions.
    92→ * Uses parameterized query via format() to prevent SQL injection.
    93→ *
    94→ * @param projectId - The project ID to use as schema name
    95→ * @param databaseName - The name of the tenant database
    96→ * @returns Promise resolving to schema details
    97→ * @throws Error if schema creation fails
    98→ */
    99→export async function createTenantSchema(
   100→  projectId: string,
   101→  databaseName: string
   102→): Promise<{ schema_name: string }> {
   103→  // Validate and sanitize schema name
   104→  const schemaName = validateDatabaseName(projectId);
   105→
   106→  console.log(`[ProvisionProject] Creating tenant schema: ${schemaName} in database ${databaseName}`);
   107→
   108→  try {
   109→    // Connect to the tenant database and create schema
   110→    // Note: We need to connect to the specific database, not the default one
   111→    // For now, we'll create the schema in the current database context
   112→    // In production, you'd want to use a connection pool per tenant database
   113→
   114→    // Use format() to properly escape schema name (prevents SQL injection)
   115→    const queryText = `
   116→      DO $$
   117→      BEGIN
   118→        EXECUTE format('CREATE SCHEMA IF NOT EXISTS %I AUTHORIZATION postgres', $1);
   119→        EXECUTE format('GRANT ALL PRIVILEGES ON SCHEMA %I TO postgres', $1);
   120→      END
   121→      $$;
   122→    `;
   123→
   124→    await query(queryText, [schemaName]);
   125→
   126→    console.log(`[ProvisionProject] Successfully created schema: ${schemaName}`);
   127→
   128→    return {
   129→      schema_name: schemaName,
   130→    };
   131→  } catch (error) {
   132→    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
   133→    console.error(`[ProvisionProject] Failed to create schema: ${errorMessage}`);
   134→    throw new Error('Failed to create tenant schema');
   135→  }
   136→}
