    15→class ServiceRegistrationError extends Error {
    16→  constructor(serviceName: string, originalError: unknown) {
    17→    // Generic error message to avoid leaking service details
    18→    super(`Failed to register with ${serviceName} service`);
    19→    this.name = 'ServiceRegistrationError';
    20→
    21→    // Log the actual error internally for debugging
    22→    if (originalError instanceof AxiosError) {
    23→      console.error(`[ProvisionProject] ${serviceName} service error:`, {
    24→        status: originalError.response?.status,
    25→        statusText: originalError.response?.statusText,
    26→        message: originalError.message,
    27→      });
    28→    } else if (originalError instanceof Error) {
    29→      console.error(`[ProvisionProject] ${serviceName} service error:`, originalError.message);
    30→    }
    31→  }
    32→}
    33→
    34→/**
    35→ * Register with auth service
    36→ *
    37→ * Registers the tenant with the authentication service via HTTP API.
    38→ * Uses service-to-service authentication if configured.
    39→ *
    40→ * @param projectId - The project/tenant ID
